#define AA 2

// { 2d cell id, distance to border, distnace to center )
vec4 hexagon( vec2 p ) 
{
	vec2 q = vec2( p.x*2.0*0.5773503, p.y + p.x*0.5773503 );

	vec2 pi = floor(q);
	vec2 pf = fract(q);

	float v = mod(pi.x + pi.y, 3.0);

	float ca = step(1.0,v);
	float cb = step(2.0,v);
	vec2  ma = step(pf.xy,pf.yx);

    // distance to borders
	float e = dot( ma, 1.0-pf.yx + ca*(pf.x+pf.y-1.0) + cb*(pf.yx-2.0*pf.xy) );

	// distance to center	
	p = vec2( q.x + floor(0.5+p.y/1.5), 4.0*p.y/3.0 )*0.5 + 0.5;
	float f = length( (fract(p) - 0.5)*vec2(1.0,0.85) );		

	return vec4( pi + ca - cb*ma, e, f );
}

float hash1( vec2  p ) { float n = dot(p,vec2(127.1,311.7) ); return fract(sin(n)*43758.5453); }

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+0.5)/256.0, 0.0 ).yx;
	return mix( rg.x, rg.y, f.z );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec4 tot = vec4(0.0);

    #if AA>1
    for( int mm=0; mm<AA; mm++ )
    for( int nn=0; nn<AA; nn++ )
    {
        vec2 off = vec2(mm,nn)/float(AA);
        vec2 uv = (fragCoord+off)/iResolution.xy;
        vec2 pos = (-iResolution.xy + 2.0*(fragCoord+off))/iResolution.y;
    #else    
    {
        vec2 uv = fragCoord/iResolution.xy;
        vec2 pos = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    #endif

        // distort
        pos *= 1.2 + 0.15*length(pos);

        // gray
        vec4 h = hexagon(8.0*pos + 0.5*iTime);
        float n = noise( vec3(0.3*h.xy+iTime*0.1,iTime) );
        vec4 col = vec4(0.0);

        // shadow
        h = hexagon(6.0*(pos+0.1*vec2(-1.3,1.0)) + 0.6*iTime);
        col *= 1.0-0.8*smoothstep(0.45,0.451,noise( vec3(0.3*h.xy+iTime*0.1,0.5*iTime) ));

        // red
        h = hexagon(6.0*pos + 0.6*iTime);
        n = noise( vec3(0.3*h.xy+iTime*0.1,0.5*iTime) );
        vec3 colb = 0.9 + 0.8*sin( hash1(h.xy)*1.5 + 2.0 + vec3(0.1,1.0,1.1) );
        colb *= smoothstep( 0.10, 0.11, h.z );
        colb *= 1.0 + 0.15*sin(40.0*h.z);
 float mask = smoothstep(0.45,0.451,n);
         col.rgb = colb * mask;
        col.a   = mask;


        col *= 2.5/(2.0+col);

        col *= pow( 16.0*uv.x*(1.0-uv.x)*uv.y*(1.0-uv.y), 0.1 );

        tot += col;
	}	
 	#if AA>1
    tot /= float(AA*AA);
    #endif

	fragColor = tot;
}

// Tileable water turbulence + iChannel0 distortion
// Only the bright highlights (and distortion) are prevented from reaching edges (non-radial)

#define TAU 6.28318530718
#define MAX_ITER 5

float luminance(vec3 c){ return dot(c, vec3(0.299,0.587,0.114)); }

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float time = iTime * 0.5 + 23.0;
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Tileable domain for the procedural
    vec2 p = mod(uv * TAU, TAU) - 250.0;

    vec2 i = p;
    float c = 1.0;
    float inten = 0.005;

    vec2 flow = vec2(0.0);

    for (int n = 0; n < MAX_ITER; n++)
    {
        float t = time * (1.0 - (3.5 / float(n + 1)));

        i = p + vec2(
            cos(t - i.x) + sin(t + i.y),
            sin(t - i.y) + cos(t + i.x)
        );

        c += 1.0 / length(vec2(
            p.x / (sin(i.x + t) / inten),
            p.y / (cos(i.y + t) / inten)
        ));

        flow += vec2(sin(i.y + t), cos(i.x + t));
    }

    c /= float(MAX_ITER);
    c = 1.17 - pow(c, 1.4);

    // ----- Build water as base + highlight -----
    vec3 baseTint  = vec3(0.0, 0.35, 0.5);               // constant tint
    vec3 highlight = vec3(pow(abs(c), 8.0));             // bright/white-ish parts

    // ----- Effect mask from brightness (where the "water" is strong) -----
    float w = luminance(clamp(baseTint + highlight, 0.0, 1.0));
    float effectMask = smoothstep(0.10, 0.45, w);

    // ----- Non-radial edge mask (fade near ANY edge) -----
    vec2 tuv = fract(uv);
    float edgeDist = min(min(tuv.x, 1.0 - tuv.x), min(tuv.y, 1.0 - tuv.y));

    const float edgeFadeStart = 0.12; // farther from edge => still full
    const float edgeFadeEnd   = 0.0; // close to edge => 0
    float edgeMask = smoothstep(edgeFadeEnd, edgeFadeStart, edgeDist);

    // Apply edgeMask ONLY to the highlight (prevents bright parts at edges)
    highlight *= edgeMask;

    vec3 water = clamp(baseTint + highlight, 0.0, 1.0);

    // ----- Distort iChannel0 (also fades near edges) -----
    flow /= float(MAX_ITER);

    const float distortionStrength = 0.04;
    float distortionMask = effectMask * edgeMask; // dies out before edges

    vec2 duv = flow * distortionStrength * distortionMask;
    vec2 uvDistorted = fract(uv + duv);

    vec4 tex = texture(iChannel0, uvDistorted);

    // Blend texture with water look (texture is NOT edge-faded)
    float waterMix = 0.55;
    vec3 col = mix(tex.rgb, water, waterMix);

    fragColor = vec4(col, tex.a);
}

//-----------------------------------------------------
// SnowIsFalling.glsl
//  v1.0  2017-08-24  initial version
//  v1.1  2018-04-17  test with mysteryMountain
// combining background of https://www.shadertoy.com/view/4dl3R4
// and falling snow of     https://www.shadertoy.com/view/ldsGDn
//-----------------------------------------------------

//=== background ===

#define mod289(x) mod(x, 289.)

vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

//-----------------------------------------------------
float snoise(vec2 v)
{
  const vec4 C = vec4(0.211324865405187,0.366025403784439,-0.577350269189626,0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);

  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;

  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
                	+ i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;

  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;

  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;

  return 130.0 * dot(m, g);		
}
//-----------------------------------------------------
float fbm(vec2 p) 
{
  float f = 0.0;
  float w = 0.5;
  for (int i = 0; i < 5; i ++) 
  {
    f += w * snoise(p);
    p *= 2.;
    w *= 0.5;
  }
  return f;
}
//-----------------------------------------------------
// background of https://www.shadertoy.com/view/4dl3R4
//-----------------------------------------------------
float background (vec2 uv)
{
  uv.x += iMouse.x / iResolution.x - 1.0;
  float speed=2.0;

  vec2 sunCenter = vec2(0.3,0.9);
  float suns = clamp(1.2-distance(uv,sunCenter), 0.0, 1.0);
  float sunsh = smoothstep(0.85,0.95,suns);

  float slope = 1.0 - smoothstep(0.55, 0.0, 0.8 +uv.x -2.3*uv.y);								

  float noise = abs(fbm(uv*1.5));
  slope = (noise*0.2)+(slope-((1.0-noise)*slope*0.1))*0.6;
  slope = clamp(slope, 0.0, 1.0);

  vec2 GA = speed * vec2 (iTime*1.8, iTime*0.9);

  // Snow layers, somewhat like an fbm with worley layers.
  return 0.35 + (slope*(suns+0.3))+(sunsh*0.6);    
}

//-----------------------------------------------------
// falling snow of https://www.shadertoy.com/view/ldsGDn
//-----------------------------------------------------

#define LAYERS 66

#define DEPTH1 .3
#define WIDTH1 .4
#define SPEED1 .6

#define DEPTH2 .1
#define WIDTH2 .3
#define SPEED2 .1

float snowing(in vec2 uv, in vec2 fragCoord )
{
  const mat3 p = mat3(13.323122,23.5112,21.71123,21.1212,28.7312,11.9312,21.8112,14.7212,61.3934);
  vec2 mp = iMouse.xy / iResolution.xy;
  uv.x += mp.x*4.0;    
  mp.y *= 0.25;
  float depth = smoothstep(DEPTH1, DEPTH2, mp.y);
  float width = smoothstep(WIDTH1, WIDTH2, mp.y);
  float speed = smoothstep(SPEED1, SPEED2, mp.y);
  float acc = 0.0;
  float dof = 5.0 * sin(iTime * 0.1);
  for (int i=0; i < LAYERS; i++)
  {
    float fi = float(i);
    vec2 q = uv * (1.0 + fi*depth);
    float w = width * mod(fi*7.238917,1.0)-width*0.1*sin(iTime*2.+fi);
    q += vec2(q.y*w, speed*iTime / (1.0+fi*depth*0.03));
    vec3 n = vec3(floor(q),31.189+fi);
    vec3 m = floor(n)*0.00001 + fract(n);
    vec3 mp = (31415.9+m) / fract(p*m);
    vec3 r = fract(mp);
    vec2 s = abs(mod(q,1.0) -0.5 +0.9*r.xy -0.45);
    s += 0.01*abs(2.0*fract(10.*q.yx)-1.); 
    float d = 0.6*max(s.x-s.y,s.x+s.y)+max(s.x,s.y)-.01;
    float edge = 0.05 +0.05*min(.5*abs(fi-5.-dof),1.);
    acc += smoothstep(edge,-edge,d)*(r.x/(1.+.02*fi*depth));
  }
  return acc;
}
//-----------------------------------------------------
// '[2TC 15] Mystery Mountains' by David Hoskins.
// Add layers of the texture of differing frequencies and magnitudes...
//-----------------------------------------------------
#define F +texture(iChannel1,.3+p.xz*s/3e3)/(s+=s) 
bool MysteryMountains( inout vec4 c, vec2 w )
{
    vec4 p = vec4(w/iResolution.xy,1,1)-.5,d=p,t;
    p.z += iTime*2.;
 //   d.x-=0.04;
    for(float i=1.5; i > 0.3; i-=.002)
    {
        float s = 0.8;
        t = F F F F F;
        c = vec4(1,1.,.9,9)+d.x-t*i;
        if (t.x > p.y*.017+1.3) return true;
        p += d;
    }
    return false;
}
//-----------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.y;

//  if (!MysteryMountains(fragColor, fragCoord))
//  {
  float bg = background(uv);
  fragColor = vec4(bg*0.9, bg, bg*1.1, 1.0);
//  }
  float snowOut = snowing(uv,fragCoord);
  fragColor += vec4(vec3(snowOut), 1.0);
}

/*

	Round Voronoi Border Refinement
	-------------------------------

	Testing Tomkh's refinements on IQ's straight-edged Voronoi formula to produce detailed, 
	rounded cell borders on hexagonal Voronoi - Basically, just another excuse to code something 
	shiny. :)

	Thanks to Tomkh, rounded Voronoi borders with continuous isolines are possible - or close enough
	to enable nice equidistant contour lines that line up at the edges. His solution was based on 
	previous work by IQ, Abje, Dr2 and others. My contribution was to sit on the sidelines and wait 
	for someone else to do the work. :D

	Anyway, this is a simple bump mapped example. The cliche faux chromatic look is achieved by 
	lighting the surface with a red-tinged light and a blue-tinged light. The individual specular 
	components have more prominent red and blue coloring. For anyone wondering, you can usually fake
	a metallic look by increasing the intensity and ramping up the diffuse lighting power. Metallic
	texturing can help too.

	I'm not entirely sure why the hardened metal substance is moving so fluidly, but I'm pretty sure
	it can all be explained with Star Trek physics. :)

	Based on:

	A Voronoi Edge distance function that is continuous across 
	the whole domain and smooth at the non-zero distance.
	Rounder Voronoi Edge Distance - tomkh
	https://www.shadertoy.com/view/ld3yRn


*/

// Very unimaginative cell color options. :)
// Grey: 0, Brown: 1, Blue: 2., 3: Green. 
#define CELL_COLOR 0

// Scene object ID, and individual cell IDs. Used for coloring.
float objID; // The rounded web lattice, or the individual Voronoi cells.
//vec2 cellID; // Individual Voronoi cell IDs.


// Standard 2D rotation formula.
mat2 r2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }

/*
// IQ's esmooth minimum function. 
float smin( float a, float b, float k ){

    float h = clamp(.5 + .5*(b - a)/k, 0., 1.);
    return mix(b, a, h) - k*h*(1. - h);
}


// IQ's exponential-based smooth minimum function. Unlike the polynomial-based
// smooth minimum, this one is associative and commutative.
float sminExp(float a, float b, float k)
{
    float res = exp(-k*a) + exp(-k*b);
    return -log(res)/k;
}
*/

// Commutative smooth minimum function. Provided by Tomkh and 
// taken from Alex Evans's (aka Statix) talk: 
// http://media.lolrus.mediamolecule.com/AlexEvans_SIGGRAPH-2015.pdf
// Credited to Dave Smith @media molecule.
float smin2(float a, float b, float r)
{
   float f = max(0., 1. - abs(b - a)/r);
   return min(a, b) - r*.25*f*f;
}

// vec2 to vec2 hash.
vec2 hash22H(vec2 p) { 

    // Faster, but doesn't disperse things quite as nicely. However, when framerate
    // is an issue, and it often is, this is a good one to use. Basically, it's a tweaked 
    // amalgamation I put together, based on a couple of other random algorithms I've 
    // seen around... so use it with caution, because I make a tonne of mistakes. :)
    float n = sin(dot(p, vec2(41, 289)));
    //return fract(vec2(262144, 32768)*n)*.8660254; 

    // Animated.
    p = fract(vec2(262144, 32768)*n);
    // Slightly lower number, so as not to overshoot the hexagonal bounds.
    return sin( p*6.2831853 + iTime )*.3660254 + .5; 

}


// Converting to the hexagonal grid.
vec2 pixToHex(vec2 p){

    return floor(vec2(p.x + .57735*p.y, 1.1547*p.y) + 1./3.);
}


// Randomized hexagonal offset point.
vec2 hexPt(vec2 p) {

    // The offset value is restricted to the radius of the incircle of the
    // hexagon, or apothem as it's technically known.
    return vec2(p.x - p.y*.5, .866025*p.y) + (hash22H(p) - .5)*.866025/2.;

}


// This is a hexagonal variation on a regular 2-pass Voronoi traversal that produces
// a Voronoi pattern based on the interior cell point to the nearest cell edge (as opposed
// to the nearest offset point). It uses elements from an old hexagonal Voronoi example, 
// and is based on IQ's original example. It was inspired by Dr2's hexagonal examples.
// The links are below:
//
// On a side note, I have no idea whether a faster solution is possible, but when I
// have time, I'm going to attemp to find one anyway.
//
// Voronoi distances - iq
// https://www.shadertoy.com/view/ldl3W8
//
// Here's IQ's well written article that describes the process in more detail.
// https://iquilezles.org/articles/voronoilines
//
// Desert Town - dr2
// https://www.shadertoy.com/view/XslBDl
vec3 Voronoi(vec2 p){

    // Convert to the hexagonal grid.
    vec2 pH = pixToHex(p); // Map the pixel to the hex grid.

    // There'd be a heap of ways to get rid of this array and speed things up. The
    // most obvious, would be unrolling the loops, but there'd be more elegant ways.
    // Either way, I've left it this way just to make the code easier to read.

    // Hexagonal grid offsets. "vec2(0)" represents the center, and the other offsets effectively circle it.
    // Thanks, Abje. Hopefully, the compiler will know what to do with this. :)
	const vec2 hp[7] = vec2[7](vec2(-1), vec2(0, -1), vec2(-1, 0), vec2(0), vec2(1), vec2(1, 0), vec2(0, 1)); 


    // Voronoi cell ID containing the minimum offset point distance. The nearest
    // edge will be one of the cell's edges.
    vec2 minCellID = vec2(0); // Redundant initialization, but I've done it anyway.

    // As IQ has commented, this is a regular Voronoi pass, so it should be
    // pretty self explanatory.
    //
    // First pass: Regular Voronoi.
	vec2 mo, o;

    // Minimum distance, "smooth" distance to the nearest cell edge, regular
    // distance to the nearest cell edge, and a line distance place holder.
    float md = 8., lMd = 8., lMd2 = 8., lnDist, d;

    for (int i=0; i<7; i++){

        // Determine the offset hexagonal point.
        vec2 h = hexPt(pH + hp[i]) - p;
        // Determine the distance metric to the point.
    	d = dot(h, h);
    	if( d<md ){ // Perform updates, if applicable.

            md = d;  // Update the minimum distance.
            // Keep note of the position of the nearest cell point - with respect
            // to "p," of course. It will be used in the second pass.
            mo = h; 
            //cellID = h + p; // For cell coloring.
            minCellID = hp[i]; // Record the minimum distance cell ID.
        }
    }

    // Second pass: Point to nearest cell-edge distance.
    //
    // With the ID of the cell containing the closest point, do a sweep of all the
    // surrounding cell edges to determine the closest one. You do that by applying
    // a standard distance to a line formula.
    for (int i=0; i<7; i++){

         // Determine the offset hexagonal point in relation to the minimum cell offset.
        vec2 h = hexPt(pH + hp[i] + minCellID) - p - mo; // Note the "-mo" to save some operations. 

        // Skip the same cell.
        if(dot(h, h)>.00001){

            // This tiny line is the crux of the whole example, believe it or not. Basically, it's
            // a bit of simple trigonometry to determine the distance from the cell point to the
            // cell border line. See IQ's article (link above) for a visual representation.            
            lnDist = dot(mo + h*.5, normalize(h));

            // Abje's addition. Border distance using a smooth minimum. Insightful, and simple.
            //
            // On a side note, IQ reminded me that the order in which the polynomial-based smooth
            // minimum is applied effects the result. However, the exponentional-based smooth
            // minimum is associative and commutative, so is more correct. In this particular case, 
            // the effects appear to be negligible, so I'm sticking with the cheaper polynomial-based
            // smooth minimum, but it's something you should keep in mind. By the way, feel free to 
            // uncomment the exponential one and try it out to see if you notice a difference.
            //
            // Polynomial-based smooth minimum. The last factor controls the roundness of the 
            // edge joins. Zero gives you sharp joins, and something like ".25" will produce a
            // more rounded look. Tomkh noticed that a variable smoothing factor - based on the
            // line distance - produces continuous isolines.
            lMd = smin2(lMd, lnDist, (lnDist*.5 + .5)*.15);
            //lMd = smin2(lMd, lnDist, .1);
            // Exponential-based smooth minimum.
            //lMd = sminExp(lMd, lnDist, 20.); 
            //lMd = sminExp(lMd, lnDist, (lnDist*.5 + .5)*50.);

            // Minimum regular straight-edged border distance. If you only used this distance,
            // the web lattice would have sharp edges.
            lMd2 = min(lMd2, lnDist);

        }

    }

    // Return the smoothed and unsmoothed distance. I think they need capping at zero... but I'm not 
    // positive. Although not used here, the standard minimum point distance is returned also.
    return max(vec3(lMd, lMd2, md), 0.);


}


// The bump function. Used for bump mapping, coloring and shading.
float bumpFunc(vec2 p){

	// Voronoi vector. It holds the rounded edge value, straight edge value,
    // and a dummy value.
    vec3 v = Voronoi(p);

    float c = v.x; // Rounded edge value.


    float ew = .07; // Border threshold value. Bigger numbers mean thicker borders.

    // If the Voronoi value is under the threshold, produce a web like contoured border.
    if(c<ew){ 

        objID = 1.; // Voronoi web border ID.

        c = abs(c - ew)/ew; // Normalize the domain to a range of zero to one.

        // Add the contoured pattern to the web border.
        c = smoothstep(0., .25, c)/4. + clamp(-cos(c*6.283*1.5) - .5, 0., 1.);

    }
    else { // Over the threshold? Use the regular Voronoi cell value.

        objID = 0.;
        c = mix(v.x,  v.y, .75); // A mixture of rounded and straight edge values.
        c = (c - ew)/(1. - ew); // Normalize the domain to a range of zero to one.
        c = clamp(c + cos(c*6.283*24.)*.002, 0., 1.); // Add some ridges.
    }

    return c; // Return the object (bordered Voronoi) value.

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    //SETUP.
    //
    // Aspect correct screen coordinates.
	vec2 uv = (fragCoord - iResolution.xy*.5)/min(iResolution.y, 800.);

	vec2 uv2 = fragCoord.xy / iResolution.xy;

    vec4 uv4 = texture(iChannel0, uv2);

    // Very subtle screen warping, for that bulbous fish-eye look.
    vec2 aspect = vec2(iResolution.y/iResolution.x, 1);
    uv *= 1. + dot(uv*aspect, uv*aspect)*.05;

    // Unit direction ray.
    vec3 r = normalize(vec3(uv.xy, 1.));

    // Scaling and movement.
    vec2 p = uv*3.5 + vec2(0, iTime*.5);

    // The webbed Voronoi value.
    float c = bumpFunc(p);

    // Saving the ID.
    float svObjID = objID; 

    // 3D screen hit point. Just a flat plane at the zero point on the Z-axix.
    vec3 sp = vec3(p, 0.);

    // Two lights, set back from the plane, and rotating about the XY plane on
    // opposite sides of an ellipse, or something to that effect.
    vec3 lp = sp + vec3(-1.3*sin(iTime/2.), .8*cos(iTime/2.), -.5);
    vec3 lp2 = sp + vec3(1.3*sin(iTime/2.), -.8*cos(iTime/2.), -.5);

    // Fake hit-point height value. Normally, you'd cast a ray to the hit point, but 
    // since it's a simple bump mapped example, we're estimating it.
    sp.z -= c*.1;


    // BUMP MAPPING AND EDGING. Pretty standard stuff. 
    //
    vec2 e = vec2(8./iResolution.y, 0); // Sample spred.
    float bf = .4; // Bump factor.

    // If we hit the webbing section, reduce the sample spread. It's a bit of fake
    // trickery to reduce artifacts on the webbing portion.
    if (svObjID>.5) { e.x = 2./iResolution.y; }

    float fx = (bumpFunc(p - e) - bumpFunc(p + e)); // Nearby horizontal samples.
    float fy = (bumpFunc(p - e.yx) - bumpFunc(p + e.yx)); // Nearby vertical samples.
	vec3 n = normalize(vec3(fx, fy, -e.x/bf)); // Bumped normal.

    float edge = abs(c*2. - fx) + abs(c*2. - fy); // Edge value.


    // TEXTURE AND COLORING.
    //
    // Texture sample with fake height information added.
    vec3 tx = texture(iChannel1, (p + n.xy*.125)*.25).xyz; tx *= tx; // sRGB to linear.
    tx = smoothstep(0., .5, tx); // Accentuating the color a bit.

    // Object color. Initialize to the texture value.
    vec3 oCol = tx; 


    if(svObjID>.5){ // The webbing portion.

        //oCol *= vec3(1.2, .8, .4); // Uncomment for gold webbing.
        //oCol *= vec3(1.4, 1, .7);
        oCol *= .9;

    }   
    #if CELL_COLOR > 0
    else { // The cell portion. Do what you want here.

	    // Uncomment for colored cells, etc.
        #if CELL_COLOR == 1
        oCol *= vec3(1.4, 1, .7);
        #elif CELL_COLOR == 2
        oCol *= vec3(.9, 1.1, 1.3); 
        #else
        oCol *= vec3(1.1, 1.4, .7); 
        #endif

    }
	#endif


    //oCol *= vec3(1.2, 1, .84); // Warmer coloring.
    //oCol *= vec3(.9, 1.1, 1.3); // Cooler coloring.


	// LIGHTING.
    //
    float lDist = length(lp - sp); // Light distance one.
    float atten = 1./(1. + lDist*lDist*.5); // Light one attenuation.
    vec3 l = (lp - sp)/max(lDist, .001); // Light one direction (normalized).
	float diff = max(max(dot(l, n), 0.), 0.); // Diffuse value one.
    float spec = pow(max(dot(reflect(l, n), r), 0.), 64.); // Specular value one.


    float lDist2 = length(lp2 - sp); // Light distance two.
    float atten2 = 1./(1. + lDist2*lDist2*.5); // Light two attenuation.
    vec3 l2 = (lp2 - sp)/max(lDist2, .001); // Light two direction (normalized).
	float diff2 = max(max(dot(l2, n), 0.), 0.); // Diffuse value two.
    float spec2 = pow(max(dot(reflect(l2, n), r), 0.), 64.); // Specular value twp.


    // Ramping up the power and increasing the intensity of the diffuse values to 
    // give more of a metallic look.
    diff = pow(diff, 4.)*2.;
    diff2 = pow(diff2, 4.)*2.;


 	// Combining the texture and lighting information above.

    // Light one.
    vec3 col = oCol*(diff*vec3(.5, .7, 1) + .25 + vec3(.25, .5, 1)*spec*32.)*atten*.5;

    // Adding light two.
    col += oCol*(diff2*vec3(1, .7, .5) + .25 + vec3(1, .3, .1)*spec2*32.)*atten2*.5;

    // Apply the edging. This provides fake AO, depth information, etc. Comment it out, and
    // the example becomes very 2-dimensional.
    col *= edge;

    // POSTPROCESSING, SCREEN PRESENTATION, ETC.
    //
    //col *= vec3(1.5, 1., .6); // Warmer coloring.
    //col *= vec3(.9, 1.2, 1.4); // Cooler coloring.

	// Subtle vignette.
    vec2 u = fragCoord/iResolution.xy;
    col *= pow(16.*u.x*u.y*(1. - u.x)*(1. - u.y) , .125);
    // Colored variation.
    //col = mix(pow(min(vec3(1.5, 1, 1)*col, 1.), vec3(1, 3, 16)), col, 
            //pow(16.*u.x*u.y*(1. - u.x)*(1. - u.y) , .125)*.75 + .25);     

    // Rough gamma correction.
    fragColor = vec4(sqrt(max(col, 0.)), uv4.a);

}

// Mirza: Example of hexagonal sampling and animation.
// I made this largely for my own learning, but hopefully it can help others.

// https://twitter.com/TheMirzaBeig/status/1731324791557140490

// See links for [re]sources and more detailed explanations.

// Not used, but a good read:
// > https://www.redblobgames.com/grids/hexagons/

#define FLAT_TOP_HEXAGON

// Critical resource:
// https://www.shadertoy.com/view/Xljczw

// Note from original author:

// Helper vector. If you're doing anything that involves regular triangles or hexagons, the
// 30-60-90 triangle will be involved in some way, which has sides of 1, sqrt(3) and 2.

// MB: This special right-angle triangle (30-60-90) is just half an equilateral ('regular') triangle (all sides = 60).

// https://en.wikipedia.org/wiki/Equilateral_triangle

#ifdef FLAT_TOP_HEXAGON
    const vec2 s = vec2(sqrt(3.0), 1.0);
#else
    const vec2 s = vec2(1.0, sqrt(3.0));
#endif

float Hex(in vec2 p)
{    
    p = abs(p);    

    #ifdef FLAT_TOP_HEXAGON
        return max(dot(p, s * 0.5), p.y);
    #else
        return max(dot(p, s * 0.5), p.x);
    #endif  
}

// xy = 2D distance in each cell. Use with Hex() to get hexagon shape.
// zw = hexagonal-quantized UV coordinates (cell ID).

// MB: See, https://andrewhungblog.wordpress.com/2018/07/28/shader-art-tutorial-hexagonal-grids/

// Essentially rows of points, where every other row is shifted.
// https://blender.stackexchange.com/questions/161701/how-to-do-uv-indexing-in-hexagonal-pattern

vec4 HexLattice(vec2 uv)
{     
    // MB: Using round (vs. floor), removing need for + 0.5.

    #ifdef FLAT_TOP_HEXAGON
        vec4 hexCenter = round(vec4(uv, uv - vec2(1.0, 0.5)) / s.xyxy);
    #else
        vec4 hexCenter = round(vec4(uv, uv - vec2(0.5, 1.0)) / s.xyxy);
    #endif    

    vec4 offset = vec4(uv - hexCenter.xy * s, uv - (hexCenter.zw + 0.5) * s);

    return

    dot(offset.xy, offset.xy) < dot(offset.zw, offset.zw) ?     
    vec4(offset.xy, hexCenter.xy) : vec4(offset.zw, hexCenter.zw + 0.5); 
}

// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83

float rand(vec2 c){
	return fract(sin(dot(c.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

#define PI 3.14159

float noise(vec2 p, float freq ){
	float unit = 1.0/freq;
	vec2 ij = floor(p/unit);
	vec2 xy = mod(p,unit)/unit;
	//xy = 3.*xy*xy-2.*xy*xy*xy;
	xy = .5*(1.-cos(PI*xy));
	float a = rand((ij+vec2(0.,0.)));
	float b = rand((ij+vec2(1.,0.)));
	float c = rand((ij+vec2(0.,1.)));
	float d = rand((ij+vec2(1.,1.)));
	float x1 = mix(a, b, xy.x);
	float x2 = mix(c, d, xy.x);
	return mix(x1, x2, xy.y);
}

// Render.

float Glow(float sdf, float width, float power)
{
    return pow(width / sdf, power);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized UVs and mouse.

    vec2 uv = fragCoord / iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy;

    float aspectRatio = iResolution.x / iResolution.y;    

    // Tiling between range, with mouse as input.

    float tiling = mix(8.0, 32.0, mouse.x);

    vec2 hexTiling = vec2(tiling);
    hexTiling.x *= aspectRatio;

    vec2 hexUV = uv * hexTiling;
    vec2 hexMouse = mouse * hexTiling;

    vec4 hexGrid = HexLattice(hexUV);    
    vec4 hexGridOriginal = hexGrid;

    vec3 colour;

    #ifdef FLAT_TOP_HEXAGON
        hexGrid.z *= aspectRatio;
    #else
        hexGrid.w *= aspectRatio;
    #endif 

    vec2 hexGridUV = hexGrid.zw / hexTiling;
    vec2 hexGridOriginalUV = hexGridOriginal.zw / hexTiling;

    #ifndef FLAT_TOP_HEXAGON    
        hexGridUV *= vec2(1.0, 0.975);    
    #endif

    colour.rg = hexGridUV.xy; // Colour red/green (XY) = UV.

    // Assign the blue (Z) value of colour to something animated wildly.

    colour.b = uv.y * (abs(sin(iTime * 10.0)) * 5.0);

    // Get distance field.
    // Used as an animated hexagonal mask with a tiling scan.

    float hex = Hex(hexGrid.xy) * 2.0;

    float scanSpeed = 1.0;

    // Tiling scan (hexGridOriginal.w), as opposed to using uv.y.

    float noiseTiling = 2.0;
    vec2 noiseAnimation = vec2(-2.0, -2.0);        

    float animatedNoise = noise(hexGrid.zw + vec2(noiseAnimation * iTime), noiseTiling);

    float scanBase = hexGridOriginalUV.y;

    // For a wild effect.    
    //scanBase *= hexGridOriginal.z; 

    // For a wipe effect.    
    //scanBase *= hexGridOriginal.z / tiling;

    // Offset scan up/down (using hex-quantized noise).

    scanBase += animatedNoise * 0.2;
    scanBase += sin(hexGridUV.x * 64.0) * 0.05;

    // Repeating scan animation (using fract), with vertical mouse input.

    float scan = fract(scanBase + (iTime * scanSpeed) + mouse.y);

    colour *= step(smoothstep(0.0, 0.7, scan), 1.0 - hex);

    // Apply noise.

    colour *= animatedNoise;

    // Highlight mouse tile.

    if (hexGridOriginal.zw == HexLattice(hexMouse).zw)
    {
        vec3 glowColour = vec3(0.5, 0.1, 1.0);
        colour = Glow(hex, 0.5, 5.0) * glowColour; 
    }  

    // Mix with texture. Use either iChannel [0] or [1].

    vec3 textureColour = texture(iChannel0, hexGridUV).rgb;
    colour = mix(colour, textureColour, 0.3);

    fragColor = vec4(colour, 1.0);
}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float rand(vec2 n) {
    return fract(sin(cos(dot(n, vec2(12.9898,12.1414)))) * 83758.5453);
}

float noise(vec2 n) {
    const vec2 d = vec2(0.0, 1.0);
    vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
    return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

float fbm(vec2 n) {
    float total = 0.0, amplitude = 1.0;
    for (int i = 0; i <5; i++) {
        total += noise(n) * amplitude;
        n += n*1.7;
        amplitude *= 0.47;
    }
    return total;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    const vec3 c1 = vec3(0.843137, 0.109804, 0.058824);
    const vec3 c2 = vec3(0.882353, 0.580392, 0.054902);
    const vec3 c3 = vec3(0.756863, 0.082353, 0.286275);
    const vec3 c4 = vec3(1.0, 0.9, 0.1);
    const vec3 c5 = vec3(0.1);
    const vec3 c6 = vec3(0.9);

    vec2 speed = vec2(1.2, 0.1);
    float shift = 1.327+sin(iTime*2.0)/2.4;
    float alpha = 1.0;

    //change the constant term for all kinds of cool distance versions,
    //make plus/minus to switch between 
    //ground fire and fire rain!
	float dist = 3.5-sin(iTime*0.4)/1.89;

    vec2 p = fragCoord.xy * dist / iResolution.xx;
    p.x -= iTime/1.1;
    float q = fbm(p - iTime * 0.01+1.0*sin(iTime)/10.0);
    float qb = fbm(p - iTime * 0.002+0.1*cos(iTime)/5.0);
    float q2 = fbm(p - iTime * 0.44 - 5.0*cos(iTime)/7.0) - 6.0;
    float q3 = fbm(p - iTime * 0.9 - 10.0*cos(iTime)/30.0)-4.0;
    float q4 = fbm(p - iTime * 2.0 - 20.0*sin(iTime)/20.0)+2.0;
    q = (q + qb - .4 * q2 -2.0*q3  + .6*q4)/3.8;
    vec2 r = vec2(fbm(p + q /2.0 + iTime * speed.x - p.x - p.y), fbm(p + q - iTime * speed.y));
    vec3 c = mix(c1, c2, fbm(p + r)) + mix(c3, c4, r.x) - mix(c5, c6, r.y);
    vec3 color = vec3(c * cos(shift * fragCoord.y / iResolution.y));
    color += .05;
    color.r *= .8;
    vec3 hsv = rgb2hsv(color);
    hsv.y *= hsv.z  * 1.1;
    hsv.z *= hsv.y * 1.13;
    hsv.y = (2.2-hsv.z*.9)*1.20;
    color = hsv2rgb(hsv);
    fragColor = vec4(color.x, color.y, color.z, alpha);
}

float rand(vec2 n) {
	return fract(cos(dot(n, vec2(12.98, 4.1414))) * 46758.5453);
}

float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
	vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

float fbm(vec2 n) {
	float total = 0.0, amplitude = 1.0;
	for (int i = 0; i < 3; i++) {
		total += noise(n) * amplitude;
		n += n;
		amplitude *= 0.5;
	}
	return total;
}

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    const vec3 c1 = vec3(0.2);
	const vec3 c2 = vec3(0.3);
	const vec3 c3 = vec3(0.5);
	const vec3 c4 = vec3(0.7);
	const vec3 c5 = vec3(0.4);
	const vec3 c6 = vec3(0.7);

	vec2 speed = vec2(0.1, 0.5);
	vec2 p = fragCoord.xy * 5.0 / iResolution.xx;
	float q = fbm(p - iTime * 0.1);
	vec2 r = vec2(fbm(p + q + iTime * speed.x - p.x - p.y), fbm(p + q - iTime * speed.y));
	vec3 c = mix(c1, c2, fbm(p + r)) + mix(c3, c4, r.x) - mix(c5, c6, r.y);

    c = c * vec3(1.0 - smoothstep(0.1, 0.8, uv.y));
    float base = 0.6;               // where fog starts
    float g = smoothstep(base, base + 0.6, uv.y);
    fragColor = vec4(c * (1.0 - g), 0.6);
}

// "Volumetric explosion" by Duke
// https://www.shadertoy.com/view/lsySzd
//-------------------------------------------------------------------------------------
// Based on "Supernova remnant" (https://www.shadertoy.com/view/MdKXzc) 
