vec2 Drops(vec2 uv, float t) {

    vec2 UV = uv;

    // DEFINE GRID
    uv.y += t*0.8;
    vec2 a = vec2(6., 1.);
    vec2 grid = a*2.;
    vec2 id = floor(uv*grid);

    // RANDOM SHIFT Y
    float colShift = N(id.x); 
    uv.y += colShift;

    // DEFINE SPACES
    id = floor(uv*grid);
    vec3 n = N13(id.x*35.2+id.y*2376.1);
    vec2 st = fract(uv*grid)-vec2(.5, 0);

    // POSITION DROPS
    //clamp(2*x,0,2)+clamp(1-x*.5, -1.5, .5)+1.5-2
    float x = n.x-.5;

    float y = UV.y*20.;

    float distort = sin(y+sin(y));
    x += distort*(.5-abs(x))*(n.z-.5);
    x *= .7;
    float ti = fract(t+n.z);
    y = (Saw(.85, ti)-.5)*.9+.5;
    vec2 p = vec2(x, y);

    // DROPS
    float d = length((st-p)*a.yx);

    float dSize = size; 

    float Drop = S(dSize, .0, d);


    float r = sqrt(S(1., y, st.y));
    float cd = abs(st.x-x);

    // TRAILS
    float trail = S((dSize*.5+.03)*r, (dSize*.5-.05)*r, cd);
    float trailFront = S(-.02, .02, st.y-y);
    trail *= trailFront;


    // DROPLETS
    y = UV.y;
    y += N(id.x);
    float trail2 = S(dSize*r, .0, cd);
    float droplets = max(0., (sin(y*(1.-y)*120.)-st.y))*trail2*trailFront*n.z;
    y = fract(y*10.)+(st.y-.5);
    float dd = length(st-vec2(x, y));
    droplets = S(dSize*N(id.x), 0., dd);
    float m = Drop+droplets*r*trailFront;
    #ifdef DEBUG
    m += st.x>a.y*.45 || st.y>a.x*.165 ? 1.2 : 0.; //DEBUG SPACES
    #endif


    return vec2(m, trail);
}

float StaticDrops(vec2 uv, float t) {
	uv *= 30.;

    vec2 id = floor(uv);
    uv = fract(uv)-.5;
    vec3 n = N13(id.x*107.45+id.y*3543.654);
    vec2 p = (n.xy-.5)*0.5;
    float d = length(uv-p);

    float fade = Saw(.025, fract(t+n.z));
    float c = S(size, 0., d)*fract(n.z*10.)*fade;

    return c;
}

vec2 Rain(vec2 uv, float t) {
    float s = StaticDrops(uv, t); 
    vec2 r1 = Drops(uv, t);
    vec2 r2 = Drops(uv*1.8, t);

    #ifdef DEBUG
    float c = r1.x;
    #else
    float c = s+r1.x+r2.x;
    #endif

    c = S(.3, 1., c);

    #ifdef DEBUG
    return vec2(c, r1.y);
    #else
    return vec2(c, max(r1.y, r2.y));
    #endif
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-.5*iResolution.xy) / iResolution.y;
    vec2 UV = fragCoord.xy/iResolution.xy;
    float T = iTime;


    float t = T*.2;

    float rainAmount = 0.8;



    UV = (UV-.5)*(.9)+.5;

    vec2 c = Rain(uv, t);

   	vec2 e = vec2(.001, 0.); //pixel offset
   	float cx = Rain(uv+e, t).x;
   	float cy = Rain(uv+e.yx, t).x;
   	vec2 n = vec2(cx-c.x, cy-c.x); //normals



    // BLUR derived from existical https://www.shadertoy.com/view/Xltfzj
        float Pi = 6.28318530718; // Pi*2

        // GAUSSIAN BLUR SETTINGS {{{
        float Directions = 32.0; // BLUR DIRECTIONS (Default 16.0 - More is better but slower)
        float Quality = 8.0; // BLUR QUALITY (Default 4.0 - More is better but slower)
        float Size = 32.0; // BLUR SIZE (Radius)
        // GAUSSIAN BLUR SETTINGS }}}

        vec2 Radius = Size/iResolution.xy;

        vec3 col = texture(iChannel0, UV).rgb;
        // Blur calculations
        for( float d=0.0; d<Pi; d+=Pi/Directions)
        {
            for(float i=1.0/Quality; i<=1.0; i+=1.0/Quality)
            {
                #ifdef DEBUG
                vec3 tex = texture( iChannel0, UV+c+vec2(cos(d),sin(d))*Radius*i).rgb;
                #else
                vec3 tex = texture( iChannel0, UV+n+vec2(cos(d),sin(d))*Radius*i).rgb;
                #endif

                col += tex;            
            }
        }

        col /= Quality * Directions - 0.0;

        vec3 tex = texture( iChannel0, UV+n).rgb;
        c.y = clamp(c.y, 0.0, 1.);

        col -= c.y;
        col += c.y*(tex+.6);

    fragColor = vec4(col, 1.);
}

// Mirza: Example of hexagonal sampling and animation.
// I made this largely for my own learning, but hopefully it can help others.

// https://twitter.com/TheMirzaBeig/status/1731324791557140490

// See links for [re]sources and more detailed explanations.

// Not used, but a good read:
// > https://www.redblobgames.com/grids/hexagons/

#define FLAT_TOP_HEXAGON

// Critical resource:
// https://www.shadertoy.com/view/Xljczw

// Note from original author:

// Helper vector. If you're doing anything that involves regular triangles or hexagons, the
// 30-60-90 triangle will be involved in some way, which has sides of 1, sqrt(3) and 2.

// MB: This special right-angle triangle (30-60-90) is just half an equilateral ('regular') triangle (all sides = 60).

// https://en.wikipedia.org/wiki/Equilateral_triangle

#ifdef FLAT_TOP_HEXAGON
    const vec2 s = vec2(sqrt(3.0), 1.0);
#else
    const vec2 s = vec2(1.0, sqrt(3.0));
#endif

float Hex(in vec2 p)
{    
    p = abs(p);    

    #ifdef FLAT_TOP_HEXAGON
        return max(dot(p, s * 0.5), p.y);
    #else
        return max(dot(p, s * 0.5), p.x);
    #endif  
}

// xy = 2D distance in each cell. Use with Hex() to get hexagon shape.
// zw = hexagonal-quantized UV coordinates (cell ID).

// MB: See, https://andrewhungblog.wordpress.com/2018/07/28/shader-art-tutorial-hexagonal-grids/

// Essentially rows of points, where every other row is shifted.
// https://blender.stackexchange.com/questions/161701/how-to-do-uv-indexing-in-hexagonal-pattern

vec4 HexLattice(vec2 uv)
{     
    // MB: Using round (vs. floor), removing need for + 0.5.

    #ifdef FLAT_TOP_HEXAGON
        vec4 hexCenter = round(vec4(uv, uv - vec2(1.0, 0.5)) / s.xyxy);
    #else
        vec4 hexCenter = round(vec4(uv, uv - vec2(0.5, 1.0)) / s.xyxy);
    #endif    

    vec4 offset = vec4(uv - hexCenter.xy * s, uv - (hexCenter.zw + 0.5) * s);

    return

    dot(offset.xy, offset.xy) < dot(offset.zw, offset.zw) ?     
    vec4(offset.xy, hexCenter.xy) : vec4(offset.zw, hexCenter.zw + 0.5); 
}

// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83

float rand(vec2 c){
	return fract(sin(dot(c.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

#define PI 3.14159

float noise(vec2 p, float freq ){
	float unit = 1.0/freq;
	vec2 ij = floor(p/unit);
	vec2 xy = mod(p,unit)/unit;
	//xy = 3.*xy*xy-2.*xy*xy*xy;
	xy = .5*(1.-cos(PI*xy));
	float a = rand((ij+vec2(0.,0.)));
	float b = rand((ij+vec2(1.,0.)));
	float c = rand((ij+vec2(0.,1.)));
	float d = rand((ij+vec2(1.,1.)));
	float x1 = mix(a, b, xy.x);
	float x2 = mix(c, d, xy.x);
	return mix(x1, x2, xy.y);
}

// Render.

float Glow(float sdf, float width, float power)
{
    return pow(width / sdf, power);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized UVs and mouse.

    vec2 uv = fragCoord / iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy;

    float aspectRatio = iResolution.x / iResolution.y;    

    // Tiling between range, with mouse as input.

    float tiling = mix(8.0, 32.0, mouse.x);

    vec2 hexTiling = vec2(tiling);
    hexTiling.x *= aspectRatio;

    vec2 hexUV = uv * hexTiling;
    vec2 hexMouse = mouse * hexTiling;

    vec4 hexGrid = HexLattice(hexUV);    
    vec4 hexGridOriginal = hexGrid;

    vec3 colour;

    #ifdef FLAT_TOP_HEXAGON
        hexGrid.z *= aspectRatio;
    #else
        hexGrid.w *= aspectRatio;
    #endif 

    vec2 hexGridUV = hexGrid.zw / hexTiling;
    vec2 hexGridOriginalUV = hexGridOriginal.zw / hexTiling;

    #ifndef FLAT_TOP_HEXAGON    
        hexGridUV *= vec2(1.0, 0.975);    
    #endif

    colour.rg = hexGridUV.xy; // Colour red/green (XY) = UV.

    // Assign the blue (Z) value of colour to something animated wildly.

    colour.b = uv.y * (abs(sin(iTime * 10.0)) * 5.0);

    // Get distance field.
    // Used as an animated hexagonal mask with a tiling scan.

    float hex = Hex(hexGrid.xy) * 2.0;

    float scanSpeed = 1.0;

    // Tiling scan (hexGridOriginal.w), as opposed to using uv.y.

    float noiseTiling = 2.0;
    vec2 noiseAnimation = vec2(-2.0, -2.0);        

    float animatedNoise = noise(hexGrid.zw + vec2(noiseAnimation * iTime), noiseTiling);

    float scanBase = hexGridOriginalUV.y;

    // For a wild effect.    
    //scanBase *= hexGridOriginal.z; 

    // For a wipe effect.    
    //scanBase *= hexGridOriginal.z / tiling;

    // Offset scan up/down (using hex-quantized noise).

    scanBase += animatedNoise * 0.2;
    scanBase += sin(hexGridUV.x * 64.0) * 0.05;

    // Repeating scan animation (using fract), with vertical mouse input.

    float scan = fract(scanBase + (iTime * scanSpeed) + mouse.y);

    colour *= step(smoothstep(0.0, 0.7, scan), 1.0 - hex);

    // Apply noise.

    colour *= animatedNoise;

    // Highlight mouse tile.

    if (hexGridOriginal.zw == HexLattice(hexMouse).zw)
    {
        vec3 glowColour = vec3(0.5, 0.1, 1.0);
        colour = Glow(hex, 0.5, 5.0) * glowColour; 
    }  

    // Mix with texture. Use either iChannel [0] or [1].

    vec3 textureColour = texture(iChannel0, hexGridUV).rgb;
    colour = mix(colour, textureColour, 0.3);

    fragColor = vec4(colour, 1.0);
}

