vec3 rgb2hsv(vec3 c) { vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0); vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g)); vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r)); float d = q.x - min(q.w, q.y); float e = 1.0e-10; return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x); } vec3 hsv2rgb(vec3 c) { vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0); vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www); return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y); } float rand(vec2 n) { return fract(sin(cos(dot(n, vec2(12.9898,12.1414)))) * 83758.5453); } float noise(vec2 n) { const vec2 d = vec2(0.0, 1.0); vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n)); return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y); } float fbm(vec2 n) { float total = 0.0, amplitude = 1.0; for (int i = 0; i <5; i++) { total += noise(n) * amplitude; n += n*1.71; amplitude *= 0.47; } return total; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Read channel0 (assumes same resolution as output; typical in Shadertoy)
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 base = texture(iChannel0, uv);

    // Only fill where channel0 is non-transparent
    float aThresh = 0.001;              // tweak if you have fringe pixels
    if (base.a <= aThresh) {
        fragColor = base;               // or vec4(0.0) if you want fully empty outside
        return;
    }

    // --- your existing shader below (unchanged) ---
    const vec3 c1 = vec3(0.5, 0.0, 0.1);
    const vec3 c2 = vec3(0.9, 0.1, 0.0);
    const vec3 c3 = vec3(0.2, 0.1, 0.7);
    const vec3 c4 = vec3(1.0, 0.9, 0.1);
    const vec3 c5 = vec3(0.1);
    const vec3 c6 = vec3(0.9);

    vec2 speed = vec2(1.2, 0.1);
    float shift = 1.327 + sin(iTime*2.0)/2.4;
    float alpha = base.a;               // keep the original alpha (recommended)

    float dist = 3.5 - sin(iTime*0.4)/1.89;

    vec2 p = fragCoord.xy * dist / iResolution.xx;
    p.x -= iTime/1.1;
    float q  = fbm(p - iTime * 0.01 + 1.0*sin(iTime)/10.0);
    float qb = fbm(p - iTime * 0.002 + 0.1*cos(iTime)/5.0);
    float q2 = fbm(p - iTime * 0.44 - 5.0*cos(iTime)/7.0) - 6.0;
    float q3 = fbm(p - iTime * 0.9  - 10.0*cos(iTime)/30.0) - 4.0;
    float q4 = fbm(p - iTime * 2.0  - 20.0*sin(iTime)/20.0) + 2.0;
    q = (q + qb - .4*q2 - 2.0*q3 + .6*q4)/3.8;

    vec2 r = vec2(
        fbm(p + q/2.0 + iTime*speed.x - p.x - p.y),
        fbm(p + q     - iTime*speed.y)
    );

    vec3 c = mix(c1, c2, fbm(p + r)) + mix(c3, c4, r.x) - mix(c5, c6, r.y);
    vec3 color = vec3(c * cos(shift * fragCoord.y / iResolution.y));
    color += .05;
    color.r *= .8;

    vec3 hsv = rgb2hsv(color);
    hsv.y *= hsv.z * 1.1;
    hsv.z *= hsv.y * 1.13;
    hsv.y = (2.2 - hsv.z*.9) * 1.20;
    color = hsv2rgb(hsv);

    // If you want the fire "inside" the mask but keep the original RGB too:
    // color = mix(base.rgb, color, 1.0);

    fragColor = vec4(color, alpha) * base.a;
}

#define SPEED 1.     // feel free to change this!
#define MORE_CHAOS // comment this line to have less chaos :P

const vec3 RED = vec3(255,4,0) / 255.;
const vec3 PINK = RED + vec3(.1, .0, .2);

const float PI = acos(-1.);

// https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

mat2x2 rotate(float r) {
    return mat2x2(cos(r), sin(r), -sin(r), cos(r));
}

float dot2(vec2 p) {
    return dot(p, p);
}

// https://iquilezles.org/articles/distfunctions/
float opExtrusion( in vec3 p, float d, in float h )
{
    vec2 w = vec2( d, abs(p.z) - h );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float sdHeart( in vec2 p )
{
    p.y += .5;
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float t = iTime;
    vec3 col = vec3(0.);
    vec2 uv = (2. * gl_FragCoord.xy - iResolution.xy) / iResolution.y;
    vec2 uv2 = fragCoord.xy / iResolution.xy;
    vec4 uv4 = texture(iChannel0, uv2);
    const float layer_dist = 5.;

    float d = 0.;
    vec3 ro = vec3(0., layer_dist * .5, -t*3.*SPEED);
    vec3 rd = normalize(vec3(uv, -1.));

    ro.x += sin(t*.5)*layer_dist;
    rd.xz *= rotate(sin(t*.4*SPEED)*.3);
    rd.xy *= rotate(sin(t*.4*SPEED)*.3);

    for (int i = 0; i < 30; ++i) {
        vec3 p = ro + rd*d;

        vec3 s = vec3(3., layer_dist, 8.);
        vec3 id = round(p / s);
        float sig = mod(id.z, 2.) < 0.5 ? 1. : -1.;
        p.xy *= rotate(hash11(id.z*100.)*9e2 + sig*t*.1*SPEED);
        p.x += sig * t*SPEED;

        id = round(p / s);

        #ifdef MORE_CHAOS
            p.y += sin(id.x*2. + t*SPEED);
            p.z += cos(id.x*2. + t*SPEED);
        #else
            p.y += sin(id.x + t*SPEED);
            p.z += cos(id.x + t*SPEED);
        #endif

        id = round(p / s);
        p -= s*id;

        p.xy *= rotate(t*SPEED+hash11(id.x)*9e2);
        p.xz *= rotate(t*SPEED+hash11(id.y)*9e2);

        float dt = opExtrusion(p, sdHeart(p.xy), .1);
        dt = max(abs(dt)*.5, 1e-3);

        col += mix(PINK, RED, sin(t+hash11(id.z+id.x+id.y)*999.)*.5+.5) / dt;
        d += dt;
    }

    col = tanh(col * .001);

    fragColor = vec4(col.rgb, uv4.a);
}

