// NEW: banding breakers (keep modest)
const float crossWindStrength = 0.65; // 0..1  (vary sideways component)
const float swirlStrength     = 0.28; // 0..1  (adds curl-like warping)
const float swirlScale        = 0.18; // 0.08..0.35 (lower = bigger swirls)
// -------------

mat2 mm2(float a){ float c=cos(a), s=sin(a); return mat2(c,s,-s,c); }

float tri(float x){ return abs(fract(x)-0.5); }
vec3  tri3(vec3 p){
    return vec3(
        tri(p.z + tri(p.y)),
        tri(p.z + tri(p.x)),
        tri(p.y + tri(p.x))
    );
}

float triNoise3d(vec3 p, float spd)
{
    float z = 1.4;
    float rz = 0.0;
    vec3 bp = p;

    for (float i = 0.0; i <= 3.0; i++ )
    {
        vec3 dg = tri3(bp * 2.0);
        p += (dg + iTime * spd);

        bp *= 1.8;
        z  *= 1.5;
        p  *= 1.2;

        rz += (tri(p.z + tri(p.x + tri(p.y)))) / z;
        bp += 0.14;
    }
    return rz;
}

// Small curl-ish warp in XZ using finite differences of the same noise
vec2 swirlWarp(vec3 p, float dist)
{
    // Make this low-frequency to avoid grainy warping
    vec3 q = p;
    q.xz *= swirlScale;
    q *= 2.2 / (dist + 20.0);

    float e = 0.55; // bigger epsilon => smoother gradient
    float a = triNoise3d(q + vec3( e, 0.0, 0.0), 0.05);
    float b = triNoise3d(q + vec3(-e, 0.0, 0.0), 0.05);
    float c = triNoise3d(q + vec3(0.0, 0.0,  e), 0.05);
    float d = triNoise3d(q + vec3(0.0, 0.0, -e), 0.05);

    vec2 g = vec2(a - b, c - d);    // gradient in x,z
    vec2 v = vec2(g.y, -g.x);       // perpendicular => curl-like

    // Normalize softly so it stays stable
    float m = max(0.35, length(v));
    v /= m;

    return v;
}

float fogmap(vec3 p, float dist)
{
    // Base wind
    vec2 w = windDir * (iTime * windSpeed);

    // NEW: spatially varying crosswind (breaks perpendicular bands)
    vec2 perp = normalize(vec2(-windDir.y, windDir.x));
    float cw = triNoise3d(vec3(p.xz * 0.10, 0.7 + iTime * 0.08), 0.0); // slow, low-freq
    w += perp * (cw - 0.35) * crossWindStrength;

    // Apply wind
    p.xz += w * 1.2;

    // Gentle evolution so it "changes" not just translates
    p.xz += vec2(
        sin(iTime * evolveSpeed + p.z*0.7),
        cos(iTime * evolveSpeed + p.x*0.6)
    ) * 0.15;

    // NEW: curl-ish warp (small, distance-aware)
    p.xz += swirlWarp(p, dist) * swirlStrength;

    // Originals distance-based scaling so far dust is smoother
    float n = triNoise3d(p * 2.2 / (dist + 20.0), 0.2);

    // Ground-hugging: fade with height
    float hug = 1.0 - smoothstep(0.0, groundHug, p.y);

    return n * hug;
}

vec4 dustStorm(vec2 uv)
{
    vec2 world = uv * WORLD_SCALE;

    float dy = HEIGHT / float(STEPS);

    float T = 1.0;
    vec3  C = vec3(0.0);

    float dist = 0.5;

    for (int i = 0; i < STEPS; i++)
    {
        float y = (float(i) + 0.5) * dy;
        vec3 pos = vec3(world.x, y, world.y);

        float d = fogmap(pos, dist);

        float d2  = fogmap(pos + vec3(0.35, 0.0, -0.25), dist);
        float grd = clamp((d - d2) * 3.0, 0.1, 1.0);

        vec3 colStep = mix(dustColorA, dustColorB, 1.7 - grd) * 0.55;

        float tau   = SIGMA * d * dy;
        float stepT = exp(-tau);
        float A     = 1.0 - stepT;

        C += colStep * (A * T);
        T *= stepT;

        dist *= 1.5 + 0.3;
    }

    float alpha = clamp(1.0 - T, 0.0, 1.0);
    return vec4(C, alpha);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 st = fragCoord.xy / iResolution.xy;
    vec2 uv = st - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    fragColor = dustStorm(uv); // premultiplied alpha
}

//How to tune obscure tokens realistically
//Make tokens disappear more in thick fog: increase sigma (e.g. 2.2  3.2)
//More overall fog coverage: decrease density (e.g. 0.55  0.48)
//Denser bright cores (but keep gaps): increase thickness (e.g. 1.2  1.8)
//Softer transitions: increase softness

#ifdef GL_ES

