const vec3 mistColor = vec3(0.807843, 0.745098, 0.05098);

// --- your base settings ---
const float scale = 3.0;
const float intensityMultiplier = 2.0;
const float speed = -0.25;
const int octaves = 8;

// Density controls (same meaning as before)
const float density   = 0.37; // lower = more coverage
const float thickness = 1.0; // higher = denser cores
const float softness  = 0.9; // edge softness

// --- NEW volumetric controls ---
const float HEIGHT = 1.9;     // thickness of fog volume
const int   STEPS  = 8;      // 12..24 (higher = smoother, slower)
const float SIGMA  = 2.4;     // extinction (higher = obscures tokens more)

// Directional flow
const vec2  windDir   = normalize(vec2(1.0, 0.25));
const float windSpeed = 0.1;

// How much the fog changes with height (prevents stacked copies look)
const float heightWarp = 1.45; // 0.6..2.0
const float zDetail    = 1.6;  // 0.4..1.4

mat2 rot(float a){ float s=sin(a), c=cos(a); return mat2(c,-s,s,c); }

vec2 random2(vec2 st){
    st = vec2(dot(st,vec2(127.1,311.7)), dot(st,vec2(269.5,183.3)));
    // kept as in your original
    return -1.0 + 2.0 * fract(sin(st) * 7.0);
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    vec2 u = f*f*(3.0-2.0*f);

    return mix(
        mix(dot(random2(i + vec2(0.0,0.0)), f - vec2(0.0,0.0)),
            dot(random2(i + vec2(1.0,0.0)), f - vec2(1.0,0.0)), u.x),
        mix(dot(random2(i + vec2(0.0,1.0)), f - vec2(0.0,1.0)),
            dot(random2(i + vec2(1.0,1.0)), f - vec2(1.0,1.0)), u.x), u.y);
}

float fractal_brownian_motion(vec2 coord) {
    float value = 0.0;
    float sc = 0.2;
    for (int i = 0; i < octaves; i++) {
        value += noise(coord) * sc;
        coord *= 2.0;
        sc *= 0.4;
    }
    return value + 0.2;
}

// Your 2D fog field => returns fog alpha-ish in [0..1]
float fog2D(vec2 pos, float t)
{
    // Directional flow
    vec2 wind = windDir * (t * windSpeed);

    // Your original motion (scalar FBM -> vec2)
    vec2 motion = vec2(fractal_brownian_motion(pos + wind + vec2(t * speed * 1.2, t * speed)));

    float raw = fractal_brownian_motion(pos + wind + motion);
    raw *= intensityMultiplier;

    float fog = smoothstep(density - softness, density + softness, raw);
    fog = pow(clamp(fog, 0.0, 1.0), thickness);

    return fog;
}

// Make a 3D density by varying the 2D field with z
float fog3D(vec2 basePos, float z, float t)
{
    // height profile: denser near ground (z=0)
    float h = clamp(1.0 - z / HEIGHT, 0.0, 1.0);
    float heightProfile = smoothstep(0.0, 1.0, h);

    // Make each slice different:
    // - offset + rotate based on z
    // - add z-dependent small warp
    vec2 p = basePos;

    // slight rotation per height
    p = rot(0.35 * z) * p;

    // z-dependent offsets so slices are not copies
    p += vec2(13.7 * z, -9.2 * z);

    // z-dependent warp (uses your fbm so it matches your look)
    vec2 w = vec2(
        fractal_brownian_motion(p * (0.35*zDetail) + vec2(0.0, t*0.08 + 5.1*z)),
        fractal_brownian_motion(p * (0.35*zDetail) + vec2(7.3, -t*0.07 + 2.7*z))
    ) - 0.5;

    p += w * heightWarp;

    // Evaluate your original fog at this slice
    float d = fog2D(p, t);

    return d * heightProfile;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // top-down coords
    vec2 st = fragCoord.xy / iResolution.xy;
    st *= iResolution.xy / iResolution.y;

    float t = iTime;

    // your world scale
    vec2 basePos = st * scale;

    float dz = HEIGHT / float(STEPS);

    // Beer-Lambert integration
    float T = 1.0;
    vec3  L = vec3(0.0);

    for (int i = 0; i < STEPS; i++)
    {
        float z = (float(i) + 0.5) * dz;

        float d = fog3D(basePos, z, t);

        float tau   = SIGMA * d * dz;
        float stepT = exp(-tau);
        float A     = 1.0 - stepT;

        // accumulate premultiplied fog color
        L += mistColor * (A * T);
        T *= stepT;

        if (T < 0.01) break;
    }

    float alpha = clamp(1.0 - T, 0.0, 1.0);
    fragColor = vec4(L, alpha); // premultiplied alpha
}

