	vec3 edgeBC = normalize(cross(points.center, points.bc));
    vec3 edgeCA = normalize(cross(points.center, points.ca));

    Model model, part;
    HexSpec spec;

	spec = transitionHexSpecs(
        animHex1(points.b, subdivisions),
        animHex2(points.b, subdivisions),
        animHex3(points.b, subdivisions)
    );
    part = hexModel(p, points.b, edgeAB, edgeBC, spec);
    model = part;

	spec = transitionHexSpecs(
        animHex1(points.c, subdivisions),
        animHex2(points.c, subdivisions),
        animHex3(points.c, subdivisions)
    );
    part = hexModel(p, points.c, edgeBC, edgeCA, spec);
    model = opU(model, part);

	spec = transitionHexSpecs(
        animHex1(points.a, subdivisions),
        animHex2(points.a, subdivisions),
        animHex3(points.a, subdivisions)
    );
    part = hexModel(p, points.a, edgeCA, edgeAB, spec);
    model = opU(model, part);

	return model;
}

Model map( vec3 p ){
    mat3 m = modelRotation();
    p *= m;  
    #ifndef LOOP
    	pR(p.xz, time * PI/16.);
    #endif
    Model model = geodesicModel(p);
    return model;
}

// --------------------------------------------------------
// LIGHTING
// Adapted from IQ https://www.shadertoy.com/view/Xds3zN
// --------------------------------------------------------

vec4 doLighting(Model model, vec3 pos, vec3 nor, vec3 ref, vec3 rd) {
    vec3 lightPos = normalize(vec3(.5,.5,-1.));
    vec3 backLightPos = normalize(vec3(-.5,-.3,1));
    vec3 ambientPos = vec3(0,1,0);

    vec3  lig = lightPos;
    float amb = clamp((dot(nor, ambientPos) + 1.) / 2., 0., 1.);
    float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
    float bac = pow(clamp(dot(nor, backLightPos), 0., 1.), 1.5);
    float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

    vec4 lin = vec4(0.0);
    lin += 1.20 * dif * vec4(.9, .9, .9, 1);
    lin += 0.80 * amb * vec4(.5, .7, .8, 1);
    lin += 0.30 * bac * vec4(.25, .25, .25, 1);
    lin += 0.20 * fre * vec4(1);

    vec4 albedo = model.albedo;
    vec4 col = mix(albedo * lin, albedo, model.glow);    

    return col;
}   


// --------------------------------------------------------
// Ray Marching
// Adapted from cabbibo https://www.shadertoy.com/view/Xl2XWt
// --------------------------------------------------------

const float MAX_TRACE_DISTANCE = 8.; // max trace distance
const float INTERSECTION_PRECISION = .001; // precision of the intersection
const int NUM_OF_TRACE_STEPS = 100;
const float FUDGE_FACTOR = .9; // Default is 1, reduce to fix overshoots

struct CastRay {
    vec3 origin;
    vec3 direction;
};

struct Ray {
    vec3 origin;
    vec3 direction;
    float len;
};

struct Hit {
    Ray ray;
    Model model;
    vec3 pos;
    bool isBackground;
    vec3 normal;
    vec4 color;
};

vec3 calcNormal( in vec3 pos ){
    vec3 eps = vec3( 0.001, 0.0, 0.0 );
    vec3 nor = vec3(
        map(pos+eps.xyy).dist - map(pos-eps.xyy).dist,
        map(pos+eps.yxy).dist - map(pos-eps.yxy).dist,
        map(pos+eps.yyx).dist - map(pos-eps.yyx).dist );
    return normalize(nor);
}

Hit raymarch(CastRay castRay){

    float currentDist = INTERSECTION_PRECISION * 2.0;
    Model model;

    Ray ray = Ray(castRay.origin, castRay.direction, 0.);

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){
        if (currentDist < INTERSECTION_PRECISION || ray.len > MAX_TRACE_DISTANCE) {
            break;
        }
        model = map(ray.origin + ray.direction * ray.len);
        currentDist = model.dist;
        ray.len += currentDist * FUDGE_FACTOR;
    }

    bool isBackground = false;
    vec3 pos = vec3(0);
    vec3 normal = vec3(0);
    vec4 color = vec4(0);

    if (ray.len > MAX_TRACE_DISTANCE) {
        isBackground = true;
    } else {
        pos = ray.origin + ray.direction * ray.len;
        normal = calcNormal(pos);
    }

    return Hit(ray, model, pos, isBackground, normal, color);
}


// --------------------------------------------------------
// Rendering
// --------------------------------------------------------

void shadeSurface(inout Hit hit){

    vec4 color = BACKGROUND_COLOR;

    if (hit.isBackground) {
        hit.color = vec4(0,0,0,0);
        return;
    }

    vec3 ref = reflect(hit.ray.direction, hit.normal);

    #ifdef DEBUG
        color = hit.normal * 0.5 + 0.5;
    #else 
        color = doLighting(
            hit.model,
            hit.pos,
            hit.normal,
            ref,
            hit.ray.direction
        );
    #endif

    hit.color = color;
}

vec4 render(Hit hit){
    shadeSurface(hit);
	return hit.color;
}


// --------------------------------------------------------
// Camera
// https://www.shadertoy.com/view/Xl2XWt
// --------------------------------------------------------

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void doCamera(out vec3 camPos, out vec3 camTar, out float camRoll, in float time, in vec2 mouse) {
    float dist = 5.5;
    camRoll = 0.;
    camTar = vec3(0,0,0);
    camPos = vec3(0,0,-dist);
    camPos *= cameraRotation();
    camPos += camTar;
}


// --------------------------------------------------------
// Gamma
// https://www.shadertoy.com/view/Xds3zN
// --------------------------------------------------------

const float GAMMA = 2.2;

vec4 gamma(vec4 color, float g) {
    return pow(color, vec4(g));
}

vec4 linearToScreen(vec4 linearRGB) {
    return gamma(linearRGB, 1.0 / GAMMA);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time = iTime;

    #ifdef LOOP
        #if LOOP == 1
            time = mod(time, 2.);   
        #endif
        #if LOOP == 2
            time = mod(time, 4.);   
        #endif
        #if LOOP == 3
            time = mod(time, 2.);
    	#endif
    #endif

    initIcosahedron();

    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy / iResolution.xy;

    vec3 camPos = vec3( 0., 0., 2.);
    vec3 camTar = vec3( 0. , 0. , 0. );
    float camRoll = 0.;

    // camera movement
    doCamera(camPos, camTar, camRoll, iTime, m);

    // camera matrix
    mat3 camMat = calcLookAtMatrix( camPos, camTar, camRoll );  // 0.0 is the camera roll

    // create view ray
    vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    Hit hit = raymarch(CastRay(camPos, rd));

    vec4 color = render(hit);

    #ifndef DEBUG
        color = linearToScreen(color);
    #endif

    fragColor = color;
}

#define NUM_BUTTERFLIES 12

float time;

// Noise functions from IQ.
float hash( float n ) { return fract(sin(n)*43758.5453123); }
float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0;
    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+157.0), hash(n+158.0),f.x),f.y);
}

float fbm2(vec2 p)
{
   float f = 0.0, x;
   for(int i = 1; i <= 9; ++i)
   {
      x = exp2(float(i));
      f += (noise(p * x) - 0.5) / x;
   }
   return f;
}


float sq(float x)
{
	return x*x;
}

vec2 rotate(float a,vec2 v)
{
	return vec2(cos(a)*v.x+sin(a)*v.y, cos(a)*v.y-sin(a)*v.x);
}

mat3 rotateXMat(float a)
{
	return mat3(1.0, 0.0, 0.0, 0.0, cos(a), -sin(a), 0.0, sin(a), cos(a));
}

mat3 rotateYMat(float a)
{
	return mat3(cos(a), 0.0, -sin(a), 0.0, 1.0, 0.0, sin(a), 0.0, cos(a));
}

vec3 butterflyColor(int i)
{
    float id = float(i) * 14459.123;

    // Random hue-like variation
    float r = hash(id + 1.0);
    float g = hash(id + 2.0);
    float b = hash(id + 3.0);

    // Make colors more vivid and less muddy
    vec3 col = normalize(vec3(r, g, b));
    col = pow(col, vec3(3.6)); // optional: punchier
    col = mix(col, vec3(1.0), 0.2); // brighten slightly

    return col;
}

vec3 wing0Node(int i)
{
	if(i<1)
		return vec3(-0.23,0.0,1.0);
	if(i<2)
		return vec3(-0.7,0.25,1.0);
	if(i<3)
		return vec3(-0.4,0.8,1.0);
	if(i<4)
		return vec3(-0.8,0.24,1.3);
	if(i<5)
		return vec3(-0.8,0.84,0.6);
	if(i<6)
		return vec3(-0.9,0.4,1.2);
	if(i<7)
		return vec3(-1.04,0.6,1.2);
	return vec3(-0.1,-0.1,1.0);
}

vec3 wing1Node(int i)
{
	if(i<1)
		return vec3(0.1,0.3,1.0);
	if(i<2)
		return vec3(-0.3,0.4,1.0);
	if(i<3)
		return vec3(-0.3,0.2,1.0);
	if(i<4)
		return vec3(-0.25,-0.1,1.0);
	if(i<5)
		return vec3(-0.2,-0.25,1.0);
	if(i<6)
		return vec3(-0.05,-0.5,1.0);
	return vec3(0.5,-0.2,1.0);
}

vec3 wing0NodeTransformed(int i)
{
	return (wing0Node(i)+vec3(-0.7,-0.05,0.0))*vec3(vec2(1.2,1.0)*0.7,1.0);
}

vec3 wing1NodeTransformed(int i)
{
	return (wing1Node(i)+vec3(-0.7,-0.05,0.0))*vec3(vec2(1.2,1.0)*0.7,1.0);
}

vec3 wing0Tex(vec2 p)
{
	p=rotate(-0.7,p+vec2(0.3,0.0));

	float a=1e3;
	float b=1e3;

	int cn=0;
	float cnd=1e3;
	for(int i=0;i<8;i+=1)
	{
		float d=distance(p,wing0NodeTransformed(i).xy);
		if(d<cnd)
		{
			cnd=d;
			cn=i;
		}
	}

	float s=0.04+pow(max(0.0,-p.y*0.4),1.3)+pow(max(0.0,-p.x-1.0),1.3)*0.1;

	s+=0.2*(1.0-smoothstep(0.0,0.4,distance(p,vec2(-1.2,0.2)))) +
		0.2*(1.0-smoothstep(0.0,0.3,distance(p,vec2(-1.0,0.5))));

	float c=0.0;
	for(int j=0;j<8;j+=1)
	{
		if(j==cn)
			continue;

		vec3 n0=wing0NodeTransformed(cn);
		vec3 n1=wing0NodeTransformed(j);
		vec2 nd=n1.xy-n0.xy;
		float d=dot(p-(n0.xy+nd*0.5),normalize(nd))+s*n0.z;
		c+=sq(max(0.0,d));
	}

	float p0=sq(max(0.0,dot(p-vec2(-0.5,0.0),normalize(vec2(1.0,-0.9)))));

	c+=sq(max(0.0,(distance(p+vec2(0.6,1.45),vec2(0.0))-2.0+s))) + p0 +
		sq(max(0.0,dot(p-vec2(-0.6,-0.2),normalize(vec2(-0.3,-0.9)))));

	float c2=sq(max(0.0,(distance(p+vec2(0.6,1.55),vec2(0.0))-2.0))) + p0 +
		sq(max(0.0,dot(p-vec2(-0.6,-0.2),normalize(vec2(-0.3,-0.9)))-0.1));

	vec2 xa=vec2(-1.7,-0.0),xb=vec2(-0.8,-0.3);
	vec2 xs=vec2(0.6,1.0);

	vec2 u=mix(xa,xb,floor(clamp(dot(p-xa,xb-xa)/dot(xb-xa,xb-xa),0.0,1.0)*5.0+0.5)/5.0);

 	float x=max(1.0-smoothstep(0.06,0.07,distance(p,vec2(-1.2,0.3))),
				1.0-smoothstep(0.02,0.025,length((p-u)*xs)));

	return vec3(1.0-smoothstep(s-0.015,s-0.015+0.006,sqrt(c)),1.0-smoothstep(0.1,0.106,sqrt(c2)-0.03),x);
}

vec3 wing1Tex(vec2 p)
{
	p=p+vec2(0.0,0.16);

	float a=1e3;
	float b=1e3;

	int cn=0;
	float cnd=1e3;
	for(int i=0;i<7;i+=1)
	{
		float d=distance(p,wing1NodeTransformed(i).xy);
		if(d<cnd)
		{
			cnd=d;
			cn=i;
		}
	}

	float s=0.04+pow(max(0.0,-p.y*0.4),1.3)+pow(max(0.0,-p.x-1.0),1.3)*0.1;

	float c=0.0;
	for(int j=0;j<7;j+=1)
	{
		if(j==cn)
			continue;

		vec3 n0=wing1NodeTransformed(cn);
		vec3 n1=wing1NodeTransformed(j);
		vec2 nd=n1.xy-n0.xy;
		float d=dot(p-(n0.xy+nd*0.5),normalize(nd))+s*n0.z;
		c+=sq(max(0.0,d));
	}

	float p0=sq(max(0.0,dot(p-vec2(-0.5,-0.4),normalize(vec2(1.0,-0.7)))));
	float p1=sq(max(0.0,dot(p-vec2(-0.3,0.3),normalize(-vec2(0.1,-0.9)))));

	c+=sq(max(0.0,(distance(p+vec2(0.52,-0.1),vec2(0.0))-0.5))) + p0 + p1;

	float c2=sq(max(0.0,(distance(p+vec2(0.5,-0.0),vec2(0.0))-0.53))) + p0 + p1;

	float xr=0.7;
	vec2 xa=vec2(-0.4,0.05);

	vec2 pd=rotate(-0.2,p-xa);
	float ang=mix(-3.1,-1.8,floor((clamp(atan(pd.y,pd.x),-3.1,-1.8)+3.1)/1.299*6.0+0.5)/6.0);

	float x=1.0-smoothstep(0.02,0.025,distance(pd,vec2(cos(ang),sin(ang))*xr));

	return vec3(1.0-smoothstep(s-0.015,s-0.015+0.006,sqrt(c)),1.0-smoothstep(0.1,0.106,sqrt(c2)-0.03),x);
}

vec4 wing(vec2 p, vec3 baseColor)
{
	p+=fbm2(p*4.0)*0.02;

	vec3 wc = mix(
    baseColor,
    baseColor * 0.3,
    fbm2(p * vec2(1.0, 16.0)) * 0.26 +
    pow(clamp((p.y * 4.0 - abs(p.x) * 2.0) / 3.0, 0.0, 1.0), 2.0)
) * 0.8;

	wc=pow(wc,vec3(1.5));

	vec3 c0=wing0Tex(p);
	vec3 c1=wing1Tex(p);

	vec3 col=vec3(0.0);

	col.rgb=mix(mix(vec3(0.0),c0.x*wc,c0.y),c1.x*wc,c1.y);
	col.rgb=mix(col.rgb,vec3(1.0),c0.z);
	col.rgb=mix(col.rgb,vec3(1.0),c1.z);

	return vec4(col,max(c0.y,c1.y));
}

vec3 traceButterflyWing(vec3 ro,vec3 rd,vec3 bo,vec3 bd,float flap)
{
	vec3 up=vec3(0.0,1.0,0.0);
	vec3 c=cross(bd,up);
	float flapangle=mix(radians(20.0),radians(150.0),flap);
	vec3 w=cos(flapangle)*c+sin(flapangle)*up;
	float t=-dot(ro,w)/dot(rd,w);
	vec3 s=cross(w,bd);
	vec3 rp=ro+rd*t;
	return vec3(dot(rp,s),dot(rp,bd),t);
}

// Returns: rgb = color, a = opacity mask (0..1)
// out hitT: distance along ray (for depth sorting)
vec4 traceButterfly(vec3 ro, vec3 rd, vec3 bo, vec3 bd, float flap, vec3 baseColor, out float hitT)

{
    flap = pow(flap, 0.75);
    bo.y -= flap * 0.5;
    ro -= bo;

    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 c = cross(bd, up);

    vec3 w0 = traceButterflyWing(ro, rd, bo, bd, flap);

    // mirror to get the other wing
    vec3 ro2 = ro - dot(ro, c) * 2.0 * c;
    vec3 rd2 = rd - dot(rd, c) * 2.0 * c;
    vec3 w1 = traceButterflyWing(ro2, rd2, bo, bd, flap);

    // If both wing planes are far out of range, bail
    if (max(abs(w0.x), abs(w0.y)) > 2.0 && max(abs(w1.x), abs(w1.y)) > 2.0) {
        hitT = 1e4;
        return vec4(0.0);
    }

    // Wing textures: .rgb = color, .a = coverage/opacity mask
vec4 t0 = wing(w0.xy, baseColor);
vec4 t1 = wing(w1.xy, baseColor);

    bool u0 = (t0.a > 0.001) && (w0.z > 0.0);
    bool u1 = (t1.a > 0.001) && (w1.z > 0.0);

    if (!u0 && !u1) {
        hitT = 1e4;
        return vec4(0.0);
    }

    // Pick nearest valid hit
    if (u0 && (!u1 || w0.z <= w1.z)) {
        hitT = w0.z;
        return vec4(t0.rgb, t0.a);
    } else {
        hitT = w1.z;
        return vec4(t1.rgb, t1.a);
    }
}

vec3 butterflyPath(float t)
{
	return vec3(cos(t),cos(t*0.22)*1.0+sin(t*4.0)*0.1,sin(t*1.3))*4.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - vec2(1.0);
    uv.x *= iResolution.x / iResolution.y;

    time = iTime;
    mat3 m = rotateYMat(time * 0.2) * rotateXMat(cos(time * 0.12) * 0.7);

    vec3 ro = m * vec3(0.0, 0.0, 7.0);
    vec3 rd = m * normalize(vec3(uv, -1.2));

    vec3 bestCol = vec3(0.0);
    float bestAlpha = 0.0;
    float bestT = 1e4;

    for (int i = 0; i < NUM_BUTTERFLIES; i += 1)
    {
        vec3 color = butterflyColor(i);
        float t = time + float(i) * 10.2;
        vec3 bo = butterflyPath(t);
        vec3 bd = vec3(normalize(butterflyPath(t + 1e-2).xz - bo.xz), 0.0).xzy;

        float hitT;
        vec4 b = traceButterfly(ro, rd, bo, bd, 0.5 + 0.5 * cos(t * 9.0), color, hitT);

        if (hitT < bestT && b.a > 0.001) {
            bestT = hitT;
            bestCol = b.rgb;
            bestAlpha = b.a;
        }
    }

    // If you want a transparent background (no environment):
    fragColor = vec4(sqrt(bestCol), bestAlpha);

    // --- OR, if you want to keep the environment behind it but still output alpha:
    // vec3 env = texture(iChannel0, rd).rgb;
    // vec3 col = mix(env, bestCol, bestAlpha);
    // fragColor = vec4(sqrt(col), bestAlpha);
}

// Source:
// http://coding-experiments.blogspot.com/2010/07/convolution.html

#define EMBOSS_WIDTH	0.0015
#define EMBOSS_HEIGHT	0.0015

// samples a pixel centerd at "uv" and with offset of dx|dy
vec4 sample_pixel(in vec2 uv, in float dx, in float dy)
{
    return texture(iChannel0, uv + vec2(dx, dy));
}

// convolves a SINGLE channel of input color_matrix
float convolve(in float[9] kernel, in vec4[9] color_matrix)
{
   float res = 0.0;
   for (int i=0; i<9; i++)
   {
      res += kernel[i] * color_matrix[i].a;
   }
   return clamp(res + 0.5, 0.0 ,1.0);
}

// builds a 3x3 color matrix centerd at "uv"
void build_color_matrix(in vec2 uv, out vec4[9] color_matrix)
{
    float dxtex = EMBOSS_WIDTH;
    float dytex = EMBOSS_HEIGHT;

	color_matrix[0].rgb = sample_pixel(uv, -dxtex, -dytex)	.rgb;
	color_matrix[1].rgb = sample_pixel(uv, -dxtex, 	0.0)	.rgb;
    color_matrix[2].rgb = sample_pixel(uv, -dxtex, 	dytex)	.rgb;
	color_matrix[3].rgb = sample_pixel(uv, 0.0, 	-dytex)	.rgb;
	color_matrix[4].rgb = sample_pixel(uv, 0.0, 	0.0)	.rgb;
    color_matrix[5].rgb = sample_pixel(uv, 0.0, 	dytex)	.rgb;
	color_matrix[6].rgb = sample_pixel(uv, dxtex, 	-dytex)	.rgb;
	color_matrix[7].rgb = sample_pixel(uv, dxtex, 	0.0)	.rgb;
    color_matrix[8].rgb = sample_pixel(uv, dxtex, 	dytex)	.rgb;
}

// builds a mean color matrix (off of .rgb of input).
// NOTE: stores the output in alpha channel
void build_mean_matrix(inout vec4[9] color_matrix)
{
   for (int i=0; i<9; i++)
   {
      color_matrix[i].a = (color_matrix[i].r + color_matrix[i].g + color_matrix[i].b) / 3.;
   }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /*	2.	0.	0.
		0.	-1	0.
		0.	0.	-1	*/

    float kerEmboss[9];
    kerEmboss[0] = 2.0;
    kerEmboss[1] = 0.0;
    kerEmboss[2] = 0.0;
    kerEmboss[3] = 0.0;
    kerEmboss[4] = -1.;
    kerEmboss[5] = 0.0;
    kerEmboss[6] = 0.0;
    kerEmboss[7] = 0.0;
    kerEmboss[8] = -1.;

    vec4 pixel_matrix[9];
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 uv4 = texture(iChannel0, uv);
    build_color_matrix(uv, pixel_matrix);
    build_mean_matrix(pixel_matrix);

    float convolved = convolve(kerEmboss, pixel_matrix);
	fragColor = vec4(vec3(convolved) ,uv4.a);
}

//<3d simplex noise by nikat https://www.shadertoy.com/view/XsX3zB>
vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

const int octs = 5;

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}
//</3d simplex noise by nikat https://www.shadertoy.com/view/XsX3zB>
float fbm(vec2 xy, float z){
    float f = 1.0;
    float a = 1.0;
    float t = 0.0;
    float a_bound = 0.0;
    for(int i=0;i<octs;i++){
        t += a*simplex3d(vec3(xy*f,z*f));
        f *= 2.0;
        a_bound += a;
        a *= 0.5;
    }
    return t/a_bound;
}
float noise_final_comp(vec2 xy, float z){
    float value = fbm(vec2(xy.x / 200.0+513.0, xy.y / 200.0+124.0), z);
    value = 1.0-abs(value);
    value = value*value;
    return value*2.0-1.0;
}
float noise_final(vec2 xy, float z){
        float value = fbm(vec2((noise_final_comp(xy, z)*15.0+xy.x) / 100.0,(noise_final_comp(xy+300.0, z)*15.0+xy.y) / 100.0), z*1.5);
        return max(0.0, min(1.0, (value*0.5+0.5)*1.3));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord*500.0/iResolution.x;

    float p = noise_final(uv,iTime*0.025+0.05*sin(iTime*0.2+(uv.x*0.3*(sin(iTime/30.0)-0.3)+uv.y)/265.0));
    vec2 rC = vec2(max(iResolution.x,iResolution.y),
                   min(iResolution.x,iResolution.y));
    p*=clamp(1.3-1.5*length(fragCoord-0.5*vec2(rC.x,rC.y))/rC.y,0.0,1.0);
    fragColor = vec4(p,p,p,p);
}

