// size of ripple
#define Size_Ripple 10.
// number of ripple
#define Num_Ripple 90.

// Set to 1 to hash twice. Slower, but less patterns.
#define DOUBLE_HASH 0

// Hash functions shamefully stolen from:
// https://www.shadertoy.com/view/4djSRW
#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv0 = fragCoord.xy / iResolution.xy ;
	vec2 uv = uv0 ;
    uv.y = (uv.y - 0.5) / (iResolution.x/ iResolution.y) + 0.5;
    vec2 p0 = floor(uv);
    float h = 1e-3;

    vec2 wave = vec2(0.);

    for(int i = 1; i <= int(Num_Ripple); i++){
        vec2 center = hash22(vec2(i, i));
        float t = fract(0.3*iTime +  hash12(vec2(i, i)));
        vec2 uv2 = (uv - center) / (0.01 * Size_Ripple);
        float distStatic = length(uv2 * 1.2);
        float dist = length(uv2) - 2. * t * 1.;
        float frequency = 30.;

        float d1 = dist - h;
        float d2 = dist + h;

        float wave1 =  sin(frequency * d1) * smoothstep(-0.6, -0.3, d1) * smoothstep(0., -0.3, d1);
        float wave2 =  sin(frequency * d2) * smoothstep(-0.6, -0.3, d2) * smoothstep(0., -0.3, d2);

        wave += 0.8 * normalize(uv2) * (wave1 - wave2) * 30. * clamp((1. - distStatic), 0. ,1.);
    }


    float intensity = mix(0.01, 0.15, smoothstep(0.1, 0.6, abs(fract(0.05*iTime + 0.5)*2.-1.)));
    vec3 n = vec3(wave, sqrt(1. - dot(wave, wave)));
    vec3 color = texture(iChannel0, uv0 - intensity*n.xy).rgb + 5.*pow(clamp(dot(n, normalize(vec3(1., 0.7, 0.5))), 0., 1.), 6.);
	fragColor = vec4(color, 1.0);
}

// Sobel Edge Detection (Shadertoy) - preserves transparency

float luminance(vec3 c)
{
    return dot(c, vec3(0.299, 0.587, 0.114));
}

// Alpha-aware luminance so transparent pixels don't create edges
float sampleLumAlpha(vec2 uv)
{
    vec4 c = texture(iChannel0, uv);
    return luminance(c.rgb) * c.a;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 t  = 1.0 / iResolution.xy;

    vec4 center = texture(iChannel0, uv);

    // If fully transparent, keep it fully transparent (and avoid halos)
    if (center.a <= 0.0)
    {
        fragColor = vec4(0.0);
        return;
    }

    // Sample 3x3 neighborhood (alpha-weighted)
    float tl = sampleLumAlpha(uv + t*vec2(-1,  1));
    float tc = sampleLumAlpha(uv + t*vec2( 0,  1));
    float tr = sampleLumAlpha(uv + t*vec2( 1,  1));

    float ml = sampleLumAlpha(uv + t*vec2(-1,  0));
    float mc = luminance(center.rgb) * center.a;
    float mr = sampleLumAlpha(uv + t*vec2( 1,  0));

    float bl = sampleLumAlpha(uv + t*vec2(-1, -1));
    float bc = sampleLumAlpha(uv + t*vec2( 0, -1));
    float br = sampleLumAlpha(uv + t*vec2( 1, -1));

    // Sobel kernels
    float gx = -tl - 2.0*ml - bl + tr + 2.0*mr + br;
    float gy =  tl + 2.0*tc + tr - bl - 2.0*bc - br;

    float edge = length(vec2(gx, gy));

    // Normalize + contrast (tweak as needed)
    edge = smoothstep(0.2, 0.3, edge);

    // Preserve original alpha
    fragColor = vec4(vec3(edge), center.a);
}

#define DOTSIZE 1.48
#define D2R(d) radians(d)
#define MIN_S 4.0
#define MAX_S 4.0
#define SPEED 0.0

#define SST 0.888
#define SSQ 0.288

#define ORIGIN (0.5 * iResolution.xy)
float R;
float S;

vec4 rgb2cmyki(in vec3 c)
{
	float k = max(max(c.r, c.g), c.b);
	return min(vec4(c.rgb / k, k), 1.0);
}

vec3 cmyki2rgb(in vec4 c)
{
	return c.rgb * c.a;
}

vec2 px2uv(in vec2 px)
{
	return vec2(px / iResolution.xy);
}

vec2 grid(in vec2 px)
{
	return px - mod(px,S);
}

vec4 ss(in vec4 v)
{
	return smoothstep(SST-SSQ, SST+SSQ, v);
}

vec4 halftone(in vec2 fc,in mat2 m)
{
	vec2 smp = (grid(m*fc) + 0.5*S) * m;
	float s = min(length(fc-smp) / (DOTSIZE*0.5*S), 1.0);
    vec3 texc = texture(iChannel0, px2uv(smp+ORIGIN)).rgb;
    texc = pow(texc, vec3(2.2)); // Gamma decode.
	vec4 c = rgb2cmyki(texc);
	return c+s;
}

mat2 rotm(in float r)
{
	float cr = cos(r);
	float sr = sin(r);
	return mat2(
		cr,-sr,
		sr,cr
	);
}

vec4 halftone( in vec2 fragCoord )
{
    vec4 fragColor = vec4(1.0);
    R = SPEED*0.333*iTime;
    S = MIN_S + (MAX_S-MIN_S) * (0.5 - 0.5*cos(SPEED*iTime));

	if (iMouse.z > 0.5)
	{
		S = MIN_S + (MAX_S-MIN_S) * 2.0*abs(iMouse.x-ORIGIN.x) / iResolution.x;
		R = D2R(180.0 * (iMouse.y-ORIGIN.y) / iResolution.y);
	}

	vec2 fc = fragCoord.xy - ORIGIN;

	mat2 mc = rotm(R + D2R(15.0));
	mat2 mm = rotm(R + D2R(75.0));
	mat2 my = rotm(R);
	mat2 mk = rotm(R + D2R(45.0));

	float k = halftone(fc, mk).a;
	vec3 c = cmyki2rgb(ss(vec4(
		halftone(fc, mc).r,
		halftone(fc, mm).g,
		halftone(fc, my).b,
		halftone(fc, mk).a
	)));

    c = pow(c, vec3(1.0/2.2)); // Gamma encode.
	fragColor = vec4(c, 1.0);
    return fragColor;
}

// Bilateral Filter for Younger. starea.
// URL: https://www.shadertoy.com/view/XtVGWG
// Press mouse for comparison.
// Filter forked from mrharicot: https://www.shadertoy.com/view/4dfGDH 
// Skin detection forked from carlolsson's Skin Detection https://www.shadertoy.com/view/MlfSzn#
// With performance improvement by athlete
#define SIGMA 10.0
#define BSIGMA 0.1
#define MSIZE 15
//#define USE_CONSTANT_KERNEL
//#define SKIN_DETECTION

const bool GAMMA_CORRECTION = false; 
float kernel[MSIZE];

float normpdf(in float x, in float sigma) {
	return 0.39894 * exp(-0.5 * x * x/ (sigma * sigma)) / sigma;
}

float normpdf3(in vec3 v, in float sigma) {
	return 0.39894 * exp(-0.5 * dot(v,v) / (sigma * sigma)) / sigma;
}

float normalizeColorChannel(in float value, in float min, in float max) {
    return (value - min)/(max-min);
}

vec4 bilateral( in vec2 fragCoord ) {
	vec4 fragColor = vec4(1.0);
    vec3 c = texture(iChannel0, (fragCoord.xy / iResolution.xy)).rgb;
    const int kSize = (MSIZE - 1) / 2;
    vec3 final_colour = vec3(0.0);
    float Z = 0.0;

#ifdef USE_CONSTANT_KERNEL
    // unfortunately, WebGL 1.0 does not support constant arrays...
    kernel[0] = kernel[14] = 0.031225216;
    kernel[1] = kernel[13] = 0.033322271;
    kernel[2] = kernel[12] = 0.035206333;
    kernel[3] = kernel[11] = 0.036826804;
    kernel[4] = kernel[10] = 0.038138565;
    kernel[5] = kernel[9]  = 0.039104044;
    kernel[6] = kernel[8]  = 0.039695028;
    kernel[7] = 0.039894000;
    float bZ = 0.2506642602897679;
#else
	//create the 1-D kernel
	for (int j = 0; j <= kSize; ++j) {
		kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), SIGMA);
	}
    float bZ = 1.0 / normpdf(0.0, BSIGMA);
#endif


    vec3 cc;
    float factor;
    //read out the texels
    for (int i=-kSize; i <= kSize; ++i) {
        for (int j=-kSize; j <= kSize; ++j) {
            cc = texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy).rgb;
            factor = normpdf3(cc-c, BSIGMA) * bZ * kernel[kSize+j] * kernel[kSize+i];
            Z += factor;
            if (GAMMA_CORRECTION) {
            	final_colour += factor * pow(cc, vec3(2.2));
            } else {
            	final_colour += factor * cc;
            }
        }
    }

    if (GAMMA_CORRECTION) {
    	fragColor = vec4(pow(final_colour / Z, vec3(1.0/2.2)), 1.0);
    } else {
    	fragColor = vec4(final_colour / Z, 1.0);
    }

    bool isSkin = true; 

#ifdef SKIN_DETECTION
    isSkin = false; 
    vec4 rgb = fragColor * 255.0;
    vec4 ycbcr = rgb;
    ycbcr.x = 16.0 + rgb.x*0.257 + rgb.y*0.504 + rgb.z*0.098;
    ycbcr.y = 128.0 - rgb.x*0.148 - rgb.y*0.291 + rgb.z*0.439;
    ycbcr.z = 128.0 + rgb.x*0.439 - rgb.y*0.368 - rgb.z*0.071;
    if (ycbcr.y > 100.0 && ycbcr.y < 118.0 &&
        ycbcr.z > 121.0 && ycbcr.z < 161.0) {
     	isSkin = true; 
    }
#endif

    if (iMouse.z > 0.0 || !isSkin) {
        fragColor = vec4(texture(iChannel0, fragCoord.xy / iResolution.xy).xyz, 1.0); 
    }
    return fragColor;
}


