const vec3 mistColor   = vec3(0.81, 0.847, 0.253);

// Base controls
const float scale = 4.0;
const float intensityMultiplier = 2.0;
const float speed = -0.25;
const int octaves = 23;

// Density controls (global)
const float density   = 0.45; // lower = more coverage
const float thickness = 1.2; // higher = denser cores
const float softness  = 0.25; // edge softness

// Wind controls
const vec2  windDir   = normalize(vec2(1.0, 0.25));
const float windSpeed = 0.18;

// Layer weights
const float L0 = 0.55; // big
const float L1 = 0.35; // mid
const float L2 = 0.2; // fine

// NEW: optical thickness for realistic obscuring
// Higher = tokens disappear more in dense areas
const float sigma = 2.2;  // try 1.2 (light haze) .. 4.0 (heavy fog)

vec2 random2(vec2 st){
  st = vec2(dot(st,vec2(127.1,311.7)), dot(st,vec2(269.5,183.3)));
  return -1.0 + 2.0 * fract(sin(st) * 7.0);
}

float noise(vec2 st) {
  vec2 i = floor(st);
  vec2 f = fract(st);
  vec2 u = f*f*(3.0-2.0*f);

  return mix(
    mix(dot(random2(i + vec2(0.0,0.0)), f - vec2(0.0,0.0)),
        dot(random2(i + vec2(1.0,0.0)), f - vec2(1.0,0.0)), u.x),
    mix(dot(random2(i + vec2(0.0,1.0)), f - vec2(0.0,1.0)),
        dot(random2(i + vec2(1.0,1.0)), f - vec2(1.0,1.0)), u.x), u.y);
}

float fractal_brownian_motion(vec2 coord) {
  float value = 0.0;
  float sc = 0.2;
  for (int i = 0; i < octaves; i++) {
    value += noise(coord) * sc;
    coord *= 2.0;
    sc *= 0.4;
  }
  return value + 0.2;
}

// One mist layer: returns [0..1] density-like contribution
float mistLayer(vec2 pos, float t, float layerScale, float layerWindMul, float warpMul, float localIntensity)
{
  vec2 wind = windDir * (t * windSpeed * layerWindMul);

  vec2 warp1 = vec2(
    fractal_brownian_motion(pos * (0.55*layerScale) + wind * 0.35 + vec2(0.0,  t * 0.05)),
    fractal_brownian_motion(pos * (0.55*layerScale) + wind * 0.35 + vec2(7.3, -t * 0.05))
  ) - 0.5;

  vec2 warp2 = vec2(
    fractal_brownian_motion(pos * (1.10*layerScale) + wind * 0.80 + vec2(2.1,  t * 0.11)),
    fractal_brownian_motion(pos * (1.10*layerScale) + wind * 0.80 + vec2(9.2, -t * 0.10))
  ) - 0.5;

  vec2 warpedPos = pos * layerScale + (warp1 * 1.25 + warp2 * 0.75) * warpMul + wind * 1.2;

  vec2 motion = vec2(fractal_brownian_motion(warpedPos + vec2(t * speed * 1.2, t * speed)));

  float raw = fractal_brownian_motion(warpedPos + motion) * (intensityMultiplier * localIntensity);

  float fog = smoothstep(density - softness, density + softness, raw);
  fog = pow(clamp(fog, 0.0, 1.0), thickness);

  return fog;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 st = fragCoord.xy / iResolution.xy;
  st *= iResolution.xy / iResolution.y;

  float t = iTime;
  vec2 basePos = st * scale;

  // 3 layers
  float fog0 = mistLayer(basePos, t, 0.55, 0.65, 0.90, 0.85);
  float fog1 = mistLayer(basePos, t, 1.00, 1.00, 1.00, 1.00);
  float fog2 = mistLayer(basePos, t, 1.85, 1.45, 1.15, 0.90);

  // Combined density field (0..1-ish)
  float d = clamp(fog0*L0 + fog1*L1 + fog2*L2, 0.0, 1.0);

  // Convert density -> optical depth -> transmittance
  float opticalDepth = d * sigma;
  float T = exp(-opticalDepth);    // how much of underlying tokens show through
  float A = 1.0 - T;               // overlay alpha = obscuration amount

  // Premultiplied output: fog light (in-scattering) approx
  vec3 rgb = mistColor * A;

  fragColor = vec4(rgb, A);
}

