// Bard Spell / Musical Notation Effect (Shadertoy)
// - core glow + pulsing rings
// - staff lines + multiple notes + accidentals along the staff
// - flying mini glyphs from center
// - CENTER TREBLE CLEF COMES FROM IMAGE in iChannel1 (PNG with alpha)
// - transparent background (alpha from brightness)
//
// Setup:
//   - Put a treble clef PNG (transparent background) into iChannel1
//   - iChannel1: Clamp, Linear
//
// Paste into Shadertoy as an Image shader.

#ifdef GL_ES

#endif

// ---------- TWEAKS ----------
const vec3 coreColor   = vec3(0.92, 0.86, 1.00);
const vec3 ringColor   = vec3(0.45, 0.85, 1.00);
const vec3 staffColor  = vec3(0.90, 0.95, 1.00);
const vec3 glyphColor  = vec3(1.00, 0.78, 0.25);   // warm gold
const vec3 accentColor = vec3(0.95, 0.40, 0.90);   // magenta accent

const float coreAlpha = 0.22;
const float coreFade  = 11.0;

const float miniBlur  = 0.007;
const float staffBlur = 0.006;

// staff geometry
const float STAFF_LINE_SPACING = 0.08;
const float STAFF_STEP         = 0.04;   // line/space half-step
const float STAFF_HALF_HEIGHT  = 0.16;   // 5 lines cover [-0.16..0.16]

// ---------- helpers ----------
float hash11(float p){ return fract(sin(p)*43758.5453123); }
vec2  hash22(vec2 p){
    float n = sin(dot(p, vec2(41.0, 289.0)));
    return fract(vec2(262144.0, 32768.0) * n);
}

float glowFalloff(float r, float k){ return exp(-r*k); }

mat2 rot2(float a){
    float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}

float ring(vec2 p, float R, float w){
    float r = length(p);
    return smoothstep(w, 0.0, abs(r - R));
}

// ---------- SDF primitives ----------
float sdCircle(vec2 p, float r){ return length(p) - r; }

float sdRoundBox(vec2 p, vec2 b, float r){
    vec2 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, q.y), 0.0) - r;
}

float smin(float a, float b, float k){
    float h = clamp(0.5 + 0.5*(b-a)/k, 0.0, 1.0);
    return mix(b, a, h) - k*h*(1.0-h);
}

float drawFill(float d, float blur){
    blur = max(blur, 1e-5);
    return smoothstep(blur, -blur, d);
}

// ---------- music glyphs (procedural) ----------
float sdNote(vec2 p, float size, float tilt, float type01, float chord01)
{
    p *= rot2(tilt);
    p /= max(size, 1e-5);

    // head (slanted ellipse)
    vec2 ph = p * rot2(-0.35);
    ph.x *= 1.18; ph.y *= 0.86;
    float head = sdCircle(ph - vec2(-0.18, -0.15), 0.18);

    // optional second head (chord)
    vec2 ph2 = p * rot2(-0.35);
    ph2.x *= 1.18; ph2.y *= 0.86;
    float head2 = sdCircle(ph2 - vec2(-0.06, 0.02), 0.16);
    head = smin(head, mix(1e5, head2, smoothstep(0.35, 0.65, chord01)), 0.08);

    // stem
    vec2 ps = p - vec2(0.02, 0.18);
    float stem = sdRoundBox(ps, vec2(0.045, 0.38), 0.02);

    // flag (for eighth)
    float flag = 1e5;
    {
        vec2 pf = p - vec2(0.13, 0.55);
        pf *= rot2(0.55);
        float f1 = sdRoundBox(pf, vec2(0.18, 0.055), 0.03);

        vec2 pf2 = p - vec2(0.18, 0.43);
        pf2 *= rot2(0.35);
        float f2 = sdRoundBox(pf2, vec2(0.15, 0.045), 0.03);

        flag = smin(f1, f2, 0.08);
    }

    float note = smin(head, stem, 0.10);
    note = smin(note, mix(1e5, flag, smoothstep(0.35, 0.65, type01)), 0.10);

    return note * size;
}

float sdSharp(vec2 p, float size, float tilt)
{
    p *= rot2(tilt);
    p /= max(size, 1e-5);

    float d = 1e5;

    d = smin(d, sdRoundBox(p - vec2(-0.10, 0.00), vec2(0.04, 0.42), 0.02), 0.10);
    d = smin(d, sdRoundBox(p - vec2( 0.12, 0.00), vec2(0.04, 0.42), 0.02), 0.10);

    vec2 q1 = (p - vec2(0.00,  0.12)) * rot2(0.35);
    vec2 q2 = (p - vec2(0.00, -0.12)) * rot2(0.35);

    d = smin(d, sdRoundBox(q1, vec2(0.28, 0.04), 0.02), 0.12);
    d = smin(d, sdRoundBox(q2, vec2(0.28, 0.04), 0.02), 0.12);

    return d * size;
}

float sdFlat(vec2 p, float size, float tilt)
{
    p *= rot2(tilt);
    p /= max(size, 1e-5);

    float d = 1e5;

    // stem
    d = smin(d, sdRoundBox(p - vec2(-0.05, 0.05), vec2(0.045, 0.50), 0.02), 0.10);

    // bulb
    vec2 pb = p - vec2(0.08, -0.05);
    pb *= rot2(-0.25);
    pb.x *= 1.05; pb.y *= 0.85;
    float bulb = sdCircle(pb, 0.16);

    // inner carve
    vec2 pi = p - vec2(0.10, -0.05);
    pi *= rot2(-0.25);
    pi.x *= 1.05; pi.y *= 0.85;
    float inner = sdCircle(pi, 0.10);

    d = smin(d, max(bulb, -inner), 0.10);
    return d * size;
}

// ---------- staff ----------
float staffLines(vec2 suv)
{
    float s = 0.0;
    for(int i=0;i<5;i++){
        float yi = (float(i) - 2.0) * STAFF_LINE_SPACING;
        float dd = abs(suv.y - yi);
        s += smoothstep(0.010, 0.0, dd);
    }
    return s;
}

// notes constrained to staff; NO per-column fade (prevents clipping artifacts)
vec2 staffNotesMask(vec2 suv, float t)
{
    float fill = 0.0;
    float glow = 0.0;

    float spacing = 0.24;
    float scroll  = t * 0.18;

    for(int j=-12; j<=12; j++)
    {
        float wx = suv.x + scroll;
        float cell = floor(wx / spacing) + float(j);
        float xCenter = (cell + 0.5) * spacing - scroll;

        float id = cell;
        vec2 rnd = hash22(vec2(id, 7.0));

        float has = step(0.45, rnd.x);
        if(has > 0.5)
        {
            // pitch strictly within staff: -4..+4 => y in [-0.16..0.16]
            float pitch = floor(rnd.y * 9.0) - 4.0;
            float y = pitch * STAFF_STEP;

            vec2 p = suv - vec2(xCenter, y);

            float type01  = step(0.55, hash11(id*3.1 + 2.0));
            float chord01 = step(0.84, hash11(id*2.7 + 9.0));
            float tilt    = (hash11(id*1.9 + 5.0) - 0.5) * 0.18;

            float dN = sdNote(p, 0.15, tilt, type01, chord01);
            float mN = drawFill(dN, staffBlur);
            float gN = drawFill(dN - 0.03, staffBlur*2.5) * 0.45;

            fill = max(fill, mN);
            glow = max(glow, gN);

            // accidentals occasionally
            float accRoll = hash11(id*5.3 + 1.0);
            if(accRoll > 0.78)
            {
                float which = step(0.5, hash11(id*6.1 + 3.0)); // 0 flat, 1 sharp
                vec2 ap = p + vec2(-0.16, 0.02);

                float dA = (which > 0.5) ? sdSharp(ap, 0.12, tilt*0.6)
                                         : sdFlat(ap,  0.12, tilt*0.6);

                float mA = drawFill(dA, staffBlur);
                float gA = drawFill(dA - 0.025, staffBlur*2.4) * 0.40;

                fill = max(fill, mA);
                glow = max(glow, gA);
            }
        }
    }

    return vec2(fill, glow);
}

// ---------- flying mini glyphs ----------
float flyingGlyphs(vec2 uv, float t)
{
    float acc = 0.0;

    for(int i = 0; i < 22; i++)
    {
        float seed = float(i) * 19.73 + 3.1;

        float h0 = hash11(seed);
        float h1 = hash11(seed + 1.0);
        float h2 = hash11(seed + 2.0);
        float h3 = hash11(seed + 3.0);

        float life = fract(t * (0.55 + 0.25*h0) + h1);

        float ang = h2 * 6.2831853;
        vec2 dir = vec2(cos(ang), sin(ang));

        float spiral = (h3 - 0.5) * 1.6;
        mat2 rr = rot2(spiral * life * 2.3);

        float speed = 0.22 + 1.05*h0;
        vec2 pos = rr * (dir * life * speed);

        float size = mix(0.18, 0.04, life);
        float tilt = (hash11(seed + 10.0) - 0.5) * 0.9;

        float pick = hash11(seed + 11.0);
        float gid = (pick < 0.80) ? 0.0 : ((pick < 0.90) ? 1.0 : 2.0);

        float type01  = step(0.55, hash11(seed + 20.0));
        float chord01 = step(0.82, hash11(seed + 21.0));

        float fadeIn  = smoothstep(0.0, 0.10, life);
        float fadeOut = smoothstep(1.0, 0.60, life);
        float fade = fadeIn * fadeOut;

        float d = 1e5;
        vec2 p = uv - pos;

        if(gid < 0.5) d = sdNote(p, size, tilt, type01, chord01);
        else if(gid < 1.5) d = sdSharp(p, size, tilt);
        else d = sdFlat(p, size, tilt);

        float m = drawFill(d, miniBlur);
        float halo = drawFill(d - size*0.06, miniBlur*2.6) * 0.35;

        acc += (m + halo) * fade;
    }

    return acc;
}

// ---------- center treble clef image (iChannel1) ----------
vec4 sampleClef(vec2 uv, vec2 center, float scale, float rot)
{
    vec2 p = (uv - center) / scale;

    // rotate first (add 180)
    float c = cos(rot + 3.14159265);
    float s = sin(rot + 3.14159265);
    p = mat2(c,-s,s,c) * p;

    // horizontal flip
    p.x = -p.x;

    vec2 tuv = p + 0.5;

    vec4 tex = texture(iChannel1, tuv);

    float inside = step(0.0, tuv.x)*step(0.0, tuv.y)*
                   step(tuv.x, 1.0)*step(tuv.y, 1.0);

    return tex * inside;
}

float clefGlow(vec2 uv, vec2 center, float scale, float rot)
{
    float px = 1.0 / min(iResolution.x, iResolution.y);
    float r = px * 11.0; // glow radius (tune)

    float g = 0.0;
    g += sampleClef(uv + vec2( r, 0.0), center, scale, rot).a;
    g += sampleClef(uv + vec2(-r, 0.0), center, scale, rot).a;
    g += sampleClef(uv + vec2(0.0,  r), center, scale, rot).a;
    g += sampleClef(uv + vec2(0.0, -r), center, scale, rot).a;
    g += sampleClef(uv,               center, scale, rot).a * 2.0;
    return g / 6.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 res = iResolution.xy;
    vec2 uv  = (fragCoord - 0.5*res) / res.y; // centered, aspect-correct
    float t  = iTime;

    float r = length(uv);

    // core glow
    float core = glowFalloff(r, coreFade) * (0.8 + 0.2*sin(t*3.0));
    core *= coreAlpha;

    // rings
    float pulse = 0.35 + 0.30*sin(t*2.1);
    float R1 = 0.11 + 0.03*sin(t*1.6);
    float R2 = 0.18 + 0.05*sin(t*1.0 + 1.2);

    float rings = 0.0;
    rings += ring(uv, R1 + 0.05*pulse, 0.015);
    rings += 0.35*ring(uv, R2 + 0.08*pulse, 0.020);
    rings *= glowFalloff(r, 1.35);

    // staff
    vec2 suv = uv;
    float staffL = staffLines(suv) * 0.22;
    vec2 staffNG = staffNotesMask(suv, t);
    float staffNote = staffNG.x;
    float staffGlowM = staffNG.y;

    // flying glyphs
    float minis = flyingGlyphs(uv, t) * glowFalloff(r, 1.0);

    // center treble clef sprite (iChannel1)
    vec2 clefCenter = vec2(0.0, 0.0);
    float clefScale = 0.55;              // bigger clef -> smaller value (try 0.45..0.70)
    float clefRot   = 0.02*sin(t*0.7);   // subtle sway (or 0.0)
    clefCenter.y += 0.01*sin(t*1.3);     // subtle bob

    vec4 clefTex = sampleClef(uv, clefCenter, clefScale, clefRot);
    float clefA  = clefTex.a;
    float clefG  = clefGlow(uv, clefCenter, clefScale, clefRot);

    // vignette (won't clip note shapes; just overall fade)
    float vign = smoothstep(1.20, 0.25, r);

    // compose color
    vec3 col = vec3(0.0);
    col += core  * coreColor * 1.25;
    col += rings * ringColor * 1.25;

    col += staffL      * staffColor * vign;
    col += staffGlowM  * mix(staffColor, ringColor, 0.50) * 0.95 * vign;
    col += staffNote   * mix(staffColor, glyphColor, 0.35) * 1.05 * vign;

    // clef (use alpha as mask; ignore RGB)
    col += clefG * mix(glyphColor, vec3(1.0), 0.35) * 0.90;
    col += clefA * mix(glyphColor, accentColor, 0.12) * 1.90;

    col += minis * mix(glyphColor, vec3(1.0), 0.25) * 1.20;

    // tone shaping
    col = 1.0 - exp(-col);
    col = pow(col, vec3(0.9));

    // alpha (transparent background)
    float brightness = max(max(col.r, col.g), col.b);
    float alpha = smoothstep(0.03, 0.20, brightness);

    fragColor = vec4(col, alpha);
}

// Generic Sprite Spell Effect (Shadertoy)
// ----------------------------------------
// iChannel0: RGB noise (tiling)  <-- REQUIRED for de-looping (Wrap Repeat, Filter Linear)
// iChannel1: Center sprite (PNG w/ alpha)
// iChannel2: Mini sprite A
// iChannel3: Mini sprite B
//
// Features:
// - Core glow + pulsing rings
// - Center sprite with configurable rotation + flips
// - Flying mini sprites using iChannel2 / iChannel3
// - Optional constant mini rotation
// - Forced mini orientation: 180 rotate + horizontal flip
// - EFFECT_SCALE scales center + rings + core together
// - MINI_COLOR controls mini particle tint
// - MINI_EDGE_FADE softly fades minis BEFORE sprite quad edge (prevents hard cut)
// - Transparent background (alpha from brightness)
//
// Channel settings:
// - iChannel0 (noise): Wrap Repeat, Filter Linear
// - iChannel1/2/3: Wrap Clamp, Filter Linear

#ifdef GL_ES

#endif

// ---------- CONFIG ----------

const float EFFECT_SCALE = 0.4;

// Center sprite
const float CENTER_SCALE = 0.25;
const float CENTER_ROT   = 3.141593;   // base rotation (PI = 180)
const bool  CENTER_FLIPX = true;       // horizontal flip
const bool  CENTER_FLIPY = false;      // vertical flip

const float CENTER_GLOW_RADIUS_PX = 14.0;
const float CENTER_GLOW_STRENGTH  = 1.0;
const float CENTER_FILL_STRENGTH  = 2.0;

// Mini sprites
const int MINI_COUNT = 15;
const float MINI_BASE_SCALE       = 0.18;
const float MINI_END_SCALE        = 0.05;

const bool  MINI_ROTATE_RANDOMLY  = false; // false = constant rotation for all minis
const float MINI_ROT_CONSTANT     = 0.0;   // used if MINI_ROTATE_RANDOMLY==false
const float MINI_ROT_RANDOM       = 1.6;   // used if MINI_ROTATE_RANDOMLY==true

// Forced mini orientation
const float MINI_BASE_ROT         = 3.141593; // 180 rotate
const bool  MINI_FLIPX            = true;     // horizontal flip
const bool  MINI_FLIPY            = false;    // vertical flip

const float MINI_GLOW_RADIUS_PX   = 8.0;
const float MINI_GLOW_STRENGTH    = 0.7;
const float MINI_FILL_STRENGTH    = 1.2;
const float MINI_PICK_BIAS        = 0.65;     // chance to use iChannel2

const vec3  MINI_COLOR_A            = vec3(0.952941, 0.917647, 0.74902); // for iChannel2
const vec3  MINI_COLOR_B          = vec3(0.909804, 0.090196, 0.690196); // for iChannel3

// Fade minis before sprite quad edge (0..0.5). 0.10 = fades over last 20% to edge.
const float MINI_EDGE_FADE        = 0.1;

// Core / ring energy
const vec3  CORE_COLOR            = vec3(0.078431, 0.921569, 0.019608);
const vec3  ACCENT_COLOR          = vec3(0.662745, 0.172549, 0.117647);
const vec3  RING_COLOR            = vec3(0.4, 0.8, 1.0);

const float CORE_ALPHA            = 0.22;
const float CORE_FADE             = 10.0;

// ---------- helpers ----------
float hash11(float p){ return fract(sin(p)*43758.5453123); }

float hash21(vec2 p){
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453123);
}
vec2 hash22(vec2 p){
    float n = hash21(p);
    return vec2(n, hash21(p + 19.19));
}

mat2 rot2(float a){
    float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}

float glowFalloff(float r, float k){ return exp(-r*k); }

float ring(vec2 p, float R, float w){
    float rr = length(p);
    return smoothstep(w, 0.0, abs(rr - R));
}

// ---------- noise helpers (iChannel0 = RGB noise) ----------
float noise1(vec2 p){ return texture(iChannel0, p).r; }
vec3  noise3(vec2 p){ return texture(iChannel0, p).rgb; }

// ---------- sprite sampling ----------
vec4 sampleSprite(
    sampler2D tex,
    vec2 uv,
    vec2 center,
    float scale,
    float rot,
    bool flipX,
    bool flipY
){
    vec2 p = (uv - center) / scale;
    p = rot2(rot) * p;

    if(flipX) p.x = -p.x;
    if(flipY) p.y = -p.y;

    vec2 tuv = p + 0.5;
    vec4 col = texture(tex, tuv);

    float inside = step(0.0, tuv.x) *
                   step(0.0, tuv.y) *
                   step(tuv.x, 1.0) *
                   step(tuv.y, 1.0);

    return col * inside;
}

// Soft-edge sampler: fades alpha as it approaches sprite quad edge
vec4 sampleSpriteSoftEdge(
    sampler2D tex,
    vec2 uv,
    vec2 center,
    float scale,
    float rot,
    bool flipX,
    bool flipY,
    float edgeFade
){
    vec2 p = (uv - center) / scale;
    p = rot2(rot) * p;

    if(flipX) p.x = -p.x;
    if(flipY) p.y = -p.y;

    // p in sprite-local coords, quad is [-0.5..0.5]
    float m = max(abs(p.x), abs(p.y));
    float soft = smoothstep(0.5, 0.5 - edgeFade, m); // 1 center -> 0 near edges

    vec2 tuv = p + 0.5;
    vec4 col = texture(tex, tuv);

    float inside = step(0.0, tuv.x) *
                   step(0.0, tuv.y) *
                   step(tuv.x, 1.0) *
                   step(tuv.y, 1.0);

    return col * inside * soft;
}

float spriteGlow(
    sampler2D tex,
    vec2 uv,
    vec2 center,
    float scale,
    float rot,
    bool flipX,
    bool flipY,
    float radiusPx
){
    float px = 1.0 / min(iResolution.x, iResolution.y);
    float rr = px * radiusPx;

    float g = 0.0;
    g += sampleSprite(tex, uv + vec2( rr, 0.0), center, scale, rot, flipX, flipY).a;
    g += sampleSprite(tex, uv + vec2(-rr, 0.0), center, scale, rot, flipX, flipY).a;
    g += sampleSprite(tex, uv + vec2(0.0,  rr), center, scale, rot, flipX, flipY).a;
    g += sampleSprite(tex, uv + vec2(0.0, -rr), center, scale, rot, flipX, flipY).a;
    g += sampleSprite(tex, uv,                center, scale, rot, flipX, flipY).a * 2.0;

    return g / 6.0;
}

// Mini glow that also respects soft-edge fade (prevents glow clipping too)
float spriteGlowSoftEdge(
    sampler2D tex,
    vec2 uv,
    vec2 center,
    float scale,
    float rot,
    bool flipX,
    bool flipY,
    float radiusPx,
    float edgeFade
){
    float px = 1.0 / min(iResolution.x, iResolution.y);
    float rr = px * radiusPx;

    float g = 0.0;
    g += sampleSpriteSoftEdge(tex, uv + vec2( rr, 0.0), center, scale, rot, flipX, flipY, edgeFade).a;
    g += sampleSpriteSoftEdge(tex, uv + vec2(-rr, 0.0), center, scale, rot, flipX, flipY, edgeFade).a;
    g += sampleSpriteSoftEdge(tex, uv + vec2(0.0,  rr), center, scale, rot, flipX, flipY, edgeFade).a;
    g += sampleSpriteSoftEdge(tex, uv + vec2(0.0, -rr), center, scale, rot, flipX, flipY, edgeFade).a;
    g += sampleSpriteSoftEdge(tex, uv,                center, scale, rot, flipX, flipY, edgeFade).a * 2.0;

    return g / 6.0;
}

// ---------- mini sprite particles (RGB-noise de-looping, NO jitter/vibration) ----------
// ---------- mini sprite particles (RGB-noise de-looping, NO jitter/vibration) ----------
// returns: (fillA, glowA, fillB, glowB)
vec4 flyingMiniSprites(vec2 uv, float t)
{
    float fillA = 0.0, glowA = 0.0;
    float fillB = 0.0, glowB = 0.0;

    for(int i=0;i<MINI_COUNT;i++)
    {
        float seed = float(i) * 19.73 + 3.1;

        float h0 = hash11(seed);
        float h1 = hash11(seed + 1.0);
        float h2 = hash11(seed + 2.0);
        float h3 = hash11(seed + 3.0);

        float spd   = (0.55 + 0.25*h0);
        float phase = t * spd + h1;

        float life  = fract(phase);
        float cycle = floor(phase);

        vec2 jump = hash22(vec2(seed, cycle));
        vec2 nUV  = fract(jump + vec2(seed*0.013, seed*0.071));
        vec3 rnd  = noise3(nUV);

        float ang = fract(h2 + (rnd.x*2.0 - 1.0) * 0.45) * 6.2831853;
        vec2 dir  = vec2(cos(ang), sin(ang));

        float spiral = ((h3 + (rnd.y - 0.5) * 0.9) - 0.5) * 1.6;

        float maxRadius = (0.22 + 1.05*h0) * EFFECT_SCALE;

        float spawnRadius = (0.03 + 0.06*rnd.z) * EFFECT_SCALE;
        spawnRadius = min(spawnRadius, maxRadius * 0.95);

        float radius = spawnRadius + life * (maxRadius - spawnRadius);
        vec2 pos = rot2(spiral * life * 2.3) * (dir * radius);

        float sizeJ = 0.85 + 0.45*noise1(nUV + 1.37);
        float size  = mix(MINI_BASE_SCALE, MINI_END_SCALE, life) * sizeJ / EFFECT_SCALE;

        float rotRnd = (hash11(seed + 10.0 + cycle*0.7) - 0.5) * 2.0 * MINI_ROT_RANDOM;
        float rot = MINI_BASE_ROT + (MINI_ROTATE_RANDOMLY ? rotRnd : MINI_ROT_CONSTANT);

        float fadeIn  = smoothstep(0.0, 0.10, life);
        float fadeOut = smoothstep(1.0, 0.85, life);
        float fade = fadeIn * fadeOut;

        bool useA = hash11(seed + 11.0 + cycle*0.3) < MINI_PICK_BIAS;

        vec3 tint = useA ? MINI_COLOR_A : MINI_COLOR_B;

        vec4 sCol = useA
            ? sampleSpriteSoftEdge(iChannel2, uv, pos, size, rot, MINI_FLIPX, MINI_FLIPY, MINI_EDGE_FADE)
            : sampleSpriteSoftEdge(iChannel3, uv, pos, size, rot, MINI_FLIPX, MINI_FLIPY, MINI_EDGE_FADE);

        // tint the sprite RGB, keep alpha
        sCol.rgb *= tint;

        float a = sCol.a;

        float g = useA
            ? spriteGlowSoftEdge(iChannel2, uv, pos, size, rot, MINI_FLIPX, MINI_FLIPY, MINI_GLOW_RADIUS_PX, MINI_EDGE_FADE)
            : spriteGlowSoftEdge(iChannel3, uv, pos, size, rot, MINI_FLIPX, MINI_FLIPY, MINI_GLOW_RADIUS_PX, MINI_EDGE_FADE);

        if(useA){
            fillA += a * fade;
            glowA += g * fade;
        }else{
            fillB += a * fade;
            glowB += g * fade;
        }
    }

    return vec4(fillA, glowA, fillB, glowB);
}

// ---------- main ----------
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 res = iResolution.xy;
    vec2 uv  = (fragCoord - 0.5*res) / res.y;
    float t  = iTime;

    float r = length(uv);

    // Core energy glow (scaled)
    float core = glowFalloff(r / EFFECT_SCALE, CORE_FADE) * (0.8 + 0.2*sin(t*3.0));
    core *= CORE_ALPHA;

    // Pulsing rings (scaled)
    float pulse = 0.35 + 0.30*sin(t*2.1);
    float R1 = (0.11 + 0.03*sin(t*1.6)) * EFFECT_SCALE;
    float R2 = (0.18 + 0.05*sin(t*1.0 + 1.2)) * EFFECT_SCALE;

    float rings = 0.0;
    rings += ring(uv, R1 + 0.05*pulse * EFFECT_SCALE, 0.015);
    rings += 0.35*ring(uv, R2 + 0.08*pulse * EFFECT_SCALE, 0.020);
    rings *= glowFalloff(r, 1.35);

    // Center sprite
    vec2 center = vec2(0.0, 0.0);
    center.y += 0.01*sin(t*1.3);

    float centerScale = CENTER_SCALE / EFFECT_SCALE;
    float centerRot = CENTER_ROT + 0.02*sin(t*0.7);

    vec4 cTex = sampleSprite(iChannel1, uv, center, centerScale, centerRot, CENTER_FLIPX, CENTER_FLIPY);
    float cA  = cTex.a;

    float cG  = spriteGlow(iChannel1, uv, center, centerScale, centerRot,
                           CENTER_FLIPX, CENTER_FLIPY, CENTER_GLOW_RADIUS_PX);

// Mini sprites
vec4 mini = flyingMiniSprites(uv, t);

float miniFillA = mini.x * glowFalloff(r, 1.0);
float miniGlowA = mini.y * glowFalloff(r, 1.0);
float miniFillB = mini.z * glowFalloff(r, 1.0);
float miniGlowB = mini.w * glowFalloff(r, 1.0);


    // Compose
    vec3 col = vec3(0.0);

// Compose minis with separate colors
col += miniGlowA * MINI_COLOR_A * MINI_GLOW_STRENGTH;
col += miniFillA * MINI_COLOR_A * MINI_FILL_STRENGTH;

col += miniGlowB * MINI_COLOR_B * MINI_GLOW_STRENGTH;
col += miniFillB * MINI_COLOR_B * MINI_FILL_STRENGTH;


    col += core  * CORE_COLOR * 1.4;
    col += rings * RING_COLOR * 1.4;

    col += cG * mix(ACCENT_COLOR, vec3(1.0), 0.3) * CENTER_GLOW_STRENGTH;
    col += cA * ACCENT_COLOR * CENTER_FILL_STRENGTH;


    // Tone mapping
    col = 1.0 - exp(-col);
    col = pow(col, vec3(0.9));

    // Transparent background
    float brightness = max(max(col.r, col.g), col.b);
    float alpha = smoothstep(0.03, 0.20, brightness);

    fragColor = vec4(col, alpha);
}

// Source:
// http://coding-experiments.blogspot.com/2010/07/convolution.html

#define EMBOSS_WIDTH	0.0015
#define EMBOSS_HEIGHT	0.0015

// samples a pixel centerd at "uv" and with offset of dx|dy
vec4 sample_pixel(in vec2 uv, in float dx, in float dy)
{
    return texture(iChannel0, uv + vec2(dx, dy));
}

// convolves a SINGLE channel of input color_matrix
float convolve(in float[9] kernel, in vec4[9] color_matrix)
{
   float res = 0.0;
   for (int i=0; i<9; i++)
   {
      res += kernel[i] * color_matrix[i].a;
   }
   return clamp(res + 0.5, 0.0 ,1.0);
}

// builds a 3x3 color matrix centerd at "uv"
void build_color_matrix(in vec2 uv, out vec4[9] color_matrix)
{
    float dxtex = EMBOSS_WIDTH;
    float dytex = EMBOSS_HEIGHT;

	color_matrix[0].rgb = sample_pixel(uv, -dxtex, -dytex)	.rgb;
	color_matrix[1].rgb = sample_pixel(uv, -dxtex, 	0.0)	.rgb;
    color_matrix[2].rgb = sample_pixel(uv, -dxtex, 	dytex)	.rgb;
	color_matrix[3].rgb = sample_pixel(uv, 0.0, 	-dytex)	.rgb;
	color_matrix[4].rgb = sample_pixel(uv, 0.0, 	0.0)	.rgb;
    color_matrix[5].rgb = sample_pixel(uv, 0.0, 	dytex)	.rgb;
	color_matrix[6].rgb = sample_pixel(uv, dxtex, 	-dytex)	.rgb;
	color_matrix[7].rgb = sample_pixel(uv, dxtex, 	0.0)	.rgb;
    color_matrix[8].rgb = sample_pixel(uv, dxtex, 	dytex)	.rgb;
}

// builds a mean color matrix (off of .rgb of input).
// NOTE: stores the output in alpha channel
void build_mean_matrix(inout vec4[9] color_matrix)
{
   for (int i=0; i<9; i++)
   {
      color_matrix[i].a = (color_matrix[i].r + color_matrix[i].g + color_matrix[i].b) / 3.;
   }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /*	2.	0.	0.
		0.	-1	0.
		0.	0.	-1	*/

    float kerEmboss[9];
    kerEmboss[0] = 2.0;
    kerEmboss[1] = 0.0;
    kerEmboss[2] = 0.0;
    kerEmboss[3] = 0.0;
    kerEmboss[4] = -1.;
    kerEmboss[5] = 0.0;
    kerEmboss[6] = 0.0;
    kerEmboss[7] = 0.0;
    kerEmboss[8] = -1.;

    vec4 pixel_matrix[9];
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 uv4 = texture(iChannel0, uv);
    build_color_matrix(uv, pixel_matrix);
    build_mean_matrix(pixel_matrix);

    float convolved = convolve(kerEmboss, pixel_matrix);
	fragColor = vec4(vec3(convolved) ,uv4.a);
}

// Image: Top-Down Snow (meandering turbulence) + Ground Accumulation
// iChannel0 MUST be Buffer A

#ifdef GL_ES

#endif

#define LAYERS 3
#define SPAWNS_PER_CELL 2
#define FLOW_STEPS 2

const float WORLD_SCALE = 9.0;
const float DENSITY = 0.60;
const float BASE_RATE = 0.55;

// Flow-field (must match Buffer A!)
const float FLOW_SCALE   = 2.2;
const float FLOW_SPEED   = 0.35;
const float FLOW_STRENGTH= 0.18;
const float FLOW_STEP    = 0.22;

// Visual
const float BRIGHTNESS = 1.10;
const float SOFT_BG    = 0.10;

// Shrink with life
const float START_SIZE = 4.0;
const float END_SIZE   = 0.18;

// Fade over life
const float FADEIN_A  = 0.03;
const float FADEIN_B  = 0.15;
const float FADEOUT_A = 0.88;
const float FADEOUT_B = 1.00;

// Ground
const vec3 GROUND_BASE = vec3(0.03, 0.04, 0.06);
const vec3 SNOW_TINT   = vec3(0.90, 0.95, 1.00);
// Accumulated ground snow alpha control
const float GROUND_ALPHA_SCALE = 1.6;   // overall opacity multiplier
const float GROUND_ALPHA_POWER = 1.2;   // contrast of alpha buildup


mat2 rot2(float a){ float c=cos(a), s=sin(a); return mat2(c,-s,s,c); }
float sat(float x){ return clamp(x, 0.0, 1.0); }

float hash21(vec2 p){
    return fract(sin(dot(p, vec2(127.1,311.7))) * 43758.5453123);
}

float noise(vec2 p){
    vec2 i = floor(p);
    vec2 f = fract(p);

    vec2 u = f*f*(3.0 - 2.0*f);

    float a = hash21(i);
    float b = hash21(i + vec2(1.0,0.0));
    float c = hash21(i + vec2(0.0,1.0));
    float d = hash21(i + vec2(1.0,1.0));

    return mix(mix(a,b,u.x), mix(c,d,u.x), u.y);
}

float nrand(float x){ return fract(sin(x)*43758.5453123); }

// a cheap chip noise for edge breakup (deterministic)
float chipNoise(vec2 p, float seed){
    return noise(p*3.0 + vec2(seed*13.1, seed*7.7));
}



// fold angle into 6-fold symmetry sector [-pi/6, pi/6]
float fold6(float ang){
    float sector = 3.14159265 / 3.0;
    return mod(ang + sector*0.5, sector) - sector*0.5;
}

// One snowflake, max variety (still mainly 6-fold)
float snowflakeMaxVar(vec2 p, float size, float blur, float seed, float rot)
{
    p = rot2(rot) * p;

    float r   = length(p);
    float ang = atan(p.y, p.x);
    float a   = fold6(ang);                 // folded angle
    float rn  = r / max(size, 1e-4);        // normalized radius

    // Pick a type 0..3
    float tsel = nrand(seed*19.17 + 0.11);
    int type = int(floor(tsel * 6.0 + 0.9)); // 0,1,2,3,4,5

    // Shared random params
    float s1 = nrand(seed*3.31 + 1.7);
    float s2 = nrand(seed*5.73 + 2.9);
    float s3 = nrand(seed*9.11 + 4.2);
    float s4 = nrand(seed*13.7 + 6.6);

    // Serration (teeth)
    float teethFreq = mix(6.0, 26.0, s1);
    float teethAmp  = mix(0.00, 0.08, s2) * smoothstep(0.15, 0.95, rn);
    float teeth = sin(rn * teethFreq * 6.2831853 + seed*40.0) * teethAmp;

    // Edge chipping / breakup
    float chip = chipNoise(p/ max(size,1e-4), seed);
    float chipAmt = mix(0.0, 0.10, s3) * smoothstep(0.35, 0.95, rn);
    teeth += (chip - 0.5) * chipAmt;

    // Arm width and taper
    float armBase = mix(0.12, 0.38, s2);
    float taper   = mix(0.35, 0.95, s4);     // 0.35 = strong taper
    float armW    = armBase * mix(1.0, taper, rn) + teeth;

    // --- core primitives ---
    float arm = smoothstep(armW + blur, armW - blur, abs(a)) *
                smoothstep(1.00, 0.40, rn);

    // Inner hex ring / facet
    float ringR = mix(0.12, 0.42, s1);
    float ringT = mix(0.010, 0.060, s3);
    float ring = smoothstep(ringT + blur, ringT - blur, abs(rn - ringR));

    // Secondary arms (12-fold-ish feel) occasionally
    float arm2 = 0.0;
    if(tsel > 0.72){
        float a12 = fold6(ang + 3.14159265/6.0);
        float w2 = armW * 0.75;
        arm2 = smoothstep(w2 + blur, w2 - blur, abs(a12)) * smoothstep(1.0, 0.55, rn);
    }

    float flake = 0.0;

    // -------------------------
    // TYPE 0: Plate / Hex star (cleaner, geometric)
    // -------------------------
    if(type == 1 || type == 2){
        float plate = smoothstep(0.55 + blur, 0.55 - blur, rn) *
                      smoothstep(0.20 + blur, 0.20 - blur, abs(a) + 0.12*rn);

        float facets = ring * mix(0.35, 0.85, s2);

        flake = max(plate, arm*mix(0.7, 1.3, s4));
        flake = max(flake, facets);
        flake = max(flake, arm2*0.6);
    }

    // -------------------------
    // TYPE 1: Dendrite (branchy)
    // -------------------------
    if(type == 0){
        flake = arm * mix(0.9, 1.4, s4);

        // Multiple branch layers (up to 4)
        float branchCountF = floor(mix(2.0, 4.99, s1)); // 2..4
        float bSpreadBase  = mix(0.12, 0.42, s3);
        float bWidth       = mix(0.10, 0.28, s2);

        for(int bi=0; bi<4; bi++){
            if(float(bi) < branchCountF){
                float fi = float(bi);

                float bPos = mix(0.22, 0.88, nrand(seed*17.0 + fi*9.3 + 1.1));
                float bAng = bSpreadBase + mix(0.02, 0.14, nrand(seed*21.0 + fi*7.7)) + fi*0.06;

                float branch =
                    smoothstep(bWidth + blur, bWidth - blur, abs(a - bAng)) *
                    smoothstep((bPos+0.02) + blur, (bPos+0.02) - blur, abs(rn - bPos));

                // mirrored branch (both sides)
                float branch2 =
                    smoothstep(bWidth + blur, bWidth - blur, abs(a + bAng)) *
                    smoothstep((bPos+0.02) + blur, (bPos+0.02) - blur, abs(rn - bPos));

                flake = max(flake, max(branch, branch2));
            }
        }

        flake = max(flake, ring*0.45);
        flake = max(flake, arm2*0.55);
    }

    // -------------------------
    // TYPE 2: Needle / Spokes (thin, sparse)
    // -------------------------
    if(type == 3 || type == 4){
        float thinW = armW * mix(0.35, 0.60, s2);
        float spoke = smoothstep(thinW + blur, thinW - blur, abs(a)) *
                      smoothstep(1.0, mix(0.62, 0.80, s3), rn);

        // occasional small nodes
        float nodePos = mix(0.30, 0.75, s1);
        float node = smoothstep(0.03 + blur, 0.03 - blur, abs(rn - nodePos)) *
                     smoothstep(thinW*1.4 + blur, thinW*1.4 - blur, abs(a));

        flake = max(spoke, node*0.8);
        flake = max(flake, ring*0.25);
        flake = max(flake, arm2*0.35);
    }

    // -------------------------
    // TYPE 3: Graupel / Clumpy (round-ish, irregular)
    // --------3----------------
    if(type == 5){
        // base blob
        float blob = 1.0 - smoothstep(0.55 - teethAmp*0.5, 0.55 + blur*2.0, rn);

        // carve facets lightly so its not a pure circle
        float facet = smoothstep(0.20 + blur, 0.20 - blur, abs(a) + 0.22*rn);
        blob *= mix(0.75, 1.0, facet);

        // some faint arms so it still reads as snow-ish
        flake = max(blob, arm*0.55);
        flake = max(flake, ring*0.25);
    }

    // readability core (helps when tiny)
    float core = 1.0 - smoothstep(0.10, 0.18 + blur*2.0, rn);
    flake = max(flake, core * 0.18);

    return sat(flake);
}


vec2  hash22(vec2 p){
    float n = sin(dot(p, vec2(41.0, 289.0)));
    return fract(vec2(262144.0, 32768.0) * n);
}

vec2 curl(vec2 p){
    float e = 0.08;
    float n1 = noise(p + vec2(0.0, e));
    float n2 = noise(p - vec2(0.0, e));
    float n3 = noise(p + vec2(e, 0.0));
    float n4 = noise(p - vec2(e, 0.0));
    float dy = (n1 - n2) / (2.0*e);
    float dx = (n3 - n4) / (2.0*e);
    return vec2(dy, -dx);
}

vec2 flowOffset(vec2 base, float life, float layerSeed, float time){
    vec2 pos = base * FLOW_SCALE + vec2(layerSeed, layerSeed*0.73);
    vec2 v = vec2(0.0);
    float tt = time*FLOW_SPEED + life*2.0 + layerSeed*0.11;

    for(int k=0; k<FLOW_STEPS; k++){
        vec2 c = curl(pos + vec2(tt, -tt));
        v += c;
        pos += c * FLOW_STEP;
        tt  += 0.37;
    }

    v /= float(FLOW_STEPS);
    return v * FLOW_STRENGTH;
}

float smoothCircle(vec2 p, float r, float blur){
    float d = length(p) - r;
    return 1.0 - smoothstep(-blur, blur, d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 res = iResolution.xy;
    vec2 uv  = fragCoord / res.xy;
    vec2 cuv = (fragCoord - 0.5*res) / res.y;
    float t  = iTime;

    float groundSnow = texture(iChannel0, uv).r;

    float n = noise(uv*6.0 + vec2(0.0, t*0.12));
    vec3 col = GROUND_BASE + SOFT_BG * vec3(n*0.10, n*0.12, n*0.16);
    col = mix(col, SNOW_TINT, clamp(groundSnow * 1.25, 0.0, 1.0));

    float snow = 0.0;

    for(int L=0; L<LAYERS; L++){
        float lf    = float(L);
        float depth = (lf + 1.0) / float(LAYERS);

        float scale = WORLD_SCALE * mix(1.6, 0.9, depth);
        float rate  = BASE_RATE   * mix(0.35, 1.0, depth);

        vec2 p = cuv * scale;
        vec2 cell = floor(p);
        vec2 f    = fract(p);

        for(int j=-1; j<=1; j++)
        for(int i=-1; i<=1; i++){
            vec2 c = cell + vec2(float(i), float(j));

            for(int s=0; s<SPAWNS_PER_CELL; s++){
                float sf = float(s);

                float rLife = hash21(c + lf* 9.1 + sf*23.7);
                float rGate = hash21(c + lf*31.9 + sf*41.7 + 7.13);

                float phase = t * rate + rLife;
                float cycle = floor(phase);
                float life  = fract(phase);

                // cycle-dependent position (prevents restart in same spot)
                vec2 rPos = hash22(c + lf*11.7 + sf*17.3 + cycle*13.7);

                if(rGate < DENSITY){
                    float life = fract(t * rate + rLife);

                    vec2 fp = rPos + flowOffset(rPos + c, life, lf*7.3 + sf*3.9, t);

                    vec2 d = (vec2(float(i), float(j)) + fp) - f;

                    float sizeK = mix(START_SIZE, END_SIZE, life);
                    float baseR = mix(0.020, 0.070, depth) * mix(0.65, 1.35, rPos.y) * sizeK;
                    float blur  = mix(0.012, 0.032, depth) * sizeK;

                    float fadeIn  = smoothstep(FADEIN_A,  FADEIN_B,  life);
                    float fadeOut = 1.0 - smoothstep(FADEOUT_A, FADEOUT_B, life);

                    // deterministic per-flake randoms (already in your loop)
                    float seed = fract(rLife * 13.37 + rPos.x * 7.31 + rPos.y * 3.17);
                    float rot  = (seed - 0.5) * 6.2831853;

                    float a = snowflakeMaxVar(d, baseR, blur, seed, rot) * fadeIn * fadeOut;
                    a *= mix(0.25, 1.05, depth);

                    snow += a;
                }
            }
        }
    }

    snow = clamp(snow, 0.0, 3.0);
    col += vec3(0.95, 0.97, 1.00) * snow * BRIGHTNESS;

    col = 1.0 - exp(-col);
    // ----- Ground alpha (configurable) -----
    float groundAlpha = pow(clamp(groundSnow,0.0,1.0), GROUND_ALPHA_POWER);
    groundAlpha *= GROUND_ALPHA_SCALE;

    float flakesAlpha = step(0.001, snow);

    float alpha = max(groundAlpha, flakesAlpha);

    // premultiply
    fragColor = vec4(col * alpha, alpha);

}

iChannel0.source:
// Buffer A: Accumulation from top-down flakes (meandering turbulence, no spirals)
// iChannel0 MUST be Buffer A (self feedback)
//
// NOTE: This is a "manual 1/2 res" simulation for non-Pro Shadertoy:
// it computes on a 2x2 block grid and replicates values to all pixels in the block.

#ifdef GL_ES

#endif

#define LAYERS 4
#define SPAWNS_PER_CELL 2
#define FLOW_STEPS 2

const float WORLD_SCALE = 9.0;

// Spawn tuning
const float DENSITY = 0.60;
const float BASE_RATE = 0.55;

// Flow-field (must match Image!)
const float FLOW_SCALE    = 2.2;
const float FLOW_SPEED    = 0.35;
const float FLOW_STRENGTH = 0.18;
const float FLOW_STEP     = 0.22;

// Accumulation
const float MELT         = 0.99995;
const float DIFFUSE      = 0.40;
const float DEPOSIT_GAIN = 3.95;

// Deposit timing + footprint (must match Image)
const float LAND_A   = 0.90;
const float LAND_B   = 1.00;
const float END_SIZE = 0.18;

float hash21(vec2 p){ return fract(sin(dot(p, vec2(127.1,311.7))) * 43758.5453123); }
vec2  hash22(vec2 p){
    float n = sin(dot(p, vec2(41.0, 289.0)));
    return fract(vec2(262144.0, 32768.0) * n);
}

float noise(vec2 p){
    vec2 i = floor(p), f = fract(p);
    vec2 u = f*f*(3.0 - 2.0*f);
    float a = hash21(i);
    float b = hash21(i + vec2(1.0,0.0));
    float c = hash21(i + vec2(0.0,1.0));
    float d = hash21(i + vec2(1.0,1.0));
    return mix(mix(a,b,u.x), mix(c,d,u.x), u.y);
}

// 2D curl noise vector (divergence-free-ish)
vec2 curl(vec2 p){
    float e  = 0.08;
    float n1 = noise(p + vec2(0.0, e));
    float n2 = noise(p - vec2(0.0, e));
    float n3 = noise(p + vec2(e, 0.0));
    float n4 = noise(p - vec2(e, 0.0));
    float dy = (n1 - n2) / (2.0*e);
    float dx = (n3 - n4) / (2.0*e);
    return vec2(dy, -dx);
}

// Integrate a small flow offset based on life (no obvious loops)
vec2 flowOffset(vec2 base, float life, float layerSeed, float time){
    vec2 pos = base * FLOW_SCALE + vec2(layerSeed, layerSeed*0.73);
    vec2 v   = vec2(0.0);

    float tt = time*FLOW_SPEED + life*2.0 + layerSeed*0.11;

    for(int k=0; k<FLOW_STEPS; k++){
        vec2 c = curl(pos + vec2(tt, -tt));
        v   += c;
        pos += c * FLOW_STEP;
        tt  += 0.37;
    }

    v /= float(FLOW_STEPS);
    return v * FLOW_STRENGTH;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 res = iResolution.xy;
    float t  = iTime;

    // -------- Manual stable 1/2-res mapping on a 2x2 block grid --------
    // Map every pixel to the SAME "block center" so feedback is consistent.
    // blockId is the logical half-res pixel coordinate.
    vec2 blockId   = floor(fragCoord * 0.5);               // 0..(res/2 - 1)
    vec2 blockCtr  = blockId * 2.0 + vec2(0.5, 0.5);       // top-left pixel center of the 2x2 block
    vec2 uv        = blockCtr / res;
    vec2 cuv       = (blockCtr - 0.5*res) / res.y;

    // Feedback + melt (sample using the SAME block-mapped uv)
    float snow = texture(iChannel0, uv).r * MELT;

    // Diffusion: IMPORTANT -> move by 2 pixels (one logical half-res cell)
    vec2 px2 = (2.0 / res);
    float b =
        texture(iChannel0, uv + vec2(px2.x, 0.0)).r +
        texture(iChannel0, uv + vec2(-px2.x,0.0)).r +
        texture(iChannel0, uv + vec2(0.0, px2.y)).r +
        texture(iChannel0, uv + vec2(0.0,-px2.y)).r;
    b *= 0.25;
    snow = mix(snow, b, DIFFUSE);

    float deposit = 0.0;

    for(int L=0; L<LAYERS; L++){
        float lf    = float(L);
        float depth = (lf + 1.0) / float(LAYERS);

        float scale = WORLD_SCALE * mix(1.6, 0.9, depth);
        float rate  = BASE_RATE   * mix(0.35, 1.0, depth);

        vec2 p    = cuv * scale;
        vec2 cell = floor(p);
        vec2 f    = fract(p);

        for(int j=-1; j<=1; j++)
        for(int i=-1; i<=1; i++){
            vec2 c = cell + vec2(float(i), float(j));

            for(int s=0; s<SPAWNS_PER_CELL; s++){
                float sf = float(s);

                // EARLY-OUT: gate first
                float rGate = hash21(c + lf*31.9 + sf*41.7 + 7.13);
                if(rGate >= DENSITY) continue;

                float rLife  = hash21(c + lf* 9.1 + sf*23.7);

                float phase  = t * rate + rLife;
                float cycle  = floor(phase);
                float life   = fract(phase);

                vec2 rPos = hash22(c + lf*11.7 + sf*17.3 + cycle*13.7);

                vec2 fp = rPos + flowOffset(rPos + c, life, lf*7.3 + sf*3.9, t);
                vec2 d  = (vec2(float(i), float(j)) + fp) - f;

                float land  = smoothstep(LAND_A, LAND_B, life);

                float sizeK = END_SIZE;
                float baseR = mix(0.020, 0.070, depth) * mix(0.65, 1.35, rPos.y) * sizeK;

                float rr   = max(0.002, baseR * 0.95);
                float bump = exp(-dot(d,d) / (rr*rr));

                deposit += land * bump * mix(0.25, 1.0, depth);
            }
        }
    }

    snow += deposit * DEPOSIT_GAIN;

    // Replicated 2x2 blocks happen naturally because all 4 pixels in the block
    // run the same math and write the same value.
    fragColor = vec4(clamp(snow, 0.0, 1.0), 0.0, 0.0, 1.0);
}

shaders.source:
const float TAU = 6.283185;
const float N = 10.;
const float floatAlpha = 0.15;

float distBand(float R1, float R2, float r) {
    return (R2-R1)*0.5 - abs(r - (R1+R2)*0.5);
}

float crest(float a, float r) {
    return 50. * distBand(a*0.5, a, r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    uv -= vec2(iResolution.x/iResolution.y, 1.) * 0.5;
    uv *= 0.5;
    float mask = 0.;
    // Main spirals
    float a = fract(0.5 - atan(uv.y, uv.x) / TAU + iTime*0.1);
    float r = length(uv);
    for (float i = 0.; i < N; ++i) {
        mask += max(crest(fract(a + i/N), r), 0.);
    }

    vec3 col = mix(vec3(1, 45, 64), vec3(166, 228, 255), mask) / 255.;

    // Output to screen
   float alpha = 1.0 - exp(-mask * floatAlpha);   // tweak 0.05
   alpha = clamp(alpha, 0.0, 1.0);
   fragColor = vec4(col * alpha, alpha);    // premultiplied
   fragColor = vec4(col, alpha);
}

/*
    Entangled Vines
    ---------------

    Entangled vine fly-through, which is nothing more than a mutated lattice traversal in disguise.

    I wrote this on a pretty slow computer, so had to do everything on the cheap, which gave me a
    good excuse to use Nimitz's Log-Bisection method. I swear I'm not stalking his code, but it seems 
    every time I need a solution to something, one of his examples magically provides the answer. :)

    The rest was written off the top of my head, but is an amalgamation of a whole bunch of things 
    I've encountered on Shadertoy at one time or another. The simple scene itself was constructed 
	with Gyabo's "Raymarching Basic" in mind. Although I've used different methods, I'm pretty sure 
	Dave Hoskins's "Skin Peeler" (based on Xyptonjtroz) influenced the coloring style. The misty 
	overlay is loosely infuenced from the same example.

    Related examples:

    Log-Bisection Tracing - Nimitz
    https://www.shadertoy.com/view/4sSXzD

    Skin Peeler - Dave Hoskins
    https://www.shadertoy.com/view/XtfSWX
    Based on Xyptonjtroz: Nimitz
    https://www.shadertoy.com/view/4ts3z2

    Raymarching Basic - Gyabo
    https://www.shadertoy.com/view/Ml2XRD    

*/

#define FAR 30.
#define PI 3.14159265

// Grey scale.
float getGrey(vec3 p){ return p.x*0.299 + p.y*0.587 + p.z*0.114; }

// 2x2 rotation matrix.
mat2 rot(float th) {
    float cs = cos(th), sn = sin(th); return mat2(cs, -sn, sn, cs);
}

vec3 firePalette(float i){

    float T = 1400. + 1300.*i; // Temperature range (in Kelvin).
    vec3 L = vec3(7.4, 5.6, 4.4); // Red, green, blue wavelengths (in hundreds of nanometers).
    L = pow(L,vec3(5.0)) * (exp(1.43876719683e5/(T*L))-1.0);
    return 1.0-exp(-5e8/L); // Exposure level. Set to "50." For "70," change the "5" to a "7," etc.
}


// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: http://http.developer.nvidia.com/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){

    //n = abs(n)/1.732051;
    n = max((abs(n) - 0.2)*7., 0.001); // n = max(abs(n), 0.001), etc.
    //n = max(abs(n), 0.001);
    n /= (n.x + n.y + n.z );  

	return (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
}


// Hash to return a scalar value from a 3D vector.
float hash31(vec3 p){ return fract(sin(dot(p, vec3(127.1,311.7, 74.7)))*43758.5453); }

// Standard hash algorithm that you'll see all over the place.
vec3 hash33(vec3 p) { 

    // Faster, but doesn't disperse things quite as nicely as the block below it. However, when framerate
    // is an issue, and it often is, this is the one to use. Basically, it's a tweaked amalgamation I put
    // together, based on a couple of other random algorithms I've seen around... so use it with caution,
    // because I make a tonne of mistakes. :)
    float n = sin(dot(p, vec3(7, 157, 113)));
    return fract(vec3(2097152, 262144, 32768)*n)*2.-1.;  
    //p = fract(vec3(2097152, 262144, 32768)*n); 
    //return sin(p*TAU + time); // Cheap...ish animation.

}

// Texture bump mapping. Four tri-planar lookups, or 12 texture lookups in total.
vec3 doBumpMap( sampler2D tex, in vec3 p, in vec3 nor, float bumpfactor){

    const float eps = 0.001;
    vec3 grad = vec3( getGrey(tex3D(tex, vec3(p.x-eps, p.y, p.z), nor)),
                      getGrey(tex3D(tex, vec3(p.x, p.y-eps, p.z), nor)),
                      getGrey(tex3D(tex, vec3(p.x, p.y, p.z-eps), nor)));

    grad = (grad - getGrey(tex3D(tex,  p , nor)))/eps; 

    grad -= nor*dot(nor, grad);          

    return normalize( nor + grad*bumpfactor );

}

// Smooth minimum function. Hardcoded with the smoothing value "0.25."
float sminP(in float a, in float b ){

    float h = clamp(2.*(b - a) + 0.5, 0.0, 1.0);
    return (b - 0.25*h)*(1. - h) + a*h;

}

// The vine structure.
// 
// I had Shadertoy user Gyabo's "Raymarching Basic" in mind when putting this together:
// https://www.shadertoy.com/view/Ml2XRD
// His example Cune is also worth looking at: https://www.shadertoy.com/view/ls2GDR
float map(vec3 p) {

    // Basic idea: Create a bunch of repeated warped cylinders aligned along the X, Y 
    // and Z directions whilst offsetting their positions. Find their minimum, then 
    // add some bumps.
    //
    // 
    // "length(p.xy) - r" is a standard way to create a cylinder in the Z-direction.
    // Performing "mod(p.xy, a) - a/2." will create multiple cylinders.
    // Perturb the XY-plane to make it windy, and add an offset, while your at it.
    // Do the same in the X and Y directions.
    vec2 perturb = vec2(sin((p.z * 2.15 + p.x * 2.35)), cos((p.z * 1.15 + p.x * 1.25)));
    vec2 perturb2 = vec2(cos((p.z * 1.65 + p.y * 1.75)), sin((p.z * 1.4 + p.y * 1.6)));
	vec2 q1 = mod(p.xy + vec2(0.25, -0.5), 2.) - 1.0 + perturb*vec2(0.25, 0.5);
	vec2 q2 = mod(p.yz + vec2(0.25, 0.25), 2.) - 1.0 - perturb*vec2(0.25, 0.3);
	vec2 q3 = mod(p.xz + vec2(-0.25, -0.5), 2.) - 1.0 - perturb2*vec2(0.25, 0.4);

	// Used to add some bumps on the overall structure. See "p.x*p.y*p.z.. etc" below.
	p = sin(p*8. + cos(p.yzx*8.));

	// The cylinders.
	float s1 = length( q1 ) - 0.24; // max(abs(q1.x), abs(q1.y)) - 0.2; // etc.
	float s2 = length( q2 ) - 0.24;
	float s3 = length( q3 ) - 0.24;

	/*
	// These are cheaper. Unfortunately, the raymarching function takes longer to find the
	// hit point, so that can be problematic.
	float s1 = dot( q1, q1 ) - 0.06; 
	float s2 = dot( q2, q2 ) - 0.06;
	float s3 = dot( q3, q3 ) - 0.06;
	*/

	// Retrieve the minimum of s1, s2 and s3, then add the bumps.
	// In this case, a smooth minimum is used, because it looks a little more organic.
	return sminP(sminP(s1, s3), s2) - p.x*p.y*p.z*0.05;

}

// The iterations should be higher for proper accuracy, but in this case, the shadows are a subtle background feature, so
// hopefully, it's not too noticeable.
float softShadow(in vec3 ro, in vec3 rd, in float start, in float end, in float k){

    float shade = 1.0;
    const int maxIterationsShad = 12; // 24 or 32 would be better. Even 16 would be good, but my computer says, "No." :)

    // The "start" value, or minimum, should be set to something more than the stop-threshold, so as to avoid a collision with 
    // the surface the ray is setting out from. It doesn't matter how many times I write shadow code, I always seem to forget this.
    // If adding shadows seems to make everything look dark, that tends to be the problem.
    float dist = start;
    float stepDist = end/float(maxIterationsShad);

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, the lowest 
    // number to give a decent shadow is the best one to choose. 
    for (int i=0; i<maxIterationsShad; i++){
        // End, or maximum, should be set to the distance from the light to surface point. If you go beyond that
        // you may hit a surface not between the surface and the light.
        float h = map(ro + rd*dist);
        shade = min(shade, k*h/dist);
        //shade = min(shade, smoothstep(0.0, 1.0, k*h/dist));

        // What h combination you add to the distance depends on speed, accuracy, etc. To be honest, I find it impossible to find 
        // the perfect balance. Faster GPUs give you more options, because more shadow iterations always produce better results.
        // Anyway, here's some posibilities. Which one you use, depends on the situation:
        // +=max(h, 0.001), +=clamp( h, 0.01, 0.25 ), +=min( h, 0.1 ), +=stepDist, +=min(h, stepDist*2.), etc.

        // In this particular instance the light source is a long way away. However, we're only taking a few small steps
        // toward the light and checking whether anything "locally" gets in the way. If a part of the vine a long distance away
        // is between our hit point and the light source, it won't be accounted for. Technically that's not correct, but the local
        // shadows give that illusion... kind of.
        dist += clamp(h, 0.0001, .2);

        // There's some accuracy loss involved, but early exits from accumulative distance function can help.
        if (abs(h)<0.001 || dist > end) break; 
    }

    // I usually add a bit to the final shade value, which lightens the shadow a bit. It's a preference thing. Really dark shadows 
    // look too brutal to me.
    return min(max(shade, 0.) + 0.4, 1.0); 
}

// Standard ambient occlusion. Based on the original by IQ.
float calculateAO(vec3 p, vec3 n){

    const float AO_SAMPLES = 5.0;
    float r = 0.0, w = 1.0, d;

    for (float i=1.0; i<AO_SAMPLES+1.1; i++){
        d = i/AO_SAMPLES;
        r += w*(d - map(p + n*d));
        w *= 0.5;
    }

    return 1.0-clamp(r,0.0,1.0);
}

// Tetrahedral normal: I remember a similar version on "Pouet.net" years ago, but this one is courtesy of IQ.
// I'm using it, because I'm trying to make as few surface function calls (4 versus 6) as possible.
vec3 getNormal( in vec3 p ){

    vec2 e = vec2(0.5773,-0.5773)*0.001;
    return normalize( e.xyy*map(p+e.xyy ) + e.yyx*map(p+e.yyx ) + e.yxy*map(p+e.yxy ) + e.xxx*map(p+e.xxx ));
}



// Log-Bisection Tracing
// https://www.shadertoy.com/view/4sSXzD

// Log-Bisection Tracing by nimitz (twitter: @stormoid)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Contact: nmz@Stormoid.com

// Notes: I've made some minor changes to Nitmitz's original to suit this particular example. It 
// seems to work, but if you're interested in the function itself, I'd probably bypass this example, 
// and refer to the original function in the link above. There, you'll find a good explanation as to 
// how it works too.

// In essence, the first loop manages to hone in on the hit point pretty quickly, mainly due to the
// line involving the logarithm (clever). However, it has a tendency to overshoot. If that happens, 
// points from either side of the hit point are fed into the second loop, which employs a bisection 
// method that eventually gets you there. There's a bunch of stuff that I'm skipping over, and you 
// can read about it in the original. The function doesn't work for all setups, but in cases such as 
// this, you can get a pretty decent performance boost.

#define PRECISION 0.001

float logBisectTrace( in vec3 ro, in vec3 rd){


    float t = 0., told = 0., mid, dn;
    float d = map(rd*t + ro);
    float sgn = sign(d);

    for (int i=0; i<80; i++){

        // If the threshold is crossed with no detection, use the bisection method.
        // Also, break for the usual reasons. Note that there's only one "break"
        // statement in the loop. I heard GPUs like that... but who knows?
        if (sign(d) != sgn || d < PRECISION || t > FAR) break;

        told = t;

        // If the distance from the root is high enough, use standard raymarching,
        // instead of the log bisect method.
        //if (d > 1.) t += d*.75;
        // "0.75" determines how fast the root finder moves in. Needs to be lowered when dealing 
        // with thin slices. The potential problem is the intersector crossing the function twice 
        // in one step.
        //else t += log(abs(d) + 1.1)*.75; // //To cross faster, a minimum step size.

        // Branchless version of the above.        
        t += step(-1., -d)*(log(abs(d) + 1.1)*.7 - d*.7) + d*.7;

        d = map(rd*t + ro);
    }

    // If a threshold was crossed without a solution, use the bisection method.
    if (sign(d) != sgn){

        // Based on suggestions from CeeJayDK, with some minor changes.

        dn = sign(map(rd*told + ro));

        vec2 iv = vec2(told, t); // Near, Far

        // 6 iterations seems to be more than enough, for most cases...
        // but there's an early exit, so I've added a couple more.
        for (int ii=0; ii<8; ii++){ 
            //Evaluate midpoint
            mid = dot(iv, vec2(.5));
            float d = map(rd*mid + ro);
            if (abs(d) < PRECISION)break;
            // Suggestion from movAX13h - Shadertoy is one of those rare
            // sites with helpful commenters. :)
            // Set mid to near or far, depending on which side we're on.
            iv = mix(vec2(iv.x, mid), vec2(mid, iv.y), step(0.0, d*dn));
        }

        t = mid; 

    }

    //if (abs(d) < PRECISION) t += d;

    return t;
}



/////
// Code block to produce four layers of fine mist. Not sophisticated at all.
// If you'd like to see a much more sophisticated version, refer to Nitmitz's
// Xyptonjtroz example. Incidently, I wrote this off the top of my head, but
// I did have that example in mind when writing this.
float trig3(in vec3 p){
    p = cos(p*2. + (cos(p.yzx) + 1. + iTime*4.)*1.57);
    return dot(p, vec3(0.1666)) + 0.5;
}

// Basic low quality noise consisting of three layers of rotated, mutated 
// trigonometric functions. Needs work, but it's OK for this example.
float trigNoise3D(in vec3 p){

    // 3D transformation matrix.
    const mat3 m3RotTheta = mat3(0.25, -0.866, 0.433, 0.9665, 0.25, -0.2455127, -0.058, 0.433, 0.899519 )*1.5;

	float res = 0.;

    float t = trig3(p*PI);
	p += (t - iTime*0.25);
    p = m3RotTheta*p;
    //p = (p+0.7071)*1.5;
    res += t;

    t = trig3(p*PI); 
	p += (t - iTime*0.25)*0.7071;
    p = m3RotTheta*p;
     //p = (p+0.7071)*1.5;
    res += t*0.7071;

    t = trig3(p*PI);
	res += t*0.5;

	return res/2.2071;
}

// Four layers of cheap trigonometric noise to produce some subtle mist.
// Start at the ray origin, then take four samples of noise between it
// and the surface point. Apply some very simplistic lighting along the 
// way. It's not particularly well thought out, but it doesn't have to be.
float getMist(in vec3 ro, in vec3 rd, in vec3 lp, in float t){

    float mist = 0.;
    ro += rd*t/8.; // Edge the ray a little forward to begin.

    for (int i = 0; i<4; i++){
        // Lighting. Technically, a lot of these points would be
        // shadowed, but we're ignoring that.
        float sDi = length(lp-ro)/FAR; 
	    float sAtt = min(1./(1. + sDi*0.25 + sDi*sDi*0.05), 1.);
	    // Noise layer.
        mist += trigNoise3D(ro*2.)*sAtt;
        // Advance the starting point towards the hit point.
        ro += rd*t/4.;
    }

    // Add a little noise, then clamp, and we're done.
    return clamp(mist/2. + hash31(ro)*0.1-0.05, 0., 1.);

}

/////

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


    // Screen coordinates.
	vec2 uv = (fragCoord.xy - iResolution.xy*0.5)/iResolution.y;

    // Ray origin. Traversing with time along the Z-axis.
    vec3 ro = vec3(0., 0., iTime*2.);

    // Lazy way to construct a unit direction ray.
    vec3 rd = normalize(vec3(uv, 0.5));

    // Equally lazy way to look around the scene by rotating the unit direction vector. 
    mat2 m2 = rot(iTime * 0.25);
    rd.xz *= m2;
    rd.xy *= m2;
    rd.yz *= m2;


    // The light position. In this case, it's the quasi-distant sun position, which is situated about 30 units in front
    // of the viewing position, "ro." I've arranged for it to rotate about its postion just a little.
    // "rd*10." is not realistic. No distant light source rotates around like that, but it makes the shadows move a little.
    // Obviously, a real sun would be much, much further away, but by keeping it within a workable distance, you can
    // get a bit of a point light effect.
    vec3 lp = vec3(0., 0., FAR) + ro + rd*10.;

    // Standard way to put a light in the sky. Dot the unit direction vector with the unit light direction vector.
    // Normalize the result, then ramp up the power. In this case, I want to spread the brightness out, so a lower figure 
    // of about "4" is being chosen. For a more contrasty sky with intense sun, larger values are used. 
    float bgShade = pow(max(dot(rd, normalize(lp - ro)), 0.)*0.5+0.5, 4.);
    // Background (or sky) color. Blend the shade between two colors. These two aren't very inspiring, but the coloring
    // is being done post process, so it's essentially dark to bright for now.
    vec3 bc = mix(vec3(.0, .0,.05), vec3(1.), bgShade);

    // Initiate the scene color to the background (sky) color.
    vec3 sc = bc;

    // Use Nimitz's really fancy raymarching algorithm. :)
    float t = logBisectTrace(ro, rd);


    if(t<FAR){

        // Surface position.
        vec3 sp = ro + rd*t;

        // Normal.
        vec3 sn = getNormal(sp);

        // Texture bump the normal.
        const float texSize0 = 1./2.;
        sn = doBumpMap(iChannel0, sp*texSize0, sn, 0.025);

        // Obtain the texel color at the surface position.
        vec3 objCol = tex3D( iChannel0, sp*texSize0, sn );//vec3(min(c*1.5, 1.), pow(c, 2.5), pow(c, 10.));

        // Light direction vector. From the sun to the surface point.
        vec3 ld = lp-sp;

        // Distance from the surface postion to the light source (sun position).
        float lDist = max(length(ld), 0.001);

        ld /= lDist; // Normalize the light direct vector.

	    // Attenuation, based on the distance of the light (sun) to the surface point.
	    lDist /= FAR; // Bringing the light distance down to the zero to one range, which is more workable.
        float sAtten = min(1./(1. + lDist*0.125 + lDist*lDist*0.05), 1.);


        // Shadowing and occlusion. 
        float shad = softShadow(sp, ld, 0.05, FAR, 8.);
        float ao = calculateAO(sp, sn);

    	// Standard diffuse and specular calculations.
        float diff = max(dot(sn, ld), 0.);
        float spec = pow(max( dot( reflect(-ld, sn), -rd ), 0.0 ), 8.);

        // Combining the properties above to produce the lit color.
        sc = (objCol*(diff + 0.5) + spec)*sAtten;

        // Applying the shadows and occlusion.
        sc = min(sc, 1.)*shad*ao;


    }


    // Fog.
    //
    // Fog - Based on distance from the viewing position. Not to be confused with the misty haze.
    // Mix the background color (sky color above) and the object color according to a falloff value,
    // which is analogous to fog, so we call it that. Pretty standard.
    float fog = min(1.0 / (1. + t*0.25 + t*t*0.025), 1.);
    sc = mix(bc, sc, fog);
    //sc = mix(sc, bc, smoothstep(0.0, FAR-20., t)); // Another way to mix things, but using a quick transition.


    // Color post processing. Fading from orange to the original color... for no particular reason.
    // Comment the following block out to see the original effect minus the orange.
    vec3  sc2 = firePalette(getGrey(sc));
    float fadeFactor = min(1.0 / (1. + t), 1.); // Color fade factor. Made up to suit the conditions.
    sc = mix(sc, sc2, fadeFactor*0.34+0.66);


    // Fake misty overlay.  
    //
    // Adding the misty haze... otherwise known as the lamest volumetric effect ever. :)
    // Start at the ray origin, then accumulate four layers between it and the hit position.
    float mist = getMist(ro, rd, lp, t);

    // Combining the mist value, sky color (bgShade, etc) and fog to give a fog color.
    // Part science, part made up.
    vec3 fogCol = mix(vec3(bgShade*0.8+0.2)*vec3(1., 0.85, 0.6), sc, mist*fog);
    // Toning down the fog color. Totally made up. :)
    sc = sc*0.65 + fogCol*0.35;


    // Done.
	fragColor = vec4(clamp(sc, 0., 1.), 1.0);

}

#define MOD3 vec3(.1031,.11369,.13787)

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}

float simplex_noise(vec3 p)
{
    const float K1 = 0.333333333;
    const float K2 = 0.166666667;

    vec3 i = floor(p + (p.x + p.y + p.z) * K1);
    vec3 d0 = p - (i - (i.x + i.y + i.z) * K2);

    vec3 e = step(vec3(0.0), d0 - d0.yzx);
	vec3 i1 = e * (1.0 - e.zxy);
	vec3 i2 = 1.0 - e.zxy * (1.0 - e);

    vec3 d1 = d0 - (i1 - 1.0 * K2);
    vec3 d2 = d0 - (i2 - 2.0 * K2);
    vec3 d3 = d0 - (1.0 - 3.0 * K2);

    vec4 h = max(0.6 - vec4(dot(d0, d0), dot(d1, d1), dot(d2, d2), dot(d3, d3)), 0.0);
    vec4 n = h * h * h * h * vec4(dot(d0, hash33(i)), dot(d1, hash33(i + i1)), dot(d2, hash33(i + i2)), dot(d3, hash33(i + 1.0)));

    return dot(vec4(31.316), n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;       

    float cc = 1.;
    for(float i = 1.; i<2.0; i+=0.1){
        vec2 iuv = uv*i*.5;
        float s = simplex_noise(vec3(iuv*10.*i, iTime*.25 + i*10.));        
        s += iTime*.25 + i;  
        s = fract(s);
        cc = min(cc, (s > .0  && s < i*.05) ? 1. - i*.945 : 1.0);                
    }    

    fragColor = vec4(fract(cc));    
}

vec3 palette(float d){
	return mix(vec3(0.2,0.7,0.9),vec3(1.,0.,1.),d);
}

vec2 rotate(vec2 p,float a){
	float c = cos(a);
    float s = sin(a);
    return p*mat2(c,s,-s,c);
}

float map(vec3 p){
    for( int i = 0; i<8; ++i){
        float t = iTime*0.2;
        p.xz =rotate(p.xz,t);
        p.xy =rotate(p.xy,t*1.89);
        p.xz = abs(p.xz);
        p.xz-=.5;
	}
	return dot(sign(p),p)/5.;
}

vec4 rm (vec3 ro, vec3 rd){
    float t = 0.;
    vec3 col = vec3(0.);
    float d;
    for(float i =0.; i<64.; i++){
		vec3 p = ro + rd*t;
        d = map(p)*.5;
        if(d<0.02){
            break;
        }
        if(d>100.){
        	break;
        }
        //col+=vec3(0.6,0.8,0.8)/(400.*(d));
        col+=palette(length(p)*.1)/(400.*(d));
        t+=d;
    }
    return vec4(col,1./(d*100.));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-(iResolution.xy/2.))/iResolution.x;
	vec3 ro = vec3(0.,0.,-50.);
    ro.xz = rotate(ro.xz,iTime);
    vec3 cf = normalize(-ro);
    vec3 cs = normalize(cross(cf,vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cf,cs));

    vec3 uuv = ro+cf*3. + uv.x*cs + uv.y*cu;

    vec3 rd = normalize(uuv-ro);

    vec4 col = rm(ro,rd);


    fragColor = col;
}

