// Transparent Spider Web (Based On Audio-Reactive Version)

float hash(vec2 p)
{
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453123);
}

float noise(vec2 p)
{
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f*f*(3.0-2.0*f);

    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));

    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}

float sdSegment(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p - a;
    vec2 ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

float sdBezier(vec2 p, vec2 a, vec2 b, vec2 c)
{
    float minDist = 1000.0;
    for(float t = 0.0; t <= 1.0; t += 0.02)
    {
        vec2 point = a*(1.0-t)*(1.0-t) +
                     b*2.0*(1.0-t)*t +
                     c*t*t;
        minDist = min(minDist, length(p - point));
    }
    return minDist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float blur = 2.0 / iResolution.y;

    vec2 center = vec2(0.0);

    float dist = length(uv);
    float angle = atan(uv.y, uv.x);

    int numRadials = 18;
    float radialAngle = 6.2831853 / float(numRadials);

    float webAlpha = 0.0;

    float webThickness = 0.003;
    float thickWebThickness = 0.006;

    // -----------------------------
    // RADIAL THREADS
    // -----------------------------
    for(int i = 0; i < numRadials; i++)
    {
        float a = float(i) * radialAngle;
        vec2 dir = vec2(cos(a), sin(a));
        vec2 endPoint = center + dir * 1.5;

        float d = sdSegment(uv, center, endPoint);

        float thickness = (mod(float(i), 4.0) == 0.0)
            ? thickWebThickness
            : webThickness;

        float intensity = smoothstep(thickness + blur,
                                     thickness - blur,
                                     d);

        webAlpha += intensity;
    }

    // -----------------------------
    // SPIRAL CAPTURE THREAD (Arcs)
    // -----------------------------
    int numCircles = 14;

    for(int i = 1; i <= numCircles; i++)
    {
        float radius = float(i) * 0.10;

        for(int j = 0; j < numRadials; j++)
        {
            float a1 = float(j) * radialAngle;
            float a2 = float(j + 1) * radialAngle;

            vec2 p1 = vec2(cos(a1), sin(a1)) * radius;
            vec2 p2 = vec2(cos(a2), sin(a2)) * radius;

            float midAngle = (a1 + a2) * 0.5;

            // natural sag
            float sag = 0.92 - float(i) * 0.015;
            vec2 control = vec2(cos(midAngle),
                                sin(midAngle)) * radius * sag;

            float arcDist = sdBezier(uv, p1, control, p2);

            // organic breaks
            float breakPattern = noise(vec2(float(j)*1.3,
                                            float(i)*0.7));
            float breakMask = step(0.2, breakPattern);

            float thickness = (mod(float(i), 3.0) == 0.0)
                ? thickWebThickness * 0.8
                : webThickness;

            float intensity = smoothstep(thickness + blur,
                                         thickness - blur,
                                         arcDist);

            webAlpha += intensity * breakMask;
        }
    }

    webAlpha = clamp(webAlpha, 0.0, 1.0);

    vec3 webColor = vec3(0.92, 0.95, 1.0);

    fragColor = vec4(webColor, webAlpha);
}

#define _ColorA vec3(0.1, 0.4, 1.0)
#define _ColorB vec3(1.0, 0.2, 0.8)
#define _Speed 1.0
#define _Layers 12.0
#define _Glow 0.15
#define _AlphaBoost 1.5

float hash(float n) { 
    return fract(sin(n) * 43758.5453); 
}

float Star(vec2 uv, float size) {
    float d = length(uv);
    float rays = max(0.0, 1.0 - abs(uv.x * uv.y * 5000.0));
    return (0.01 * size / d + rays * 0.1 * size) * smoothstep(0.1, 0.02, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);
    float phi = (1.0 + sqrt(6.0)) / 2.0;
    float invP = 1.0 / phi;
    float alp = 0.;

    vec3 v[20];
    v[0]=vec3(-1,-1,-1); v[1]=vec3(1,-1,-1); v[2]=vec3(1,1,-1); v[3]=vec3(-1,1,-1);
    v[4]=vec3(-1,-1,1); v[5]=vec3(1,-1,1); v[6]=vec3(1,1,1); v[7]=vec3(-1,1,1);
    v[8]=vec3(0,-invP,-phi); v[9]=vec3(0,invP,-phi); v[10]=vec3(0,invP,phi); v[11]=vec3(0,-invP,phi);
    v[12]=vec3(-invP,-phi,0); v[13]=vec3(invP,-phi,0); v[14]=vec3(invP,phi,0); v[15]=vec3(-invP,phi,0);
    v[16]=vec3(-phi,0,-invP); v[17]=vec3(phi,0,-invP); v[18]=vec3(phi,0,invP); v[19]=vec3(-phi,0,invP);


    int ed[60] = int[](0,8, 1,8, 8,9, 2,9, 3,9, 0,16, 3,16, 16,19, 4,19, 7,19, 1,17, 2,17, 17,18, 5,18, 6,18, 4,12, 5,12, 12,13, 0,13, 1,13, 2,14, 3,14, 14,15, 6,15, 7,15, 4,11, 5,11, 10,11, 6,10, 7,10);

    for (float j = 0.0; j < _Layers; j++)
    {
        float t = iTime * _Speed + j * 0.15*cos(iTime);


        float s1 = sin(t * 0.1), c1 = cos(t * 0.1);
        mat3 rotX = mat3(1, 0, 0, 0, c1, -s1, 0, s1, c1);

        float s2 = sin(t * 0.1), c2 = cos(t * 0.4);
        mat3 rotY = mat3(c2, 0, s2, 0, 1, 0, -s2, 0, c2);

        mat3 transform = rotX*rotY;

        float size = 0.2 + j * 0.055;
        vec3 layerCol = mix(_ColorA, _ColorB, j / _Layers);

        for (int n = 0; n < 30; n++)
        {
            vec3 p1 = transform * (v[ed[n*2]] * size);
            vec3 p2 = transform * (v[ed[n*2+1]] * size);


            float z1 = 1.0 / (3.0 - p1.z), z2 = 1.0 / (3.0 - p2.z);
            vec2 a = p1.xy * z1, b = p2.xy * z2;

            vec2 pa = uv - a, ba = b - a;
            float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
            float d = length(pa - ba * h);

            float lineDist = smoothstep(0.005, 0.0, d);
            col += layerCol * lineDist * (p1.z + 2.0) * 0.1;


            float shift = hash(float(n) + j * 13.7);
            vec3 pStar = mix(p1, p2, shift);
            float twinkle = sin(iTime * 5.0 + float(n) + j) * 0.5 + 0.5;
            col += layerCol * Star(uv - pStar.xy * (1.0 / (3.0 - pStar.z)), 0.4) * twinkle * (pStar.z + 1.5);

            alp = col.r+col.g+col.b;
        }
    }


    col += _ColorA * (_Glow * 0.05 / (length(uv) + 0.1));


    fragColor = vec4(col, alp);
}

const float CIRCLE_RADIUS = 0.95;
const float daggerSize = 0.07;
const int BLADES = 40;
const float fade = 0.94; // smaller = longer trails
const float trailIntensity = 0.01; // smaller = longer trails

struct Dagger {
    vec2 pos;
    float t;
    float wobble;
};

float hash(float n) { return fract(sin(n)*43758.5453); }

Dagger getDagger(int i, float iTime){
    float fi = float(i);
    float seed1 = hash(fi*1.7);
    float seed2 = hash(fi*3.1);
    float seed3 = hash(fi*5.9);

    float baseRadius = mix(CIRCLE_RADIUS*0.4, CIRCLE_RADIUS*0.95, seed1);
    float speed = mix(0.6,2.0,seed2);
    float wobble = mix(0.3,1.5,seed3);
    float t = iTime*speed + fi*6.28318;

    vec2 orbit = vec2(cos(t), sin(t*wobble))*baseRadius;
    orbit += 0.08*vec2(sin(t*3.0+fi), cos(t*2.5+fi));

    Dagger d;
    d.pos = orbit;
    d.t = t;
    d.wobble = wobble;
    return d;
}

vec2 getuv(vec2 fragCoord, vec3 iResolution)
{  
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    return uv;
}

float blade(vec2 p, float angle, float width){
    float ca = cos(angle);
    float sa = sin(angle);
    p = mat2(ca, -sa, sa, ca) * p;

    float d = abs(p.x) + abs(p.y*3.0);
    return smoothstep(width, 0.0, d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = getuv(fragCoord, iResolution);

    // Load trails
    vec3 col = texture(iChannel0, fragCoord.xy/iResolution.xy).rgb;
    float trailAlpha = smoothstep(0.01, 0.2, max(col.r, max(col.g, col.b)));
    float alpha = trailAlpha;
    for(int i=0;i<BLADES;i++){        
        Dagger d = getDagger(i, iTime);
        vec2 orbit = d.pos;

        // Hard constraint to circle
        float len = length(orbit);
        if(len > CIRCLE_RADIUS) orbit = orbit/len*CIRCLE_RADIUS;

        // Blade orientation
        float angle = d.t + sin(d.t*d.wobble)*0.4;

        vec2 p = uv - orbit;
        float b = blade(p, angle, daggerSize);

        vec3 bladeColor = vec3(0.75,0.78,0.82);
        bladeColor += 0.25*cos(vec3(0.0,1.0,2.0) + float(i)*1.9);

        col += bladeColor * b;
        alpha += b;
    }

    col = pow(col, vec3(0.85));
    alpha = smoothstep(0.05,0.4,alpha);
    fragColor = vec4(col, alpha);
}

