const float CIRCLE_RADIUS = 0.95;
const float daggerSize = 0.07;
const int BLADES = 40;
const float fade = 0.94; // smaller = longer trails
const float trailIntensity = 0.01; // smaller = longer trails

struct Dagger {
    vec2 pos;
    float t;
    float wobble;
};

float hash(float n) { return fract(sin(n)*43758.5453); }

Dagger getDagger(int i, float iTime){
    float fi = float(i);
    float seed1 = hash(fi*1.7);
    float seed2 = hash(fi*3.1);
    float seed3 = hash(fi*5.9);

    float baseRadius = mix(CIRCLE_RADIUS*0.4, CIRCLE_RADIUS*0.95, seed1);
    float speed = mix(0.6,2.0,seed2);
    float wobble = mix(0.3,1.5,seed3);
    float t = iTime*speed + fi*6.28318;

    vec2 orbit = vec2(cos(t), sin(t*wobble))*baseRadius;
    orbit += 0.08*vec2(sin(t*3.0+fi), cos(t*2.5+fi));

    Dagger d;
    d.pos = orbit;
    d.t = t;
    d.wobble = wobble;
    return d;
}

vec2 getuv(vec2 fragCoord, vec3 iResolution)
{  
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    return uv;
}

// Buffer A  trails
// iChannel0 = previous frame

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = getuv(fragCoord, iResolution);

    // Load previous frame
    vec3 prev = texture(iChannel0, fragCoord.xy/iResolution.xy).rgb;

    // Fade old trails
    vec3 trailCol = prev * fade;

    // Add current dagger positions
    for(int i=0;i<BLADES;i++){
        Dagger dag = getDagger(i, iTime);
        vec2 orbit = dag.pos;

        // Discard fragments outside the circle
        float len = length(orbit);
        if(len > CIRCLE_RADIUS) orbit = orbit / len * CIRCLE_RADIUS;

        // Thin trail
        float d = length(uv - orbit);
        float b = smoothstep(trailIntensity,0.0,d);

        // Sparkle along trail
        b *= 0.5 + 0.5*fract(sin(dot(uv*50.0 + dag.t, vec2(12.989,78.233)))*43758.5453);

        trailCol += vec3(0.5,0.7,1.0)*b;
    }

    fragColor = vec4(trailCol,1.0);
}

