//  GLOBAL STYLE CONTROLS 

// DAGGER (GLYPH) STYLE
const float GLYPH_SCALE      = 0.2;
const float GLYPH_BRIGHTNESS = 1.2;
const float GLYPH_ALPHA      = 1.0;
const bool  GLYPH_MULTICOLOR = true;
const vec3  GLYPH_COLOR = vec3(0.55, 0.98, 0.82);

// TRAIL CORE
const float TRAIL_RADIUS      = 0.003;
const float TRAIL_FADE        = 0.94;
const float TRAIL_BRIGHTNESS  = 1.0;
const bool TRAIL_MULTICOLOR = true;

// TRAIL ENERGY MODULATION
const float TRAIL_ENERGY_INTENSITY    = 20.;
const float TRAIL_ENERGY_SCALE        = 10.0;
const float TRAIL_ENERGY_SCROLL_SPEED = 4.0;
const float TRAIL_ENERGY_THRESHOLD    = 0.85;
const float TRAIL_ENERGY_STRETCH      = 20.0;
const float TRAIL_LONG_INTENSITY      = 1.8;

// COLORS
const vec3 DAGGER_COLOR = vec3(0.75, 0.78, 0.82);
const vec3 TRAIL_COLOR  = vec3(0.8, 0.5, 1.0);
const vec3 TRAIL_LONG_COLOR = vec3(1.0, 0.0, 0.0);

// MOTION
const float CIRCLE_RADIUS = 0.95;
const int   BLADES        = 40;

// 

struct Dagger {
    vec2 pos;
    float angle;
};

float hash(float n) {
    return fract(sin(n) * 43758.5453);
}

float rand(float n) {
    return fract(sin(n) * 43758.5453);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0/3.0, 1.0/3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 randomGlyphColorHSV(float i, float baseS, float baseV) {
    float h = rand(i); // random hue 0..1
    return hsv2rgb(vec3(h, baseS, baseV));
}

vec2 rgb2sv(vec3 c) {
    float mx = max(c.r, max(c.g, c.b));
    float mn = min(c.r, min(c.g, c.b));
    float v = mx;
    float s = (mx > 0.0) ? (mx - mn)/mx : 0.0;
    return vec2(s, v);
}

// renamed from hash21
float trailNoise21(vec2 p) {
    return fract(sin(dot(p, vec2(27.609, 57.583))) * 43758.5453);
}

Dagger getDagger(float fi, float iTime) {
    float seed1 = hash(fi * 1.7);
    float seed2 = hash(fi * 3.1);
    float seed3 = hash(fi * 5.9);

    float baseRadius = mix(CIRCLE_RADIUS * 0.4, CIRCLE_RADIUS * 0.95, seed1);
    float speed  = mix(0.6, 2.0, seed2);
    float wobble = mix(0.3, 1.5, seed3);

    float t = iTime * speed + fi * 6.28318;

    vec2 orbit = vec2(cos(t), sin(t * wobble)) * baseRadius;
    orbit += 0.08 * vec2(sin(t * 3.0 + fi), cos(t * 2.5 + fi));

    // HARD circle clamp (must be identical everywhere)
    float len = length(orbit);
    if (len > CIRCLE_RADIUS) orbit = orbit / len * CIRCLE_RADIUS;

    Dagger d;
    d.pos   = orbit;
    d.angle = t + sin(t * wobble) * 0.4;
    return d;
}

float sampleDagger(vec2 p, float angle) {
    float c = cos(angle);
    float s = sin(angle);

    // rotate into dagger space
    vec2 r = mat2(c, -s, s, c) * p;

    // map to texture UV
    vec2 uv = r / GLYPH_SCALE + 0.5;

    // outside texture  transparent
    if (uv.x < 0.0 || uv.x > 1.0 || uv.y < 0.0 || uv.y > 1.0)
        return 0.0;

    return texture(iChannel1, uv).a; // use .r if grayscale
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec4 trail = texture(iChannel0, fragCoord.xy / iResolution.xy);

    vec3 col = trail.rgb;
    float alpha = trail.a;

    const int BLADES = 40;

    for (int i = 0; i < BLADES; i++) {
        Dagger d = getDagger(float(i), iTime);

        vec2 p = uv - d.pos;
        float b = sampleDagger(p, d.angle);

        vec2 sv = rgb2sv(GLYPH_COLOR);
        vec3 bladeColor = GLYPH_MULTICOLOR ? randomGlyphColorHSV(float(i), sv.x, sv.y) : GLYPH_COLOR;    

        col += bladeColor * b;
        alpha += b;
    }
    //gamma correction
    col = pow(col, vec3(0.85));
    alpha = smoothstep(0.05, 0.8, alpha);

    fragColor = vec4(col, alpha);
}

iChannel0.source:
//  GLOBAL STYLE CONTROLS 

// DAGGER (GLYPH) STYLE
const float GLYPH_SCALE      = 0.09;
const float GLYPH_BRIGHTNESS = 1.2;
const float GLYPH_ALPHA      = 1.0;
const bool  GLYPH_MULTICOLOR = true;
const vec3  GLYPH_COLOR = vec3(0.55, 0.98, 0.82);

// TRAIL CORE
const float TRAIL_RADIUS      = 0.03;
const float TRAIL_FADE        = 0.94;
const float TRAIL_BRIGHTNESS  = 1.0;
const bool TRAIL_MULTICOLOR = true;

// TRAIL ENERGY MODULATION
const float TRAIL_ENERGY_INTENSITY    = 20.;
const float TRAIL_ENERGY_SCALE        = 10.0;
const float TRAIL_ENERGY_SCROLL_SPEED = 4.0;
const float TRAIL_ENERGY_THRESHOLD    = 0.85;
const float TRAIL_ENERGY_STRETCH      = 20.0;
const float TRAIL_LONG_INTENSITY      = 1.8;

// COLORS
const vec3 DAGGER_COLOR = vec3(0.75, 0.78, 0.82);
const vec3 TRAIL_COLOR  = vec3(0.8, 0.5, 1.0);
const vec3 TRAIL_LONG_COLOR = vec3(1.0, 0.0, 0.0);

// MOTION
const float CIRCLE_RADIUS = 0.95;
const int   BLADES        = 40;

// 

struct Dagger {
    vec2 pos;
    float angle;
};

float hash(float n) {
    return fract(sin(n) * 43758.5453);
}

float rand(float n) {
    return fract(sin(n) * 43758.5453);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0/3.0, 1.0/3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 randomGlyphColorHSV(float i, float baseS, float baseV) {
    float h = rand(i); // random hue 0..1
    return hsv2rgb(vec3(h, baseS, baseV));
}

vec2 rgb2sv(vec3 c) {
    float mx = max(c.r, max(c.g, c.b));
    float mn = min(c.r, min(c.g, c.b));
    float v = mx;
    float s = (mx > 0.0) ? (mx - mn)/mx : 0.0;
    return vec2(s, v);
}

// renamed from hash21
float trailNoise21(vec2 p) {
    return fract(sin(dot(p, vec2(27.609, 57.583))) * 43758.5453);
}

Dagger getDagger(float fi, float iTime) {
    float seed1 = hash(fi * 1.7);
    float seed2 = hash(fi * 3.1);
    float seed3 = hash(fi * 5.9);

    float baseRadius = mix(CIRCLE_RADIUS * 0.4, CIRCLE_RADIUS * 0.95, seed1);
    float speed  = mix(0.6, 2.0, seed2);
    float wobble = mix(0.3, 1.5, seed3);

    float t = iTime * speed + fi * 6.28318;

    vec2 orbit = vec2(cos(t), sin(t * wobble)) * baseRadius;
    orbit += 0.08 * vec2(sin(t * 3.0 + fi), cos(t * 2.5 + fi));

    // HARD circle clamp (must be identical everywhere)
    float len = length(orbit);
    if (len > CIRCLE_RADIUS) orbit = orbit / len * CIRCLE_RADIUS;

    Dagger d;
    d.pos   = orbit;
    d.angle = t + sin(t * wobble) * 0.4;
    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Previous frame
    vec3 prev = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;

    // Fade old trails (RGB only!)
    vec3 col = prev * TRAIL_FADE;

    for (int i = 0; i < BLADES; i++) {
        Dagger d = getDagger(float(i), iTime);

        float dist = length(uv - d.pos);
        float glow = smoothstep(TRAIL_RADIUS, 0.0, dist);

        // Motion direction
        vec2 dir = vec2(cos(d.angle), sin(d.angle));
        float flow = dot(uv - d.pos, dir) * TRAIL_ENERGY_STRETCH;

        // Trail energy field (was sparkle)
        vec2 energyUV =
            (uv - d.pos) * TRAIL_ENERGY_SCALE +
            vec2(flow, iTime * TRAIL_ENERGY_SCROLL_SPEED);

        float energy = trailNoise21(floor(energyUV));
        energy = smoothstep(TRAIL_ENERGY_THRESHOLD, 1.0, energy);

        // Energy boosts trail brightness
        float energyBoost = energy * TRAIL_ENERGY_INTENSITY * glow;
        float energyLong = TRAIL_LONG_INTENSITY * glow;
        vec3 trailColor = TRAIL_COLOR;
        vec3 longColor = TRAIL_LONG_COLOR;
        if (TRAIL_MULTICOLOR)
        {
            vec2 sv = rgb2sv(TRAIL_COLOR);
            trailColor = randomGlyphColorHSV(float(i), sv.x, sv.y);
            sv = rgb2sv(TRAIL_LONG_COLOR);
            longColor = randomGlyphColorHSV(float(i), sv.x, sv.y);
        }
        // Trail body
        col += trailColor * (energyBoost +  glow) * TRAIL_BRIGHTNESS;

        // Long Trail
        col += longColor * (glow + energyLong) * TRAIL_LONG_INTENSITY;


        // HARD clamp to avoid white lock-in
        //col = min(col, 1.0);
    }

    // Derive alpha from brightness (CRITICAL)
    float alpha = smoothstep(
        0.02,
        0.25,
        max(col.r, max(col.g, col.b))
    );

    fragColor = vec4(col, alpha);
}

