//#define LEFT_COLOR vec4(13,11,18,0);  // original color
#define LEFT_COLOR vec4(9, 15, 17, 1)
#define RIGHT_COLOR vec4(19,11,11,1);

float voronoise(vec2 p, sampler2D noiseTex) {
    float d = 1000.;

    vec2 q = fract(p);
    vec2 f = floor(p);

    for(float x = -0.5; x <= 0.5; x++)
    for(float y = -0.5; y <= 0.5; y++) {
        vec2 h = vec2(x,y);

        d = min(d, length(q - h - texture(noiseTex, (f + h) / 256.).xy));
    }
    return d;
}

mat2 rot(float x) {
    return mat2(cos(x),sin(x),-sin(x),cos(x));
}

// reference: https://www.shadertoy.com/view/WXycRW
// reference: comment from the page linked above

#define NOISE_SCALE 6.0      // The crackling intensity of lightning (greater intensity indicates greater detail)
#define NOISE_SPEED 5.0      // The speed at which lightning moves
#define SPLIT_INTENSITY 0.02 // The range in which lightning deviates from a straight path
#define GLOW_WIDTH 0.001     // Basic thickness of the lightning-line

// color of lightning-line
const vec3 BOLT_COLOR = vec3(0.8, 0.9, 1.0);

float hash(float n) 
{
    return fract(sin(n) * 43758.5453123);
}

float noise(vec2 x) 
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f * f * (3.0 - 2.0 * f);
    float n = p.x + p.y * 57.0;

    return mix(mix(hash(n + 0.0), hash(n + 1.0), f.x),
               mix(hash(n + 57.0), hash(n + 58.0), f.x), f.y);
}

float fbm(vec2 p) 
{
    float f = 0.0;
    float w = 0.5;
    for (int i = 0; i < 5; i++) 
    {
        f += w * noise(p);
        p *= 2.0;
        w *= 0.5;
    }

    return f;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 color = vec4(0);
    float splitPos = 0.5;

    if (iMouse.x > 1.0) 
        splitPos = iMouse.x / iResolution.x;  

    // lightning-line settings
    vec2 noiseUV = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float noiseTime = iTime * NOISE_SPEED;
    float displacement = fbm(noiseUV * NOISE_SCALE + vec2(noiseTime, noiseTime * 0.5));
    displacement = (displacement - 0.5) * 2.0;
    float baseDistX = (fragCoord.x / iResolution.x) - splitPos;
    float dist = baseDistX + displacement * SPLIT_INTENSITY;

    if(dist < 0.)    
        color = texture(iChannel0, uv);    
    else    
        color = texture(iChannel1, uv);    

    // Adding lightning-line color
    float glow = GLOW_WIDTH / abs(dist);
    glow = pow(clamp(glow, 0.0, 1.0), 1.2);
    glow *= (0.8 + 0.2 * sin(iTime * 20.0));
    color.rgb += BOLT_COLOR * glow * 4.0;

    fragColor = color;
}

