//#define LEFT_COLOR vec4(13,11,18,0);  // original color
#define LEFT_COLOR vec4(9, 15, 17, 1)
#define RIGHT_COLOR vec4(19,11,11,1);

float voronoise(vec2 p, sampler2D noiseTex) {
    float d = 1000.;

    vec2 q = fract(p);
    vec2 f = floor(p);

    for(float x = -0.5; x <= 0.5; x++)
    for(float y = -0.5; y <= 0.5; y++) {
        vec2 h = vec2(x,y);

        d = min(d, length(q - h - texture(noiseTex, (f + h) / 256.).xy));
    }
    return d;
}

mat2 rot(float x) {
    return mat2(cos(x),sin(x),-sin(x),cos(x));
}

void mainImage(out vec4 c,vec2 p) {
    vec2 r = iResolution.xy;
    p = (p + p - r) / sqrt(r.x * r.y);
    if(length(p) < 0.1) 
    {
        c = vec4(0);
        return;
    }

    p /= dot(p,p) * 2.1;
    p *= rot(pow(dot(p,p),1.5) * 0.4);
    c = vec4(0);
    float q;
    float t = -iTime * 7.;
    for(float n = fract(t); n < 31.; n++) 
    {
        c += pow(voronoise(p * n * 0.4 + texture(iChannel0,vec2(floor(n - floor(t))) / 256.).xy * 256., iChannel0) * 1000. + 10., -1.) * (1. - n / 30.) * min(n, 1.);
    }
    c += dot(p,p) * 0.1;
    c *= LEFT_COLOR;
    c -= dot(p,p) * 0.6 * vec4(0.9,1.2,1.4,0);
    c = max(c,0.);
    c += length(p*.01)-.5;
    c *= smoothstep(1.,0.,length(p*.6)-.5); 
    c *= c;
    c=tanh(c);
}

