// River Over Depth Map (top-down)
// RESTORED: All Uniform Metadata and Original Normal Mapping Logic.

uniform float uFlowAngleDeg;      // @editable 24.0 @tip "Flow direction in degrees." @order 1 @min -180 @max 180
uniform float uFlowSpeed;         // @editable 0.28 @tip "Overall river flow speed." @order 2 @min 0.1 @max 5
uniform float uTurbulence;        // @editable 0.7 @tip "Amount of flow distortion and chaos." @order 4 @min 0.0 @max 5.0
uniform float uPatternScale;      // @editable 3.0 @tip "Global scale for waves, foam, and silt patterns." @order 3 @min 0.1 @max 10
uniform float uWaterLevel;        // @editable 0.5 @tip "Waterline threshold against depth map." @order 5 @min 0.0 @max 1.0
uniform float uTransparency;      // @editable 0.85 @tip "Visibility of refracted riverbed through water." @order 6 @min 0.0 @max 1.0
uniform float uRefraction;        // @editable 0.05 @tip "Base refraction distortion strength." @min 0.00 @max 2
uniform float uDiffraction;       // @editable 0.0025 @tip "Chromatic fringe amount around refraction." @min 0.00 @max 0.02
uniform float uRefractionFlow;    // @editable 0.55 @tip "Extra flow-driven refraction strength." @min 0.0 @max 1.0
uniform float uIor;               // @editable 1.333 @tip "Index of refraction used for physical bend."
uniform float uNormalIntensity;   // @editable 1.2 @tip "Strength of surface normal perturbation." @min 0.0 @max 5.0
uniform float uSpecularity;       // @editable 0.8 @tip "Specular highlight intensity." @min 0.0 @max 3.0
uniform float uShininess;         // @editable 48.0 @tip "Specular highlight sharpness." @min 0 @max 255
uniform float uFoamIntensity;     // @editable 2.0 @tip "Overall shoreline foam intensity." @min 0 @max 20
uniform float uFoamThreshold;     // @editable 0.02 @tip "Width of shoreline foam band near waterline." @min 0 @max 0.1
uniform float uFoamSpeed;         // @editable 0.25 @tip "How fast foam patterns evolve over time." @min 0 @max 1
uniform float uVortexStrength;    // @editable 0.5 @tip "Extra turbulent churn contribution to foam." @min 0 @max 1
uniform vec3 uDeepColor;          // @editable 0.01,0.24,0.43 @tip "Tint color used in deepest water."
uniform vec3 uMediumColor;        // @editable 0.07,0.45,0.57 @tip "Tint color used in mid-depth water."
uniform vec3 uShallowColor;       // @editable 0.30,0.74,0.67 @tip "Tint color used in shallow water."
uniform vec3 uDepthWeights;       // @editable -1.0,0.0,1.0 @tip "RGB channel weights used to derive depth." @order 7
uniform float uDepthGamma;        // @editable 1.0 @tip "Gamma curve applied to depth mapping." @order 8 @min 0 @max 3
uniform float uRedFix;            // @editable 0.7 @tip "Enable red correction to suppress land flooding." @order 9 @min -1 @max 1
uniform float uGreenFix;           // @editable 0.4 @tip "Enable green correction to suppress land flooding." @order 10 @min -1 @max 1
uniform float uAnalysisBlueBoost; // @editable 0.0 @min 0.0 @max 10.0 @tip "Boost blue-dominant pixels when blue is already stronger than red and green." @order 11
uniform bool uDebugHeightVsWater;  // @editable 0.0 @tip "Show depth/waterline debug visualization." @order 11
uniform float uSiltIntensity;     // @editable 1.0 @tip "Overall strength of suspended silt plumes." @min 0 @max 5
uniform float uSiltScale;         // @editable 12.0 @tip "Scale of silt plume patterns." @min 0 @max 40
uniform float uSiltSpeed;         // @editable 0.25 @tip "Advection speed of silt through flow." @min 0 @max 3
uniform float uSiltContrast;      // @editable 0.55 @tip "Contrast of silt cloud breakup." @min 0 @max 1
uniform float uSiltShallowBias;   // @editable 0.65 @tip "Bias silt placement toward shallow water." @min 0 @max 1
uniform float uSiltDepthBias;     // @editable 0.25 @tip "Target depth where silt is emphasized." @min 0 @max 1
uniform vec3 uSiltColorA;         // @editable 0.53,0.59,0.42 @tip "Primary silt tint color."
uniform vec3 uSiltColorB;         // @editable 0.34,0.43,0.30 @tip "Secondary silt tint color."

// --- HELPERS ---
float luma(vec3 c) { return dot(c, vec3(0.2126, 0.7152, 0.0722)); }
float boostedBlue(vec3 c) {
  float blueLead = max(c.b - max(c.r, c.g), 0.0);
  return clamp(c.b + blueLead * max(0.0, uAnalysisBlueBoost), 0.0, 1.0);
}
float depthMapFromRgb(vec3 c) {
  c.b = boostedBlue(c);
  vec3 w = uDepthWeights;
  float wsumAbs = abs(w.x) + abs(w.y) + abs(w.z);
  float d = wsumAbs > 0.00001 ? dot(c - vec3(0.5), w) / wsumAbs + 0.5 : luma(c);
  float g = max(0.01, uDepthGamma);
  float depthLinear = pow(clamp(d, 0.0, 1.0), g);
  depthLinear -= max(c.r - c.b, 0.0) * uRedFix + max(c.g - c.b, 0.0) * uGreenFix;
  return clamp(depthLinear, 0.0, 1.0);
}
float hash12(vec2 p) {
  vec3 p3 = fract(vec3(p.xyx) * 0.1031);
  p3 += dot(p3, p3.yzx + 33.33);
  return fract((p3.x + p3.y) * p3.z);
}
float noise2(vec2 p) {
  vec2 i = floor(p); vec2 f = fract(p);
  vec2 u = f * f * (3.0 - 2.0 * f);
  float a = hash12(i + vec2(0.0, 0.0));
  float b = hash12(i + vec2(1.0, 0.0));
  float c = hash12(i + vec2(0.0, 1.0));
  float d = hash12(i + vec2(1.0, 1.0));
  return mix(mix(a, b, u.x), mix(c, d, u.x), u.y);
}
float fbm(vec2 p) {
  float v = 0.0; float a = 0.5;
  mat2 m = mat2(0.80, -0.60, 0.60, 0.80);
  for (int i = 0; i < 3; i++) {
    v += a * noise2(p);
    p = m * p * 2.01;
    a *= 0.52;
  }
  return v;
}

// --- LOGIC ---
vec2 flowWarp(vec2 p, vec2 flowDir, float t, float turb) {
  float tt = t * (0.35 + turb * 0.55);
  vec2 q = vec2(fbm(p * 0.85 + vec2(0.0, tt * 0.9)), fbm(p * 0.82 + vec2(13.7, -tt * 0.7))) - 0.5;
  vec2 r = vec2(fbm(p * 1.37 + q * 2.4 + vec2(-tt * 1.3, 4.2)), fbm(p * 1.41 + q * 2.1 + vec2(tt * 1.1, -8.3))) - 0.5;
  vec2 swirl = vec2(r.y - q.x, q.y - r.x);
  float spin = sin(dot(p, flowDir.yx * vec2(1.7, -2.3)) + tt * 2.6);
  return (swirl * 2.0 + q + r) * (0.25 + turb * 0.9) + flowDir.yx * spin * 0.15 * (0.2 + turb);
}

float surfaceField(vec2 uv, vec2 flowDir, float t, float turb) {
  vec2 p = uv * 7.5;
  float advTime = t * (1.0 + turb * 0.9);
  vec2 baseAdv = p - flowDir * advTime;
  vec2 warpA = flowWarp(baseAdv + vec2(2.1, -1.3), flowDir, t, turb);
  vec2 warpB = flowWarp(baseAdv * 1.23 + vec2(-6.2, 4.4), flowDir, t + 3.7, turb);
  vec2 q1 = baseAdv + warpA;
  vec2 q2 = baseAdv * 1.75 - warpB * 1.35 + flowDir.yx * sin(t * 1.8 + dot(baseAdv, vec2(1.3, -1.1)));
  float crests = sin(dot(q1, flowDir * vec2(5.8, 4.1)) + fbm(q2 + vec2(t * 0.8, -t * 0.6)) * 3.14159);
  float chop = sin(dot(q2, flowDir.yx * vec2(3.2, -5.4)) + t * 2.1);
  float micro = fbm(q1 * 2.2 + warpB * 2.0 + vec2(t * 1.5, -t * 1.2)) * 2.0 - 1.0;
  float macro = fbm(q2 * 0.95 - warpA * 1.7 + vec2(-t * 0.5, t * 0.4)) * 2.0 - 1.0;
  float vort = sin((warpA.x - warpB.y) * 8.0 + t * (1.2 + turb * 1.7));
  return crests * 0.35 + chop * 0.22 + micro * 0.28 + macro * 0.18 + vort * 0.16;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord.xy / iResolution.xy;

  // Pattern Space Pre-calc
  float aspect = iResolution.x / max(iResolution.y, 1.0);
  float s = max(0.001, uPatternScale);
  vec2 pScale = vec2(aspect * s, s);
  vec2 uvP = (uv - 0.5) * pScale + 0.5;

  float t = uTime * max(0.0, uFlowSpeed);
  float rad = radians(uFlowAngleDeg);
  vec2 flowDir = vec2(cos(rad), sin(rad));
  vec2 flowDirP = normalize(flowDir * pScale + 1e-5);

  // RESTORED: 4-Tap Finite Difference Normals with Original Epsilon
  vec2 e = vec2(1.0 / iResolution.x, 1.0 / iResolution.y) * pScale;
  float hL = surfaceField(uvP - vec2(e.x, 0.0), flowDirP, t, max(0.0, uTurbulence));
  float hR = surfaceField(uvP + vec2(e.x, 0.0), flowDirP, t, max(0.0, uTurbulence));
  float hD = surfaceField(uvP - vec2(0.0, e.y), flowDirP, t, max(0.0, uTurbulence));
  float hU = surfaceField(uvP + vec2(0.0, e.y), flowDirP, t, max(0.0, uTurbulence));
  vec2 g = vec2(hR - hL, hU - hD) * max(0.01, uNormalIntensity);
  vec3 N = normalize(vec3(-g.x, -g.y, 1.0));

  vec4 bed = texture(iChannel0, uv);
  float bedDepthMap = depthMapFromRgb(bed.rgb);
  float rawDepth = uWaterLevel + bedDepthMap - 1.0;
  float depth = clamp(rawDepth, 0.0, 1.0);
  float waterMask = smoothstep(0.002, 0.03, depth);

  if (uDebugHeightVsWater) {
    vec3 landWater = mix(vec3(0.55, 0.15, 0.10), vec3(0.08, 0.38, 0.90), step(0.0, rawDepth));
    vec3 debugColor = mix(vec3(1.0 - bedDepthMap), landWater, 0.65);
    debugColor = mix(debugColor, vec3(1.0, 0.92, 0.18), 1.0 - smoothstep(0.0, 0.02, abs(rawDepth)));
    fragColor = vec4(debugColor, 1.0); return;
  }

  // Shoreline Grad
  vec2 texel = 1.0 / iResolution.xy;
  float dL = depthMapFromRgb(texture(iChannel0, uv - vec2(texel.x, 0)).rgb);
  float dR = depthMapFromRgb(texture(iChannel0, uv + vec2(texel.x, 0)).rgb);
  float dD = depthMapFromRgb(texture(iChannel0, uv - vec2(0, texel.y)).rgb);
  float dU = depthMapFromRgb(texture(iChannel0, uv + vec2(0, texel.y)).rgb);
  vec2 terrainGrad = vec2(dR - dL, dU - dD);
  float shoreline = smoothstep(-max(0.002, uFoamThreshold), 0.0, rawDepth) * (1.0 - step(0.0, rawDepth));

  // Refraction
  float eta = 1.0 / max(1.01, uIor);
  vec3 refrV = refract(vec3(0,0,-1), N, eta);
  vec2 physOffset = (refrV.xy / max(0.20, refrV.z + 0.20)) * uRefraction;
  float fDistA = surfaceField(uvP * 1.35 + flowDirP * 0.23, flowDirP, t * 1.10, max(0.0, uTurbulence));
  float fDistB = surfaceField(uvP * 1.28 + flowDirP.yx * vec2(-0.31, 0.17), flowDirP, t * 0.94, max(0.0, uTurbulence));
  vec2 refrOff = (physOffset + vec2(fDistA, fDistB) * (0.015 + 0.03 * clamp(uRefraction, 0.0, 1.5)) * uRefractionFlow) * mix(0.35, 1.2, depth);
  vec2 chroma = N.xy * uDiffraction;
  vec3 refrBed = vec3(texture(iChannel0, uv + refrOff + chroma).r, texture(iChannel0, uv + refrOff).g, texture(iChannel0, uv + refrOff - chroma).b);

  vec3 wCol = mix(uShallowColor, mix(uMediumColor, uDeepColor, smoothstep(0.40, 0.95, depth)), smoothstep(0.08, 0.45, depth));

  // Foam & Silt
  float eddy = 0.5 + 0.5 * sin((fbm(uvP * 9.0 - flowDirP * t * 0.75 + vec2(4.7, -2.1)) * 2.0 - 1.0) * 9.5 + t * 2.2);
  vec2 bankShallowP = normalize(-terrainGrad * pScale + 1e-5);
  float fImpact = max(0.0, dot(flowDirP, bankShallowP));
  float fRate = (0.2 + uFlowSpeed * 0.85) * (0.25 + uFoamSpeed);
  vec2 fDrift = normalize(flowDirP * (0.75 + 0.45 * fImpact) + vec2(-flowDirP.y, flowDirP.x) * (0.16 + 0.28 * (1.0 - fImpact))) * (uTime * fRate);

  float along = dot(uvP, flowDirP); float across = dot(uvP, vec2(-flowDirP.y, flowDirP.x));
  vec2 fCoord = vec2(along, across);
  float mA = fbm(fCoord * vec2(11, 7.5) - fDrift * vec2(2.8, 2.2) + bankShallowP * 0.45);
  float mB = fbm(fCoord.yx * vec2(9.2, 8.7) - fDrift * vec2(3.4, 2.6) - bankShallowP.yx * 0.35 + vec2(3.7, -2.5));
  float mC = fbm((fCoord * vec2(11, 7.5) - fDrift * vec2(2.8, 2.2) + mB) * 0.62);

  float foamMask = clamp((shoreline * (0.65 + 0.35 * fbm(uvP * 18.0 - flowDirP * t * 0.35)) * (0.42 + 0.58 * smoothstep(0.004, 0.055, length(terrainGrad))) * mix(0.72, 1.3, clamp(uFlowSpeed/2.0, 0.0, 1.0)) * (0.68 + 0.32 * fImpact) * clamp(smoothstep(0.3, 0.82, mA * 0.48 + mB * 0.34 + mC * 0.18) * (0.62 + 0.38 * (1.0 - smoothstep(0.12, 0.56, abs(mA - mB)))), 0.0, 1.0) + shoreline * mix(0.2, 0.7, clamp(uFlowSpeed/2.0, 0.0, 1.0)) * (0.2 + 0.6 * fImpact) * uVortexStrength * smoothstep(0.45, 0.9, mC) * 0.2) * uFoamIntensity, 0.0, 1.0);

  vec2 pBase = uvP * (0.2 * uSiltScale);
  vec2 pWarp = flowWarp(pBase * 0.55 + vec2(2.7, -1.9), flowDirP, t * 0.55 + 0.9, max(0.0, uTurbulence)) * (0.45 + 0.35 * max(0.0, uTurbulence));
  float pA = fbm(pBase - flowDirP * (t * (0.01 + uSiltSpeed)) + pWarp);
  float pB = fbm(pBase * 1.45 - flowDirP * (t * (0.01 + uSiltSpeed) * 1.2) - pWarp * 0.75 + vec2(4.2, -3.6));
  float sField = smoothstep(max(0.05, uSiltContrast * 0.25), min(0.98, 0.55 + uSiltContrast * 0.35), clamp(smoothstep(0.3, 0.86, pA * 0.52 + pB * 0.33 + fbm((pBase - flowDirP * (t * (0.01 + uSiltSpeed)) + pWarp + pBase * 1.45) * 0.65) * 0.15) * (0.55 + 0.45 * (1.0 - smoothstep(0.16, 0.62, abs(pA - pB)))), 0.0, 1.0));
  float sAmt = uSiltIntensity * mix(1.0 - smoothstep(0.0, 0.55, abs(depth - uSiltDepthBias)), 1.0 - smoothstep(0.12, 0.9, depth), clamp(uSiltShallowBias, 0.0, 1.0)) * sField;

  vec3 wBody = mix(refrBed, wCol, 1.0 - clamp(uTransparency * mix(1.0, mix(1.0, exp(-depth * 1.9), 0.8), 1.0 - uTransparency), 0.0, 1.0));
  wBody = mix(wBody, mix(wBody, mix(uSiltColorA, uSiltColorB, pB), 0.5), (1.0 - exp(-sAmt * 1.2)) * 0.85);

  float spec = pow(max(dot(reflect(vec3(0.32, 0.26, -0.91), N), vec3(0,0,1)), 0.0), max(4.0, uShininess)) * (0.35 + pow(1.0 - clamp(dot(N, vec3(0,0,1)), 0.0, 1.0), 3.0)) * uSpecularity;
  wBody = (wBody * (0.96 + 0.04 * clamp(dot(N, normalize(vec3(-0.32, -0.26, 0.91))), 0.0, 1.0))) + spec;

  vec3 final = mix(bed.rgb, mix(wBody, vec3(0.95, 0.98, 1.0), foamMask * waterMask * 0.2), waterMask);
  final = mix(final, mix(bed.rgb, vec3(0.95, 0.98, 1.0), 0.88), foamMask * (1.0 - waterMask) * shoreline);

  fragColor = vec4(final, 1.0);
}

