//////////////////////////////////////////////////////////////////////////////////

#define PI 3.14159265359
#define time PI*iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    vec2 uvScene = fragCoord.xy / iResolution.xy;

    vec4 fragScene = texture(iChannel0, uvScene);

    // uv between -.5 - .5
	vec2 uv = fragCoord/iResolution.xy;
	uv -= vec2(.5);

    // square aspect
    uv.x *= iResolution.x/iResolution.y;

    vec3 c1; // blue part
    float s; // stripes
    {
        // angle & distance
        float a = atan(uv.x, uv.y);
        float d = length(uv);

        // waves
        d += .1;
        d += .3*pow(d, 2.5)*sin(a*18.);
        d = pow(d, .005);
        d *= 5999.;

        // rings
        d = .5 + .5*sin(d - 2.*8.*time);

        // fade at sides
        float b = d;
        b *= 1.0 - length(uv);

        // thin stripes at sides
        s = 1. + 1.*length(uv)*sin(a*300.);
        b *= s;

        // color
        c1 = vec3(0, 0, b);
    }

	vec3 c2; // pink/cyan part
	{
		float y = uv.y;
		float x = uv.x;

		// skew
		x -= sin(y*6.)/9.;

		// perspective
		x /= pow(y+sign(y), 6.);

		// waves
		float a = pow(abs(y) + .03, .1)*32.;
		x += .03*sin(a - time);

		// stripes
		x = sin(x*30.);

		// color - cycle pink and cyan
		float b = pow(abs(x), 20.);    
		float p = sin(time/2.)*.5 + .5;
		c2 = vec3(b*p, b*(1. - p), b*.9);

		// apply inverse stripes
		c2 /= s;
	}

	fragColor = vec4(c1 + c2*.5, 1);
    fragColor = vec4(fragColor.rgb*fragScene.a, fragScene.a);
}

vec4 perm(vec4 x) { x *= x * 34.0 + 1.0; return x - floor(x / 289.0) * 289.0; }

float noise(vec3 p) {
    vec3 a = floor(p);
    vec3 d = p - a;
    vec4 b = a.xxyy + vec4(0, 1, 0, 1);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww) + a.zzzz;
    vec4 o1 = fract(perm(k2) * 0.02439024);
    vec4 o2 = fract(perm(k2 + 1.0) * 0.02439024);
    vec4 o3 = mix(o1, o2, d.z);
    vec2 o4 = mix(o3.xz, o3.yw, d.x);
    return mix(o4.x, o4.y, d.y);
}

float fbm(vec3 p, float t) {
	float s = 0.0, m = 0.0, a = 1.0;
	for(int i = 0; i < 6; i++) {
		s += a * noise(p);
		m += a;
		a *= 0.6;
		p *= 2.0;
        p += cos(s * 8.0 + t);
	}
	return s / m;
}

float fbm2(vec3 p) {
	float s = 0.0, m = 0.0, a = 1.0;
	for(int i = 0; i < 6; i++) {
		s += a * noise(p);
		m += a;
		a *= 0.65;
		p *= 1.8;
	}
	return s / m;
}

vec3 rot_axis(vec3 v, vec3 axis, float angle) {
	return mix(dot(v, axis) * axis, v, cos(angle)) + sin(angle) * cross(axis, v);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y) * 1.25;
    float t = iTime * 0.1;
    //uv *= exp2(-t * 4.0 + 0.5);

    float l = length(uv);

    vec3 p = vec3(uv, sqrt(max(0.0, 1.0 - l * l)));
    p = rot_axis(p, normalize(vec3(0, 1, 0)), t);
    float f = length(fwidth(uv));
    float n = fbm2(p * 3.0);
    float w = n * 0.02 / max(0.01, 1.0 - sqrt(l));
    float d = n + w;
    d = min(1.0, d);
    float b = fbm(p * 9.0, t * 2.0);
    d = mix(d, b, b);
    float s = smoothstep(1.0, 1.0 - f * 3.0, l);
    d *= s;
    float h = smoothstep(1.3, 0.0, l) * 5.0 * (1.0 - s);
    d += h;
    d = d + (1.0 - d) * (1.0 - d) * (0.3 + sqrt(b) * 0.8) * (0.05 / (0.01 + (l - 0.98)))*(1.0 - s);
    vec3 c = vec3(1, d, d * d * d) * d;
    c += h * abs(n - 0.5);
    c *= 1.0 + b;
    float alpha = smoothstep(0.0,0.76,d*2.);

    fragColor = vec4(c, alpha);
}
